INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_CU', 'dl_npc_rhendal', 'See you later.','Bis später.','Bis spöter.', 'Hasta pronto.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IGotSilkweed', 'dl_npc_rhendal', 'I got the Silkweed.','Ich habe das Seidenkraut.','Ich hans Sidechrütli.', 'Conseguí la hierbaseda.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_CanITrustYou', 'dl_npc_rhendal', 'Can I trust you? What if that potion is going to poison me?','Kann ich dir vertrauen? Was, wenn der Trank mich vergiftet?','Chan ich dir vertraue? Was, wenn de Trank mich vergiftet?', '¿Puedo confiar en usted? ¿Qué tal si la poción me envenena?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhereIsYourGarden', 'dl_npc_rhendal', 'Where can I find the Silkweed?','Wo kann ich das Seidenkraut finden?','Wo chan ich es Sidechrütli finde?', '¿Dónde puedo encontrar la hierbaseda?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_Hello', 'dl_npc_rhendal', 'Hello, young man. What brings you into my humble home?','Hallo, junger Mann. Was führt dich in mein bescheidenes Heim?','Hallo, junge Ma. Was führt dich i mis bescheidene Heim?', 'Buenas, jovencito. ¿Qué te trae a mi humilde morada?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IGotProblem', 'dl_npc_rhendal', 'I have a problem.','Ich habe ein Problem.','Ich han es Problem.', 'Tengo un problema.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_EdmondSentMe', 'dl_npc_rhendal', 'The hunter Edmond sent me to you.','Der Jäger Edmond hat mich zu dir geschickt.','De Jäger Edmond het mich zu dir gschickt.','El cazador Edmond me envió con usted.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IngridSentMe', 'dl_npc_rhendal', 'Ingrid told me you can help me.','Ingrid hat mir gesagt, du könntest mir helfen.','D''Ingrid het gseit du chöntsch mer helfe.', 'Ingrid me dijo que usted podría ayudarme.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_EdmondSentYou', 'dl_npc_rhendal', 'Ah, Edmond. Our talented hunter.','Ah, Edmond, unser begabter Jäger.','Ah, de Emond, üse begabti Jäger.', 'Ah, Edmond, nuestro talentoso cazador.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_IngridSentYou', 'dl_npc_rhendal', 'Ah, Ingrid. Our talented cook.','Ah, Ingrid, unsere begabte Köchin.','Ah, d''Ingrid, üsi begabt Chöchin.', 'Ah, Ingrid, nuestra talentosa cocinera.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_WhatIsProblem', 'dl_npc_rhendal', 'So, what exactly is your problem?','Was hast du denn für ein Problem?','Was isch den dis Problem gnau?', 'Entonces, ¿cuál es tu aflicción exactamente?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Cendric_Problem', 'dl_npc_rhendal', 'I''ve awoken in the forest in the north and I can''t remember anything. I have no clue who I am and how I got there. My name is Cendric, that''s the only thing I know.','Ich bin im Wald im Norden aufgewacht und kann mich an nichts erinnern. Ich habe keine Ahnung wer ich bin und wie ich dort hin gekommen bin. Mein Name ist Cendric, das ist das einzige, was ich weiss.','Ich bin im Wald im Norde ufgwacht und cha mich a nüt erinnere. Ich han ken Plan wer ich bin und we ich döt hi cho bi. Min Name isch Cendric, da isches einzige, woni no weiss.', 'Desperté en el bosque al norte y no puedo recordar nada. No tengo idea alguna de quién soy ni de cómo llegué ahí. Lo único que sé es que mi nombre es Cendric.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_Eyeballing', 'dl_npc_rhendal', 'That''s interesting and worrying at the same time. But you''re in luck, I happen to be initiated in the art of alchemy and I know just the right potion that may help you.','Das ist zugleich interessant und beunruhigend. Aber du kannst dich glücklich schätzen, ich verstehe etwas von der Kunst der Alchemie und kenne einen Trank, der dir helfen könnte.','Das isch gliichziitig interessant und beunruhigend. Aber du chasch dich glücklich schätze, ich verstand chli was vo de Kunst vode Alchemie und kenn en Trank, wo dir hälfe chönt.', 'Hmmm... interesante y preocupante a la vez. Andas de suerte ya que soy iniciado en el arte de la alquimia y conozco la poción apropiada que podría ayudarte.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_BringMe', 'dl_npc_rhendal', 'I have most ingredients for it right here. If you could go pick the fresh leaves of a Silkweed I can start to prepare the potion for you. It grows in my garden, just outside.','Ich habe die meisten Zutaten dafür schon hier. Wenn du mir ein paar Blätter frisches Seidenkraut bringen könntest, kann ich währenddessen mit der Vorbereitung des Tranks beginnen. Es wächst in meinem Garten vor dem Haus.','Ich han di meiste Zuetate defür scho da. Wenn du mir no es paar frischi Blätter vomne Sidechrütli bringe chöntsch, chan ich währenddesse mitem Trank afange. Es wachst i mim Garte, grad vorem Huus.', 'Tengo la mayoría de los ingredientes acá. Si pudieras ir por unas hojas frescas de hierbaseda, yo podría empezar a preparar la poción para ti. Crece en mi jardín, justo aquí afuera.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_NoChoice', 'dl_npc_rhendal', 'Do you have another choice?','Hast du denn eine andere Wahl?','Bliibt der denn was anderes übrig?', '¿Tienes otra opción?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Cendric_NoChoice', 'dl_npc_rhendal', '...','...','...', '...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_SilkweedLocation', 'dl_npc_rhendal', 'It grows in my garden, right in front of this house.','Es wächst in meinem Garten, direkt vor meinem Haus.','Es wachst im Garte, direkt vor mim Huus.', 'Crece en mi jardín, justo al frente de la cabaña.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_DrinkThePotion', 'dl_npc_rhendal', 'Very good. I just completed the other preparations. Now let''s add that Silkweed ... and, it''s done. You can drink it now.','Sehr gut, ich bin gerade mit den anderen Vorbereitungen fertig geworden. Jetzt fügen wir noch das Seidenkraut hinzu ... und, fertig. Du kannst es jetzt trinken.','Sehr guet, ich bin grad mit de andere Vorbereitige fertig worde. Jetz gämmer no es Sidechrütli dri, so... und fertig. Chasches ez trinke.', 'Muy bien. Acabo de completar las otras preparaciones. Ahora agreguemos la hierbaseda ... y ... está lista. Ya la puedes tomar.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Okay', 'dl_npc_rhendal', 'Okay...','Okay...','Okee...', 'Bueno...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_But', 'dl_npc_rhendal', 'But...','Aber...','Aber...', 'Pero...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_NoBut', 'dl_npc_rhendal', 'No "but"! You want to get your memories back, don''t you.','Kein "Aber"! Trink den Trank jetzt, du willst doch deine Erinnerungen zurück.','Nüt "Aber". Trink ez.', '¡Nada de peros! Quieres recuperar tus recuerdos, ¿no?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Cendric_OkayThen', 'dl_npc_rhendal', '... Okay then.','... Okay.','... Okay, dennhalt.', '... Bueno, ni modo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IGotStaff', 'dl_npc_rhendal', 'I got the Hawthorn staff.','Ich habe den Weissdorn-Stab.','Ich han de Wiisdorn-Stab.', 'Conseguí el báculo de espino.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhatDoesThatMean', 'dl_npc_rhendal', 'A mage... What does that mean?','Ein Magier... Was bedeutet das?','En Magier... Und was bedüütet das?', 'Un mago... ¿Qué significa eso?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhatJob', 'dl_npc_rhendal', 'What job can you offer?','Was für einen Auftrag hast du anzubieten?','Was für en Uftrag hesch denn az''büüte?', '¿Qué trabajo me puede ofrecer?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_HowCanIHelp', 'dl_npc_rhendal', 'Okay, how can I help you?','Okay, wie kann ich euch helfen?','Okay, we chan ich eu hälfe?', 'Muy bien. ¿Cómo le puedo ayudar?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhatAboutTheMark', 'dl_npc_rhendal', 'About that mark on my wrist...','Wegen dem Mal auf meinem Handgelenk...','Wege dem Zeiche uf mim Handglenk...', 'En cuanto a esa marca en mi muñeca...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_NiceVision', 'dl_npc_rhendal', '(Smiles) It seems that your vision was of use. I''ve never seen a guest of mine freeze my water before.','(Lächelt) Es scheint mir als hätte deine Vision etwas gebracht. Noch nie zuvor hat einer meiner Gäste mein Wasser gefroren.','(Lächlet) Es schiint mir als sig dini Vision erfolgriich gsi. Nanie hett en Gascht vo mir mis Wasser gfrore.', '(Sonríe) Al parecer la visión fue de ayuda. Nunca antes había visto a un invitado mío congelar el agua.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_SoYoureAMage', 'dl_npc_rhendal', 'So you''re a mage. I''ve felt that you have a strong magic aura the first time you walked through that door.','Du bist also ein Magier. Ich habe schon als du das erste Mal durch meine Tür getreten bist gefühlt, dass du eine starke magische Aura besitzt.','Du bisch also en Magier. Hanis doch gwüsst. Han das scho ganz am Afang im Gspüri gha, dass du e starchi magischi Aura hesch.', 'Sentí que tenías un aura mágica bastante fuerte la primera vez que entraste por la puerta.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_MageIs', 'dl_npc_rhendal', 'It means that you are able to focus the magic energy that flows through this world and use it to your favour.','Es bedeutet, dass du dir die magische Energie, die durch diese Welt strömt zu Nutzen machen kannst.','Es bedüütet, dass du di magisch Energie wo dur die Welt flüsst, dir z''Nutze mache chasch.', 'Quiere decir que eres capaz de concentrar la energía mágica que fluye a través del mundo y utilizarla a tu favor.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_MagiciansAre', 'dl_npc_rhendal', 'It''s a special gift - not all people in Admantris have it.','Es ist ein seltenes Talent - nicht alle Menschen von Admantris sind dazu fähig.','Es isch es seltenigs Talent... nid all Lüüt vo Admantris chönd das.', 'Es un don especial. No todas las personas en Admantris lo tienen.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_YouCanRead', 'dl_npc_rhendal', 'And even if I''m not able to use that power myself, I still take an interest in the art of magic. You should definitely take a look into my book I wrote about it, its right next to the fireplace.','Und auch wenn ich diese Kraft nicht selbst nutzen kann, interessiere ich mich dennoch sehr für diese Kunst. Du solltest dir mein Buch anschauen, dass ich dazu geschrieben habe. Es steht gleich neben dem Herd.','Und obwohl ich die Chraft au nid für mich selber nutze chan, findis doch es cheibe interessants Thema. Du sötsch mal i das Buech inegüggsle woni dezue gschribe ha, es staht grad nebed em Schminee.','Incluso aunque no pueda utilizar ese poder yo mismo, me interesa el arte de la magia. Definitivamente deberías ojear el libro que escribí al respecto. Está junto a la chimenea.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_YouHaveTo', 'dl_npc_rhendal', 'But, what''s more important at the moment, is that you have to get to know your powers. What insights did the vision bring you?','Aber, was momentan noch wichtiger ist, ist dass du deine Kräfte besser kennen lernst. Was für Einsichten hat dir die Vision gebracht?','Aber, wa im Moment na wichtigier isch, du sötsch dini Chräft chli besser kenne lerne. Wa für Liechtli sind der denn so ufgange bi dinere Vision?', 'No obstante, lo más importante ahora es que conozcas tus poderes. ¿Qué conocimientos te aportó la visión?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IHadStaff', 'dl_npc_rhendal', 'I once had a staff and a fancy hat.','Ich hatte früher einen Stab und einen lustigen Hut.','Ich ha früener en Stab und en lustige Huet gha.', 'Alguna vez tuve un báculo y un sombrero fantástico.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_ICanFreeze', 'dl_npc_rhendal', 'I can apparently freeze water.','Ich kann anscheinend Wasser gefrieren lassen.','Ich chan anschiinend Wasser gfüre. Eifach so.', 'Aparentemente puedo congelar el agua.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_YouHadStaff', 'dl_npc_rhendal', 'Yes, that''s what some mages wear. But we saw you are a mage that is able to use ice magic.','Ja, das tragen gewisse Magier. Aber wir haben auch gesehen, dass du ein Magier bist, der Eismagie verwenden kann.','Ja, das träged gwüssi Magier halt. Aber wamer au gse hend, isch dass du chasch Iismagie verwende.', 'Sí, eso es lo que algunos magos visten, pero ya vimos que eres un mago capaz de utilizar magia de hielo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_AndThisIsGood', 'dl_npc_rhendal', 'And that''s exactly what I was looking for. I can offer you a job, if you''re interested.','Und das ist genau, was ich gesucht habe. Ich kann dir einen Auftrag anbieten, wenn du Interesse hast.','Und das isch gnau, was ich gsuecht ha. Ich chan der en Uftrag abüüte, wend möchtisch.', 'Eso es exactamente lo que estaba buscando. Puedo encomendarte una tarea, si te interesa.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_LetMeSee', 'dl_npc_rhendal', 'Hmm, let me see... Interesting. It seems like the potion has not only brought you back some memories, but has also revealed this mark.','Hmm, lass mich das ansehen... Interessant. Es scheint, als hätte der Trank dir nicht nur gewisse Erinnerungen zurück gebracht, sondern auch dieses Mal zum Vorschein gebracht.','Hm, lah mich mal luege... Interessant. Es schiint mir, als hätti de Trank nid nur es paar vo dine Erinnerige geweckt, sondern au das Zeiche da zum Vorschii bracht.', 'Hmmm, déjame ver... Interesante. Tal parece que por la poción no solo recuperaste algunos recuerdos sino que también reveló esta marca.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_SideEffect', 'dl_npc_rhendal', 'It is not some side-effect of the potion?','Es ist keine Nebenwirkung des Trankes?','Es isch ke Nebedwürkig vom Trank?', '¿No será un efecto secundario de la poción?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_DoYouKnowAnything', 'dl_npc_rhendal', 'Do you know anything about this mark?','Weisst du irgendetwas über dieses Mal?','Weisch du denn irgendöppis über das Zeiche?', '¿Sabe usted algo de esta marca?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_SideEffect', 'dl_npc_rhendal', 'No, the Silkweed potion doesn''t create this kind of marks. It only reveals what''s hidden.','Nein, der Seidenkraut Trank hinterlässt nicht solche Spuren. Er bringt nur hervor, was zuvor verborgen war.','Nei, de Sidechrütli Trank hinterlaht kei derartigi Spuure. Es chunt eifach füre, was vorene verborge gsi isch.', 'No, la poción hierbaseda no causa ese tipo de marcas, solo devela aquello que yace oculto.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_TheMark', 'dl_npc_rhendal', 'I don''t recognize the symbol... it''s definitely magic but doesn''t have a friendly aura, no, not at all...','Ich erkenne dieses Symbol nicht... Es ist definitiv Magie, aber mit einer sehr unfreundlichen Aura...','Nei, ich erkenn das Symbol nid... Es isch definitiv Magie, aber mit enere sehr unfründliche Aura...', 'No reconozco el símbolo... Es definitivamente mágico, pero no tiene un aura amigable en lo más mínimo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_Curse', 'dl_npc_rhendal', 'I think it''s some kind of curse, and it''s getting stronger. You''d be better off with finding someone that can break it. I have no knowledge about this kind of magic, I''m sorry.','Ich glaube, es ist eine Art Fluch und es wird stärker. Du wärst wohl besser beraten, wenn du jemanden findest, der ihn brechen kann. Ich habe zu wenig Wissen über diese Art von Magie, tut mir leid.','Ich glaub, es isch enart Fluech und es wird stärcher. Wer wohl besser wend der wen sueche würsch, wo das breche chan. Ich han leider selber ken Blasse vo dere Art vo Magie, tuet mer leid.', 'Creo que es una especie de maldición y se hace más fuerte. Será mejor que busques a alguien que la pueda romper. No tengo conocimiento en este tipo de magia. Lo lamento.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_YouNeedAStaff', 'dl_npc_rhendal', 'Before you can take this job, you need a staff to control and focus your magic. I grow a magical Hawthorn tree in my garden. Take a branch from this tree and you will have what you need.','Bevor du diesen Auftrag ausführen kannst, brauchst du zuerst einen Stab, der dir hilft, deine Magie zu fokussieren. Ich züchte einen magischen Weissdornbaum in meinem Garten. Brich dir einen Ast davon ab und du wirst haben, was du brauchst.','Bevor du de Uftrag animmsch bruchsch zersch en Stab zum dini Magie fokussiere. Ich züchte es magisches Wissdorn-Bäumli i mim Garte. Brich der en Ast devo ab und du wirsch ha was du bruchsch.', 'Antes emprender esta tarea, necesitas un báculo para concentrar y dirigir tu magia. En mi jardín, crece un árbol de espino mágico. Toma una de sus ramas y obtendrás aquello que necesitas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Cendric_IAlreadyHave', 'dl_npc_rhendal', 'Erm... I think I...','Ähm... Ich glaube, ich...','Ähhm... ich glaub, ich...', 'Este... Creo que yo...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_YouAlreadyHave', 'dl_npc_rhendal', 'Ah, I see, you already took a branch. Well, I won''t be angry with you, but the next person from which you take things probably won''t be that lenient... ','Ah, ich sehe, du hast dir diesen Ast bereits genommen. Nunja, ich werde dir das zwar verzeihen, aber die nächste Person, die du antriffst, wird vermutlich nicht so nachsichtig sein...','Ah, ich gse, du Schlingel hesch de Ast scho abbroche. Naja, ich wird ez nid hässig si, aber di nechst Person wo''d atriffsch, wird dir das vilicht nüm eifach so durega lah...', 'Oh, veo que ya tomaste una rama. Está bien, no me enojaré contigo, pero puede que la próxima persona de quien tomes cosas no sea tan permisiva.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_YouHaveStaff', 'dl_npc_rhendal', 'It suits you well.','Steht dir gut.','Gsesch guet uus demit.', 'Te queda bien.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_OurProblem', 'dl_npc_rhendal', 'The only way between our village and the tavern leads over the bridge in the West. The innkeeper brews a special kind of schnapps which I need for one of my potions.','Der einzige Weg zwischen unserem Dorf und der Taverne führt über die Brücke im Westen. Der Wirt dort braut einen speziellen Schnaps, den ich für einen meiner Tränke brauche.','De einzig Weg zwüsche üsem Dorf und de Taverne führt über die Brugg im Weste. De Wirt det braut en spezielle Schnapps wo ich für eine vo mine Tränk bruche.', 'La única senda que comunica a la aldea con la taberna yace sobre el puente del Oeste. El tabernero prepara un aguardiente especial que necesito para una de mis pociones.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_BrokenBridge', 'dl_npc_rhendal', 'Unfortunately, that bridge has broken lately and it will take us some days to rebuild it.','Unglücklicherweise ist diese Brücke kürzlich kaputt gegangen und wir werden einige Tage brauchen, bis wir sie wieder aufgebaut haben.','Unglücklicherwiis isch die Brugg letztins kabutt gange und mir bruched es paar Täg bis mer si wider ufbaut hend.', 'Desafortunadamente, el puente se dañó hace poco y nos tomará varios días repararlo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_YourJob', 'dl_npc_rhendal', 'Your job would be to get to the other side of the river by freezing a small passage, and get me a bottle of this schnapps from the tavern.','Deine Aufgabe wäre es, auf die andere Seite des Flusses zu gelangen, indem du einen kleinen Teil des Flusses gefrieren lässt, und mir eine Flasche davon zu bringen.','Dini Ufgab wers denn, uf di ander Siite vom Fluss z''ga, indem du en chliine Teil vom Fluss gfüürsch, und mir es Gütterli devo z''bringe.', 'Tu tarea consistirá en congelar el agua del río para crear un camino, llegar al otro lado, y obtener una botella de aguardiente de la taberna.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IWillHelp', 'dl_npc_rhendal', 'Okay, I''ll see what I can do.','Okay, ich werde sehen, was sich machen lässt.','Okay, ich lueg mal was sich mache laht.', 'Está bien, veré qué puedo hacer');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhatsInIt', 'dl_npc_rhendal', 'So, what''s in it for me then?','Und was springt für mich dabei raus?','Und was springt für mich debii use?', 'Y yo, ¿qué gano?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IWontHelp', 'dl_npc_rhendal', 'I don''t think I should waste my power like this...','Ich glaube nicht, dass ich meine Kraft dafür verschwenden sollte...','Ich glaub nid, dasich mini Chraft dadefür verschwende söt...', 'Creo que no debería malgastar mis poderes así.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_BrokenBridgeQuest', 'dl_npc_rhendal', 'Great. Just ask the innkeeper for a "Feudal Fire", he''ll know what you want. And take these coins, it should be enough.','Gut. Frage den Wirt einfach nach einem "Feudalen Feuer", er wird wissen, was du suchst. Und nimm diese Münzen, das sollte reichen.','Guet, frög de Wirt eifach nachme "Feudale Füür", er wird denn scho wüsse was du suechsch. Und nimm die Münze, das sött lange.', '¡Genial! Solo pídele al tabernero un Fuego Feudal, él sabrá lo que buscas. Toma estas monedas; con eso debería ser suficiente.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_YouAreEvil', 'dl_npc_rhendal', 'And I don''t think you''ll get on the right way like this, boy.','Und ich glaube nicht, dass du so auf den rechten Weg kommen wirst, Junge.','Und ich glaub nid, dass du so uf de richtig Wäg chunsch, Bürschtli.', 'Me parece que así no irás por buen camino, joven.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_WhyYouShould', 'dl_npc_rhendal', 'Well, you''d prove that you plan to use your power for greater good. I''m sure that one or two people may be pleased to hear about your good deed.','Nunja, du würdest damit beweisen, dass du deine Kräfte für das Gute einsetzen willst. Ich bin mir sicher, der eine oder andere wird erfreut sein, von deiner guten Tat zu hören.','Naja, du würsch demit bewiise, dass du dini Chräft fürs Gueti bruche würsch. Ich bimer sicher, de eint oder ander het sicher Freud, wener vo dinere guete Tat ghört.', 'Muy bien, Cendric, has probado que planeas usar tus poderes para un bien mayor. Estoy seguro de que una o dos personas se alegrarán cuando escuchen de tu buena acción.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IGotSchnapps', 'dl_npc_rhendal', 'I got the schnapps for you.','Ich habe den Schnaps für dich.','Ich han de Schnaps für dich.', 'Le conseguí el aguardiente.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_ThankForSchnaps', 'dl_npc_rhendal', 'Oh, perfect, thank you. So you managed to cross the river. Hmm, a truly special drink. Do you want some? ','Oh, perfekt, vielen Dank. Du hast es also geschafft, den Fluss zu überqueren. Hmm, ein wahrlich spezielles Getränk. Willst du auch ein bisschen?','Oh, perfekt, viele Dank. Du heschs also gschafft, de Fluss z''überquere. Hmm, würkli es speziells Getränk. Wetsch nid au chli?', 'Oh, perfecto, gracias. Entonces lograste cruzar el río. Hmmm, ciertamente una bebida especial. ¿Te gustaría probarla?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IWantDrink', 'dl_npc_rhendal', 'Yes, why not.','Gern, wieso nicht.','Gern, weso nid.', 'Sí, por supuesto.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IDontWantDrink', 'dl_npc_rhendal', 'No, thank you.','Nein, danke.','Nei danke.', 'No, gracias.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_GivesSchnapps', 'dl_npc_rhendal', 'Yes, that''s the right decision. Here, take a glass.','Ja, das ist die richtige Entscheidung. Hier, nimm einen Schluck.','Ja, das isch di richtig Entscheidig. Da, nimm en Schluck.', 'Sí, muy buena decisión. Ven, toma un vaso.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Cendric_Thanks', 'dl_npc_rhendal', 'Thanks.','Danke.','Merci.', 'Gracias.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IDrankSchnapps', 'dl_npc_rhendal', 'I got the schnapps for you... and it was delicious.','Ich habe den Schnaps für dich gekauft... und er war köstlich.','Ich han de Schnaps für dich kauft... und er isch fein gsi.', 'Le conseguí el aguardiente... y estaba delicioso.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_NoSchnappsPity', 'dl_npc_rhendal', 'Oh, what a pity. You don''t know what you''re missing.','Sehr schade. Du weisst ja nicht, was dir entgeht.','Sehr schad. Du weisch ja nid, was der entgoht.', 'Ah, qué pena. No sabes de lo que te estás perdiendo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IGotSchnappsAnyway', 'dl_npc_rhendal', 'I''ve changed my decision and brought you some schnapps - for free.','Ich habe meine Meinung geändert und dir trotzdem Schnaps gebracht...','Ich ha mini Meinig gänderet und dir trotzdem en Schnaps brocht...','Cambié de opinión y le traje un poco de aguardiente... y gratis.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Rhendal_YouDrankSchnaps', 'dl_npc_rhendal', 'What? Oh you silly boy! You drank it yourself? You''re not the sharpest knife in the drawer, are you?!','Was? Oh du einfältiger Junge! Du bist wirklich nicht der Hellste, was?!','Waaa? Oh du dumme Bueb. Du bisch würkli dumm wenes Brot, oder?', '¿Qué? ¡Muchacho tonto! ¿Te lo tomaste tú solo? No eres el más listo de grupo, ¿verdad?');
