INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Hello', 'dl_npc_rhendal', 'Hello, young man. What brings you into my humble home?', 'Hallo, junger Mann. Was führt dich in mein bescheidenes Heim?', 'Hallo, junge Ma. Was führt dich i mis bescheidene Heim?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IGotProblem', 'dl_npc_rhendal', 'I have a problem.', 'Ich habe ein Problem.', 'Ich han es Problem.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_EdmondSentMe', 'dl_npc_rhendal', 'The hunter Edmond sent me to you.', 'Der Jäger Edmond hat mich zu dir geschickt.', 'De Jäger Edmond het mich zu dir gschickt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IngridSentMe', 'dl_npc_rhendal', 'Ingrid told me you can help me.', 'Ingrid hat mir gesagt, du könntest mir helfen.', 'D''Ingrid het gseit du chöntsch mer helfe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_EdmondSentYou', 'dl_npc_rhendal', 'Ah, Edmond. Our talented hunter.', 'Ah, Edmond, unser begabter Jäger.', 'Ah, de Emond, üse begabti Jäger.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_IngridSentYou', 'dl_npc_rhendal', 'Ah, Ingrid. Our talented cook.', 'Ah, Ingrid, unsere begabte Köchin.', 'Ah, d''Ingrid, üsi begabt Chöchin.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_WhatIsProblem', 'dl_npc_rhendal', 'So, what exactly is your problem?', 'Was hast du denn genau für ein Problem?', 'Was isch den dis Problem gnau?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_Problem', 'dl_npc_rhendal', 'I''ve awoken in some forest and I can''t remember anything. I have no clue who I am and how I got there.', 'Ich bin in einem Wald aufgewacht und kann mich an nichts erinnern. Ich habe keine Ahnung wer ich bin und wie ich dort hin gekommen bin.', 'Ich bin imne Wald ufgwacht und cha mich a nüt erinnere. Ich han ken Plan wer ich bin und we ich döt hi cho bi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Problem', 'dl_npc_rhendal', 'Hm. That doesn''t sound good.', 'Hm. Das hört sich gar nicht gut an.', 'Hm. Das tönt gar nid guet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Problem2', 'dl_npc_rhendal', 'But you''re in luck, I happen to be initiated in the art of alchemy and I know just the right potion that may help you.', 'Aber du kannst dich glücklich schätzen, ich verstehe etwas von der Kunst der Alchemie und kenne einen Trank, der dir helfen könnte.', 'Aber du chasch dich glücklich schätze, ich verstand chli was vo de Kunst vode Alchemie und kenn en Trank, wo dir hälfe chönt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_BringMe', 'dl_npc_rhendal', 'I have most ingredients for it right here.', 'Ich habe die meisten Zutaten dafür schon hier.', 'Ich han di meiste Zuetate defür scho da.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_BringMe2', 'dl_npc_rhendal', 'I still need some fresh leaves from a silkweed and a magical stone from a cairn wraith though.', 'Ich brauche jedoch noch ein paar frische Blätter eines Seidenkrauts und den magischen Stein eines Cairn Geistes.', 'Ich bruch aber no es paar frischi Blätter vomne Sidechrüütli und en magische Stei vomne Steimännli Geist.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_BringMe3', 'dl_npc_rhendal', 'If you could bring me those, I will brew the potion for you.', 'Wenn du mir diese bringst, werde ich dir den Trank brauen.', 'Wenn du mer die bringsch, wirdi der de Trank braue.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FoundIngredients', 'dl_npc_rhendal', 'I found the ingredients for the potion.', 'Ich habe die Zutaten für den Trank gefunden.', 'Ich ha d''Zuetate für de Trank gfunde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereSilkweed', 'dl_npc_rhendal', 'Where can I find the silkweed?', 'Wo kann ich das Seidenkraut finden?', 'Wo chanis Siidechrütli finde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereCairn', 'dl_npc_rhendal', 'Where do cairn wraiths live?', 'Wo leben denn Cairn Geister?', 'Wo lebed denn Steimännli Geister?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TrustYou', 'dl_npc_rhendal', 'Why should I trust you? What if that potion is going to poison me?', 'Wieso sollte ich dir vertrauen? Was, wenn dieser Trank mich vergiftet?', 'Weso sötti dir traue? Was, wenn de Trank mich vergiftet?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_FoundIngredients', 'dl_npc_rhendal', 'Very good. I just completed the other preparations. Now let''s add the silkweed and the stone...', 'Sehr gut, ich bin gerade mit den anderen Vorbereitungen fertig geworden. Jetzt fügen wir noch das Seidenkraut und den Stein hinzu...', 'Sehr guet. Ich bi grad mit de andere Vorbereitige fertig worde. Jetz tömmer no es Siidechrütli und de Stei dri...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_FoundIngredients2', 'dl_npc_rhendal', '... and it''s done. You can drink it now.', '... und er ist fertig. Du kannst ihn jetzt trinken.', '... und er isch fertig. Du chaschen jetz trinke.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_DrinkOkay', 'dl_npc_rhendal', 'Okay...', 'Okay...', 'Okay...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_DrinkBut', 'dl_npc_rhendal', 'But...', 'Aber...', 'Aber...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_DrinkBut', 'dl_npc_rhendal', 'No "but"! Drink it.', 'Kein "aber"! Trink jetzt.', 'Nüt "aber"! Trink jetz.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_WhereSilkweed', 'dl_npc_rhendal', 'I grow some silkweed in my garden, just in front of my house.', 'Ich züchte Seidenkraut in meinem Garten vor meinem Haus.', 'Ich züchte Siidechrütli i mim Garte, grad vor mim Hüsli.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_WhereCairn', 'dl_npc_rhendal', 'Usually in the mountains. I think I''ve seen some on the windy plateau, right behind the crypt of our village.', 'Normalerweise in den Bergen. Ich glaube, ich habe einige auf der windigen Ebene hinter der Krypta unseres Dorfes gesehen.', 'Normalerwiis ide Berge. Ich ha glaub es paar uf de windige Ebeni hinter de Krypta vo üsem Dorf gse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_TrustYou', 'dl_npc_rhendal', 'Do you have another choice?', 'Hast du denn eine andere Wahl?', 'Hesch denn en anderi Wahl?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_TrustYou', 'dl_npc_rhendal', '...', '...', '...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Snowing', 'dl_npc_rhendal', '(Shivers) What... it''s snowing? ', '(Fröstelt) Was... Es schneit?', '(Fröstlet) Was... Es schneit?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Snowing2', 'dl_npc_rhendal', 'So you''re an elementalist? A mage that controls the elements?', 'Dann bist du also ein Elementarmagier? Ein Magier, der die Elemente kontrolliert?', 'Denn bisch also en Elementarmagier? En Magier, wo d''Element kontrolliert?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Snowing3', 'dl_npc_rhendal', 'Fascinating! What did you see in your vision?', 'Faszinierend! Was hast du denn in deiner Vision gesehen?', 'Faszinierend! Was hesch denn i dinee Vision gse?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_VisionDungeon', 'dl_npc_rhendal', 'I was imprisoned in some dungeon.', 'Ich war in einem Verlies eingesperrt.', 'Ich bi imne Verlies igsperrt gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_VisionFlames', 'dl_npc_rhendal', 'There were... purple flames.', 'Da waren... violette Flammen.', 'Do sind... violetti Flamme gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_VisionMark', 'dl_npc_rhendal', 'This mark appeared on my wrist. What does it mean?', 'Dieses Mal ist auf meinem Handgelenk erschienen. Was bedeutet das?', 'Das Zeiche isch uf mim Handglänk erschine. Was heisst das?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_VisionDungeon', 'dl_npc_rhendal', 'Hm. Not everything that appears in your vision has to be true. What else did you see?', 'Hm. Nicht alles, was in deiner Vision erscheint, muss auch wahr sein. Was hast du sonst noch gesehen?', 'Hm. Nid alles, wa i dinere Vision erschiint, mu au wohr si. Wa hesch susch na gse?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_VisionFlames', 'dl_npc_rhendal', 'Maybe some kind of magic. I''ve never heard of purple magic, though.', 'Vielleicht eine Art von Magie. Ich habe aber noch nie von violetter Magie gehört.', 'Vilicht enart Magie. Ich ha aber nonie was vo violetter Magie ghört.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_VisionMark', 'dl_npc_rhendal', 'Hmm, let me see... Interesting.', 'Hmm, lass mich mal sehen... Interessant.', 'Hmm, zeig mal... Interessant.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_VisionMark2', 'dl_npc_rhendal', 'It seems like the potion has not only brought you back some memories, but has also revealed this mark.', 'Es scheint, als hätte die Vision nicht nur ein paar Erinnerungen zurückgebracht, sondern auch dieses Mal zum Vorschein gebracht.', 'Es schiint, als het d''Vision nid nur es paar Erinnerige zruggbrocht, sondern au das Zeiche zum Vorschi brocht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_SideEffect', 'dl_npc_rhendal', 'Could it be some side-effect of the potion?', 'Könnte es eine Nebenwirkung des Trankes sein?', 'Chönts e Nebedwürkig vom Trank sii?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_KnowAnythingMark', 'dl_npc_rhendal', 'Do you know anything about this mark?', 'Weisst du irgendetwas über dieses Mal?', 'Weisch du irgendwas über das Zeiche?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_SideEffect', 'dl_npc_rhendal', 'No, the silkweed potion doesn''t create this kind of marks. It only reveals what''s hidden.', 'Nein, der Seidenkraut-Trank hinterlässt keine Spuren. Er deckt nur auf, was verborgen war.', 'Nei, de Siidechrütli-Trank hinterloht ke so Spure. Er deckt nur uf, was verborge gsi isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_KnowAnythingMark', 'dl_npc_rhendal', 'I don''t recognize the symbol... it''s definitely magic but doesn''t have a friendly aura, no, not at all...', 'Ich kenne das Symbol nicht... es ist aber definitiv Magie, die allerdings keine gute Aura hat, nein, nein, gar nicht...', 'Ich kenns Symbol nid... es isch aber definitiv Magie, allerdings het si kei gueti Aura, nei, nei, gar nid...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_KnowAnythingMark2', 'dl_npc_rhendal', 'I think it''s some kind of curse, and it''s getting stronger.', 'Ich nehme an, es ist eine Art Fluch, und er wird stärker.', 'Ich nimm ah, es isch enart Fluech, und er wird stärcher.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_KnowAnythingMark3', 'dl_npc_rhendal', 'You''d be better off with finding someone who knows more about it than me and can break it.', 'Du solltest besser jemanden finden, der mehr darüber weiss als ich und ihn brechen kann.', 'Du sötsch besser öppert finde, wo me drüber weiss we ich und en breche chan.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mage', 'dl_npc_rhendal', 'What exactly is a mage?', 'Was genau ist ein Magier?', 'Was gnau isch en Magier?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YouMage', 'dl_npc_rhendal', 'Are you a mage?', 'Bist du ein Magier?', 'Bisch du en Magier?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Magic', 'dl_npc_rhendal', 'How can I use my magic?', 'Wie kann ich meine Magie benutzen?', 'We chani mini Magie bruche?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Job', 'dl_npc_rhendal', 'Do you have a job for me?', 'Hast du Arbeit für mich?', 'Hesch Arbet für mich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_BrokenBridge', 'dl_npc_rhendal', 'I''m ready for your job.', 'Ich bin bereit für deinen Auftrag.', 'Ich bin parat für din Job.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotSchnapps', 'dl_npc_rhendal', 'I got the schnapps for you.', 'Ich habe den Schnaps für dich.', 'Ich han de Schnaps für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IDrankSchnaps', 'dl_npc_rhendal', 'I got the schnapps for you... and it was delicious.', 'Ich habe den Schnaps für dich gekauft... und er war köstlich.', 'Ich han de Schnaps für dich kauft... und er isch fein gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Bye', 'dl_npc_rhendal', 'That''s everything I needed to know. Bye.', 'Das ist alles, was ich wissen wollte. Tschüss.', 'Da isch alles, wani ha wele wüsse. Tschüss.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Mage', 'dl_npc_rhendal', 'Mages are able to control the magic energy that flows through this world.', 'Magier können die magische Energie kontrollieren, die durch diese Welt fliesst.', 'Magier chönd di magisch Energie kontrolliere, wo dur die Welt flüsst.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Mage2', 'dl_npc_rhendal', 'It''s a special gift - not all people in Admantris have it.', 'Es ist ein seltenes Talent - nicht alle Menschen von Admantris sind dazu fähig.', 'Es isch es seltenigs Talent... nid all Lüüt vo Admantris chönd das.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_YouMage', 'dl_npc_rhendal', 'No. I''m "only" an alchemist. I use the magical energy contained in some plants, but I can''t control it myself.', 'Nein. Ich bin "nur" ein Alchemist. Ich nutze die magische Energie, die in Pflanzen enthalten ist, kann sie aber selbst nicht kontrollieren.', 'Nei. Ich bi "nur" en Alchemist. Ich bruch di magisch Energie wo i Pflanze drin isch, chan si selber aber nid kontrolliere.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Magic', 'dl_npc_rhendal', 'First, you need a staff that is able to focus magical energy.', 'Zuerst brauchst du einen Stab, der magische Energie fokussieren kann.', 'Zersch mal bruchsch en Stab, wo magischi Energie fokussiere chan. ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Magic2', 'dl_npc_rhendal', 'I grow a magical Hawthorn tree in my garden. Just take a branch from there and you will have what you need.', 'Ich züchte einen magischen Weissdornbaum in meinem Garten. Brich dir einen Ast davon ab und du wirst haben, was du brauchst.', 'Ich züchte es magisches Wissdorn-Bäumli i mim Garte. Brich der en Ast devo ab und du wirsch ha was du bruchsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Magic3', 'dl_npc_rhendal', 'Equip the staff and open your spellbook to equip your iceball spell.', 'Rüste den Stab aus und öffne dein Zauberbuch, um deinen Eisball-Zauber auszurüsten.', 'Leg de Stab ah und mach dis Zauberbuech uf, zum din Iisball-Zauber uusrüste.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Job', 'dl_npc_rhendal', 'Yes, I would have a job for an ice mage.', 'Ja, für einen Eismagier habe ich eine Arbeit.', 'Ja, für en Iismagier hani en Arbet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_Job2', 'dl_npc_rhendal', 'I will tell you more about it, after you''ve equipped a staff with an iceball spell.', 'Ich werde dir mehr davon erzählen, wenn du einen Stab mit einem Eisball-Zauber ausgerüstet hast.', 'Ich wird der me devo verzelle, wend en Stab mitme Iisball-Zauber usgrüstet hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_BrokenBridge', 'dl_npc_rhendal', 'Perfect, the staff suits you well.', 'Perfekt, der Stab steht dir gut.', 'Perfekt, de Stab stoht der guet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_BrokenBridge2', 'dl_npc_rhendal', 'Well. I need some special schnapps for my potions. The only one who brews it is the innkeeper of the tavern in the West.', 'Nun. Ich brauche einen speziellen Schnaps für meine Tränke. Der einzige, der ihn braut, ist der Wirt der Taverne im Westen.', 'Guet. Ich bruch en spezielle Schnaps für mini Tränk. De einzig, wonen braut, isch de Wirt vode Taverne im Weste.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_BrokenBridge3', 'dl_npc_rhendal', 'The problem is now, that the bridge that connects this village and the tavern, is broken.', 'Das Problem hierbei ist, dass die Brücke, die das Dorf und die Taverne verbindet, eingestürzt ist.', 'Es Problem isch aber, dass d''Brugg, wo das Dorf und d''Taverne verbindet, iigstürzt isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_BrokenBridge4', 'dl_npc_rhendal', 'We would need some time to rebuild it, but I need the schnapps as soon as possible.', 'Wir würden eine Weile brauchen um sie wieder aufzubauen, aber ich brauche den Schnaps so schnell wie möglich.', 'Mir würded es Ziitli bruche, zum sie wieder ufbaue, aber ich bruch de Schnaps so schnell we mögli.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_BrokenBridge5', 'dl_npc_rhendal', 'Use your ice magic to freeze the river and get to the other side.', 'Nutze deine Eismagie, um den Fluss zu gefrieren und auf die andere Seite zu gelangen.', 'Bruch dini Iismagie, zum de Fluss gfrühre und uf di ander Siite z''go.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_BrokenBridge6', 'dl_npc_rhendal', 'Ask the innkeeper for a "Feudal Fire". Here, this gold should be enough. Hurry!', 'Frage den Wirt nach einem "Feudalen Feuer". Hier, dieses Gold sollte genug sein. Beeil dich!', 'Frög de Wirt nochme "Feudale Füür". Do, das Gold sött gnueg si. Mach schnell!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_GotSchnapps', 'dl_npc_rhendal', 'Oh, perfect, thank you. So you managed to cross the river. Hmm, a truly special drink. Do you want some?', 'Oh, perfekt, vielen Dank. Du hast es also geschafft, den Fluss zu überqueren. Hmm, ein wahrlich spezielles Getränk. Willst du auch ein bisschen?', 'Oh, perfekt, viele Dank. Du heschs also gschafft, de Fluss z''überquere. Hmm, würkli es speziells Getränk. Wetsch nid au chli?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IWantDrink', 'dl_npc_rhendal', 'Yes, why not.', 'Gern, wieso nicht.', 'Gern, weso nid.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IDontWantDrink', 'dl_npc_rhendal', 'No, thank you.', 'Nein, danke.', 'Nei danke.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_GivesSchnapps', 'dl_npc_rhendal', 'Yes, that''s the right decision. Here, take a glass.', 'Ja, das ist die richtige Entscheidung. Hier, nimm einen Schluck.', 'Ja, das isch di richtig Entscheidig. Da, nimm en Schluck.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_NoSchnappsPity', 'dl_npc_rhendal', 'Oh, what a pity. You don''t know what you''re missing.', 'Sehr schade. Du weisst ja nicht, was dir entgeht.', 'Sehr schad. Du weisch ja nid, was der entgoht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rhendal_YouDrankSchnaps', 'dl_npc_rhendal', 'What? Oh you silly boy! You drank it yourself? You''re not the sharpest knife in the drawer, are you?!', 'Was? Oh du einfältiger Junge! Du bist wirklich nicht der Hellste, was?!', 'Waaa? Oh du dumme Bueb. Du bisch würkli dumm wenes Brot, oder?');
