INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Hello', 'dl_npc_robert2', 'There''s nothing to see here, just walk on.', 'Hier gibt''s nichts zu sehen, geh einfach weiter.', 'Do gits nüt z''gse, lauf eifach wiiter.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatDoing', 'dl_npc_robert2', 'What are you doing on the cemetery?', 'Was machst du auf dem Friedhof?', 'Was machsch du ufem Friedhof?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_robert2', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FromHere', 'dl_npc_robert2', 'Are you living here in this village?', 'Lebst du hier im Dorf?', 'Lebsch du do im Dorf?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Crypt', 'dl_npc_robert2', 'Have you been inside the crypt then?', 'Warst du denn in der Krypta?', 'Bisch denn ide Krypta gsi?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TeleportQuest', 'dl_npc_robert2', 'What''s in for me if I help you get your stone back?', 'Was springt für mich raus wenn ich dir helfe, den Stein zu holen?', 'Wa springt für mich use weni der hilf, de Stei z''hole?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Afraid', 'dl_npc_robert2', 'Why are you so afraid of that skeleton?', 'Wieso hast du solche Angst vor diesem Skelett?', 'Weso hesch so Schiss vor dem Skelett?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_StartRobertQuest', 'dl_npc_robert2', 'I''ll get your stone from the crypt.', 'Ich hole deinen Stein aus der Krypta.', 'Ich hole din Stei us de Krypta.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FinishRobertQuest', 'dl_npc_robert2', 'I got your stone.', 'Ich habe deinen Stein.', 'Ich ha din Stei.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CU', 'dl_npc_robert2', 'See you.', 'Tschüss.', 'Tschüss.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_WhatDoing', 'dl_npc_robert2', 'Hmm, nothing. But I strongly advise you not to go down into the crypt. It''s spooky there. ', 'Hmm, nichts. Aber ich würde dir davon abraten, in die Krypta hinunter zu gehen. Es spukt dort.', 'Hmm, nüt. Aber ich wür der devo abrote, id Krypta z''go. Es isch gruslig dete.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_WhoAreYou', 'dl_npc_robert2', 'I''m Bob and I possess magical powers! Impressive, ain''t it.', 'Ich bin Bob und ich habe magische Kräfte! Beeindruckend, nicht.', 'Ich bi de Bob und ich ha magischi Chräft! Beidruckend, eh.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_VeryImpressive', 'dl_npc_robert2', 'Very impressive, indeed.', 'Sehr beeindruckend, wirklich.', 'Sehr beidruckend, würkli.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotVeryImpressed', 'dl_npc_robert2', 'Hm. How''s that special?', 'Hm. Wieso ist das so aussergewöhnlich?', 'Hm. Wieso isch da so speziell?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_NotVeryImpressed', 'dl_npc_robert2', 'Not many are able to wield magic here. You''d better show some respect.', 'Nicht viele hier können Magie benutzen. Du solltest besser etwas Respekt zeigen.', 'Nid viel do chönd Magie bruche. Du sötsch besser chli Respekt zeige.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_LivingHere', 'dl_npc_robert2', 'No, I''m not from here. I''m just passing through.', 'Nein, ich bin nicht von hier. Nur auf der Durchreise.', 'Nei, ich bi nid vo do. Nur uf de Durchreis.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Crypt', 'dl_npc_robert2', 'Hm. Yes. Listen, I was just trying out my powers... and then a little mistake happened. ', 'Hm. Ja. Hör zu, ich wollte nur meine Kräfte ausprobieren und dann ist ein kleiner Unfall passiert.', 'Hm. Jo. Los, ich ha nur wele mini Chräft usprobiere und denn isch en chliine Unfall passiert.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Crypt2', 'dl_npc_robert2', 'A skeleton in the crypt suddenly started to move! I was so shocked that I ran out of the crypt and then I noticed that my teleport stone was missing. ', 'Ein Skelett in der Krypta hat sich plötzlich bewegt! Ich war so geschockt, dass ich hinausgerannt bin. Doch dann habe ich bemerkt, dass ich meinen Teleportstein in der Krypta verloren habe.', 'E Skelett ide Krypta het sich plötzlich bewegt! Ich bi so gschockt gsi dasi us de Krypta grennt bi und denn hani gmerkt dasi min Teleportstei det verlore han.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Crypt3', 'dl_npc_robert2', 'But I really need this stone! I can''t get away without it.', 'Aber ich brauche diesen  Stein wirklich! Ohne ihn komme ich nicht weg von hier.', 'Aber ich bruch de Stei würkli! Ohni ihn chumi nid weg vo do.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_TeleportQuest', 'dl_npc_robert2', 'You? You look like a mere commoner. I don''t think you can help me.', 'Du? Du siehst aus wie ein Gewöhnlicher. Ich glaube nicht, dass du mir helfen kannst.', 'Du? Du gsesch us wenen Gwöhnliche. Ich glaub nid dasmer helfe chasch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_TeleportQuest2', 'dl_npc_robert2', 'But if you manage to get my stone back... Well, I have an inactive teleport stone here.', 'Aber wenn es dir gelingt, meinen Stein zurückzuholen... Ich habe einen inaktiven Teleporstein hier.', 'Aber wens schaffsch, min Stei zrugg z''hole... Ich han en inaktive Stei do.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_TeleportQuest3', 'dl_npc_robert2', 'You could have it. It''s worth a lot to mages, so you could sell it.', 'Du könntest ihn haben. Er ist ziemlich wertvoll für Magier. Du könntest ihn verkaufen.', 'De chöntsch ha. Er isch zimli wertvoll für Magier. Du chöntschen ja verchaufe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Afraid', 'dl_npc_robert2', 'I''m not afraid. Only cautious.', 'Ich habe keine Angst, bin nur vorsichtig.', 'Ich ha kei Schiss, bi nur vorsichtig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_StartRobertQuest', 'dl_npc_robert2', 'Maybe you''re lucky. I''ll waith here.', 'Vielleicht hast du ja Glück. Ich warte hier.', 'Vilicht hesch ja Glück. Ich warte do.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_FinishRobertQuest', 'dl_npc_robert2', 'Wow. How did you... Well, that doesn''t matter now. Thanks.', 'Wow. Wie hast du... Nun, das ist jetzt auch egal. Danke.', 'Wow. We hesch du... Eh, das isch ez au egal. Danke.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_FinishRobertQuest2', 'dl_npc_robert2', 'As promised, you get the inactive stone.', 'Wie versprochen, hier der inaktive Stein.', 'We versproche, do hesch de inaktiv Stei.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_FinishRobertQuest3', 'dl_npc_robert2', 'If you''d be a mage, you''d be able to infuse it with magic from a cairn wraith.', 'Wenn du ein Magier wärst, könntest du ihn mit der Magie eines Cairn Geistes aufladen.', 'Wennd en Magier wärsch, chöntschen mit de Magie vomne Steimännli Geischt uflade.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_FinishRobertQuest4', 'dl_npc_robert2', 'I think I''ve seen some of them in the Stone Garden in the North. ', 'Ich glaube, ich habe ein paar davon im Steingarten im Norden gesehen.', 'Ich glaub, ich ha paar vo dene im Steigarte im Norde gse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_FinishRobertQuest5', 'dl_npc_robert2', 'Good luck with it. I''m off then. (Robert takes the stone and disappears)', 'Viel Glück damit. Ich bin dann mal weg. (Robert nimmt den Stein und verschwindet)', 'Viel Glück demit. Ich bi denn mal weg. (De Robert nimmt de Stei und verschwindet)');
