INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Hello', 'dl_npc_robert2', 'There''s nothing to see here, just walk on.', 'Hier gibt''s nichts zu sehen, geh einfach weiter.', 'Do gits nüt z''gseh, lauf eifach witer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatDoing', 'dl_npc_robert2', 'What are you doing in the cemetery?', 'Was machst du auf dem Friedhof?', 'Was machsch du ufem Friedhof?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_robert2', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FromHere', 'dl_npc_robert2', 'Do you live here in this village?', 'Lebst du hier im Dorf?', 'Lebsch du do im Dorf?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Crypt', 'dl_npc_robert2', 'Have you been inside the crypt then?', 'Warst du denn in der Krypta?', 'Bisch denn ide Krypta gsi?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TeleportQuest', 'dl_npc_robert2', 'What''s in it for me if I help you get your stone back?', 'Was springt für mich raus, wenn ich dir helfe, den Stein zu holen?', 'Wa springt für mich use weni der hilf, de Stei z''hole?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Afraid', 'dl_npc_robert2', 'Why are you so afraid of that skeleton?', 'Wieso hast du solche Angst vor diesem Skelett?', 'Weso hesch so Schiss vor dem Skelett?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_StartRobertQuest', 'dl_npc_robert2', 'I''ll get your stone from the crypt.', 'Ich hole deinen Stein aus der Krypta.', 'Ich hole din Stei us de Krypta.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FinishRobertQuest', 'dl_npc_robert2', 'I got your stone.', 'Ich habe deinen Stein.', 'Ich ha din Stei.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Cairn', 'dl_npc_robert2', 'Have you seen any cairn wraiths around here?', 'Hast du hier irgendwelche Cairn Geister gesehen?', 'Hesch do irgendwelchi Steimännli Geister gse?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CU', 'dl_npc_robert2', 'See you.', 'Tschüss.', 'Tschüss.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_WhatDoing', 'dl_npc_robert2', 'Hmm, nothing. But I strongly advise you not to go down into the crypt. It''s scary there. ', 'Hmm, nichts. Aber ich würde dir davon abraten, in die Krypta hinunter zu gehen. Es spukt dort.', 'Hmmm, nüt. Aber ich würd dr dervo abroote, in d Krypta z''goh. Es isch gruselig dert.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_WhoAreYou', 'dl_npc_robert2', 'I''m Bob and I possess magical powers! Impressive, ain''t it?', 'Ich bin Bob und ich habe magische Kräfte! Beeindruckend, nicht?', 'Ich bin dr Bob und ich han magischi Kräft! Beihdruckend, odr?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_VeryImpressive', 'dl_npc_robert2', 'Very impressive, indeed.', 'Sehr beeindruckend, wirklich.', 'Sehr beidruckend, würkli.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotVeryImpressed', 'dl_npc_robert2', 'Hm. How''s that special?', 'Hm. Wieso ist das so aussergewöhnlich?', 'Hm. Wieso isch da so speziell?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_NotVeryImpressed', 'dl_npc_robert2', 'Not many are able to wield magic here. You''d better show some respect.', 'Nicht viele hier können Magie benutzen. Du solltest besser etwas Respekt zeigen.', 'Nit viel do chönne Magie benutze. Du sötsch besser e chli Reschpäkt zeige.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_LivingHere', 'dl_npc_robert2', 'No, I''m not from here. I''m just passing through.', 'Nein, ich bin nicht von hier. Nur auf der Durchreise.', 'Nei, ich bi nit vo do, nur uf dr Durchreis.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Crypt', 'dl_npc_robert2', 'Hm. Yes. Listen, I was just trying out my powers... and then a little mistake happened. ', 'Hm. Ja. Hör zu, ich wollte nur meine Kräfte ausprobieren und dann ist ein kleiner Unfall passiert.', 'Hm. Jä. Los, ich han nur welle mini Kräft usprobiere, und denne isch en chliine Unfall passiert.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Crypt2', 'dl_npc_robert2', 'A skeleton in the crypt suddenly started to move! I was so shocked that I ran out of the crypt and then I noticed that my teleport stone was missing. ', 'Ein Skelett in der Krypta hat sich plötzlich bewegt! Ich war so geschockt, dass ich hinausgerannt bin. Doch dann habe ich bemerkt, dass ich meinen Teleportstein in der Krypta verloren habe.', 'Es Skelett i dr Krypta het sich plötzlich bewegt! Ich bi so gschockt gsi, dass i us de Krypta gsegglet bin und denn han ich gmerkt, das ich mi Teleportstei dert verlore ha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Crypt3', 'dl_npc_robert2', 'But I really need this stone! I can''t get away without it.', 'Aber ich brauche diesen Stein wirklich! Ohne ihn komme ich nicht weg von hier.', 'Abr ich bruuch dr Stei würklich! Ohni ihn chum ich nid wäg vo do.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_TeleportQuest', 'dl_npc_robert2', 'You? You look like a mere commoner. I don''t think you can help me.', 'Du? Du siehst aus wie ein Gewöhnlicher. Ich glaube nicht, dass du mir helfen kannst.', 'Du? Du gsehsch us wie e Gwöhnliche. Ich glaub nit, dass du mer chasch hälfe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_TeleportQuest2', 'dl_npc_robert2', 'But if you manage to retrieve my stone... Well, I have an inactive teleport stone here.', 'Aber wenn es dir gelingt, meinen Stein zurückzuholen... Ich habe einen inaktiven Teleportstein hier.', 'Abr wenns schaffsch, min Stei zrugg z''hole... Ich ha en inaktive Stei do.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_TeleportQuest3', 'dl_npc_robert2', 'You could have it. It''s worth a lot to mages, so you could sell it.', 'Du könntest ihn haben. Er ist ziemlich wertvoll für Magier. Du könntest ihn verkaufen.', 'Dä chönntsch ha. Er isch zimlich wärtvoll für Magier. Du chönntsch en jo verkaufe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Afraid', 'dl_npc_robert2', 'I''m not afraid. Merely cautious.', 'Ich habe keine Angst, bin nur vorsichtig.', 'Ich ha kei Schiss, ich bi nur vorsichtig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_StartRobertQuest', 'dl_npc_robert2', 'Maybe you''re lucky. I''ll wait here.', 'Vielleicht hast du ja Glück. Ich warte hier.', 'Vilicht hesch jo Glück. Ich wart do.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_FinishRobertQuest', 'dl_npc_robert2', 'Wow. How did you... Well, that doesn''t matter now. Thanks.', 'Wow. Wie hast du... Nun, das ist jetzt auch egal. Danke.', 'Wow. Wie hesch du... Ähm, das isch jetz au egal. Danke.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_FinishRobertQuest2', 'dl_npc_robert2', 'As promised, here''s the inactive stone.', 'Wie versprochen, hier der inaktive Stein.', 'Wie versproche, do hesch dr inaktivi Stei.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_FinishRobertQuest3', 'dl_npc_robert2', 'If you were a mage, you''d be able to infuse it with magic from a cairn wraith.', 'Wenn du ein Magier wärst, könntest du ihn mit der Magie eines Cairn Geistes aufladen.', 'Wenn de en Magier wärsch, chöntsch en mit de Magie vomene Steimännli Geischt uflaade.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_FinishRobertQuest4', 'dl_npc_robert2', 'I think I just saw one going towards the windy plateau behind the crypt. ', 'Ich glaube, ich habe gerade gesehen, wie einer auf die windige Ebene hinter der Krypta gestiegen ist.', 'Ich glaub, ich han grad gseh, wie eine uf die windigi Ebeni hinder de Krypta gstiige isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_FinishRobertQuest5', 'dl_npc_robert2', 'Good luck with it. I''m off then. (Robert takes the stone and disappears)', 'Viel Glück damit. Ich bin dann mal weg. (Robert nimmt den Stein und verschwindet)', 'Viel Glück demit. Ich bin denn mol wäg. (De Robert nimmt de Stei und verschwindet)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Cairn', 'dl_npc_robert2', 'Hm... Maybe on the plateau behind the crypt.', 'Hm... Vielleicht auf der Ebene hinter der Krypta.', 'Hmm... Vilicht uf dr Ebeni hinter dr Krypta.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Cairn2', 'dl_npc_robert2', 'But you''d need to go through the crypt for that... (shivers)', 'Aber du müsstest durch die Krypta gehen dafür... (schaudert)', 'Abr du miestisch durch d''Krypta go defür... (schauderet)');
