INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_Congrats', 'dl_npc_robert', 'Oh, so you''re the new one here! I hope we can be friends then.', 'Oh, du bist also der Neue hier! Ich hoffe, wir können dann Freunde sein.', 'Oh, du bisch also de Neu da! Ich hoff, mir chönd Kollege si.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_robert', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HeyBob', 'dl_npc_robert', 'Hey Bob!', 'Hey Bob!', 'Hey Bob!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_WhoAreYou', 'dl_npc_robert', 'I''m Robert, you can call me Bob. ', 'Ich bin Robert, du kannst mich auch Bob nennen.', 'Ich bide Robert, du chaschmer au Bob sege.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_WhoAreYou2', 'dl_npc_robert', 'I''m new here, but already working like crazy to become a good Necromancer. Luiz says I have talent.', 'Ich bin neu hier, aber strenge mich schon sehr an, ein guter Nekromant zu werden. Luiz sagt, ich hätte Talent.', 'Ich bi neu da aber ich streng mi also scho sehr ah zum en guete Nekromant z''werde. De Luiz seit zumindest, ich heb Talent.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YouHaveTalent', 'dl_npc_robert', 'I''m sure you''ll do great.', 'Du wirst es bestimmt weit bringen.', 'Du wirsches sicher guet mache.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoTalent', 'dl_npc_robert', 'At least someone who thinks that way.', 'Wenigstens denkt jemand so.', 'Wenigstens öpper, he.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_YouHaveTalent', 'dl_npc_robert', 'Thank you. I guess you could learn a lot too, maybe with a bit more time.', 'Danke. Ich denke du könntest auch vieles lernen, vielleicht mit ein bisschen mehr Zeit.', 'Danke. Ich denk du chöntsch au viel lerne, vilicht mit chli me Ziit.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_NoTalent', 'dl_npc_robert', 'Better than no one. Looking at you...', 'Besser als niemand und ich sehe dich an...', 'Besser als niemert weni mer dich so alueg...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Robert_HeyBob', 'dl_npc_robert', '(Surprised) You, here? So you''re a mage after all...', '(Überrascht) Du, hier? Du bist also doch ein Magier...', '(Überrascht) Du, do inne? Du bisch also doch en Magier...');
