INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Royalguard_Hey', 'dl_npc_royalguard_t', 'Hey you! How dare you to walk in here without permission!', 'Hey du! Wie kannst du es wagen, hier einfach ohne Bewilligung hineinzuspazieren!', 'He du! Wiä chaschäs waagä, da eifach inä z''spazierä ohni ä Bewiuigung.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Royalguard_Hey2', 'dl_npc_royalguard_t', 'I really hope you have a good reason, otherwise I''ll have to let you out immediately.', 'Ich hoffe wirklich, du hast einen guten Grund. Sonst muss ich dich nämlich wieder hinausbefördern.', 'Ig hoffä das würkläch, ä guetä Grund hesch. Süsch muessi di nämlech wider usegheiä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Velius', 'dl_npc_royalguard_t', 'I''m looking for some mage called "Velius".', 'Ich suche einen Magier namens "Velius".', 'Ich suech en Magier namens "Velius".');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_King', 'dl_npc_royalguard_t', 'I''d like to have an audience with the king.', 'Ich möchte eine Audienz mit dem König haben.', 'Ich het gern a Audienz mitem König.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Royalguard_Velius', 'dl_npc_royalguard_t', 'The royal highmage is very busy. He does not receive any visitors.', 'Der königliche Hochmagier ist äusserst beschäftigt. Er empfängt keine Besucher.', 'Dr königlech Hochmagier isch schampar beschäftiget. Är empfaht ke Bsuech.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Royalguard_King', 'dl_npc_royalguard_t', 'And why should our highness King Logan III waste his time talking to you?', 'Und wieso sollte unsere Hoheit König Logan III seine Zeit mit dir verschwenden?', 'Und weso söu üsä König Logan III si Zit mit dir vergüdä?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Charming', 'dl_npc_royalguard_t', 'I''d love to get to know the king. ', 'Ich würde den König wirklich gerne kennenlernen.', 'Ich wür de König würkli gern kennelerne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoneBusiness', 'dl_npc_royalguard_t', 'That''s none of your business. I only talk to the king himself.', 'Das geht dich nichts an. Ich rede nur mit dem König persönlich.', 'Das goht di nüt ah. Ich red nur mitem König persönlich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mark', 'dl_npc_royalguard_t', 'I''m a member. (Show the barely visible mark on your wrist)', 'Ich gehöre dazu. (Das verblasste Mal auf dem Handgelenk zeigen)', 'Ich ghör dezue. (Es verblasste Zeiche ufem Handglenk zeige)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Back', 'dl_npc_royalguard_t', 'Ehm... Never mind. [BACK]', 'Ehm... Auch egal. [ZURÜCK]', 'Ehm... Au egal. [ZRUGG]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Royalguard_Charming', 'dl_npc_royalguard_t', 'Well, I''m pretty sure he doesn''t share this intention. Bugger off!', 'Nun, ich bin mir ziemlich sicher, dass er diesen Wunsch nicht mit dir teilt. Schwirr ab.', 'Hm, ig bi mir zimläch sicher, das är dä Wunsch nid mit dir teut. Fahr ab.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Royalguard_NoneBusiness', 'dl_npc_royalguard_t', 'Hn. Everyone could tell that. We don''t just let everyone to the king. ', 'Hn. Jeder könnte das sagen. Wir lassen nicht einfach jeden zum König.', 'Hm... Jedä chönnt das verzöuä. Mir löh ni eifach jedä zum König.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Royalguard_Mark', 'dl_npc_royalguard_t', 'If that''s so... (he stares at your mark) ...you can have a very short audience.', 'Wenn das so ist... (er starrt auf dein Mal) ...kannst du eine sehr kurze Audienz haben.', 'We das so isch... (er starrt uf dis Zeiche) ...chasch ä ganz churzi Audiänz ha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Royalguard_Mark2', 'dl_npc_royalguard_t', 'Follow me.', 'Folge mir.', 'Chumm mit.');
