INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Hi', 'dl_npc_runa', 'Hi.', 'Hey.', 'Hey.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Hi', 'dl_npc_runa', 'Oh, hello there! If you want to buy something from our farm, talk to my son Tristan.', 'Oh, hallo! Wenn du hier bist, um etwas von unserem Bauernhof zu kaufen, rede mit meinem Sohn, Tristan.', 'Oh, hoi! Wänn du da bisch, zum vo eus öpis vom Buurehof z''chaufä, gang und red mit mim Sohn, em Tristan.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_runa', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Job', 'dl_npc_runa', 'Do you have a job for me?', 'Hast du Arbeit für mich?', 'Hesch Arbet für mich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_SheepDone', 'dl_npc_runa', 'I took care of the sheep thief.', 'Ich hab mich um den Schafsdieb gekümmert.', 'Ich ha mi um de Schöflidieb kümmeret.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Delivery', 'dl_npc_runa', 'Tell me more about that delivery.', 'Erzähl mir mehr von dieser Lieferung.', 'Verzell mer me vo dere Lieferig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RunasDealStart', 'dl_npc_runa', 'Okay, give me the Physalis; I''ll take them to Bjarne.', 'Okay, gib mir die Physalis, ich werde sie Bjarne bringen.', 'Okay, gib mer die Physalis, ich wird si em Bjarne bringe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoBjarne', 'dl_npc_runa', 'Who is "Bjarne"?', 'Wer ist dieser "Bjarne"?', 'Wer isch de "Bjarne"?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Paladins', 'dl_npc_runa', 'What is your problem with the paladins?', 'Was habt ihr denn für Probleme mit den Paladinen?', 'Wa hender denn für Problem mit de Paladin?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoMage', 'dl_npc_runa', 'Is this Bjarne a mage?', 'Ist dieser Bjarne ein Magier?', 'Isch de Bjarne en Magier?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Pendant', 'dl_npc_runa', 'What is this "precious pendant" exactly?', 'Was ist denn dieser "wertvoller Anhänger" genau?', 'Wa isch denn de "wertvoll Ahänger" genau?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Physalis', 'dl_npc_runa', 'What exactly are "Physalis"?', 'Was genau sind "Physalis"?', 'Was genau sind die "Physalis"?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyProtect', 'dl_npc_runa', 'Why do you need protection against magic?', 'Wieso brauchst du Schutz gegen Magie?', 'Weso bruchsch du Schutz gege Magie?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Paranoid', 'dl_npc_runa', 'I think you''re a bit too paranoid...', 'Ich glaube, du bist ein bisschen paranoid...', 'Ich glaub, du bisch bitz paranoid...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotPendant', 'dl_npc_runa', 'I got your pendant. Here.', 'Ich habe deinen Anhänger. Hier.', 'Ich ha din Ahänger. Do.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_WhoAreYou', 'dl_npc_runa', 'I''m Runa and I''m working as a farmer here. From time to time, I also help out in the River Inn.', 'Ich bin Runa und ich arbeite als Bäuerin hier. Manchmal helfe ich auch in der Taverne am Fluss etwas aus.', 'Ich bin d''Runa und ich schaff da als Büürin. Mängisch hilf ich au i dä Taverne am Fluss chli us.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_WhoAreYou2', 'dl_npc_runa', 'And you? Are you just running around, asking everyone who they are?', 'Und du? Rennst du einfach hier rum und fragst jeden, wer er sei?', 'Und du? Rännsch du da eifach chli ume und frögsch jede, wer er seig?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AskEveryoneYes', 'dl_npc_runa', 'Yes...', 'Ja...', 'Jo...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AskEveryoneNo', 'dl_npc_runa', 'No.', 'Nein.', 'Nei.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IJustWantedToTalk', 'dl_npc_runa', 'I just wanted to have a little chat.', 'Ich wollte doch nur ein bisschen reden.', 'Ich ha doch nur chli rede wele.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_AskEveryoneYes', 'dl_npc_runa', 'Ach... the youth of today... as if there wasn''t enough work to do already!', 'Ach... die Jugend von heute... als ob es nicht genügend andere Arbeit zu tun gäbe!', 'Ach... d''Jugend vo hüt... als ob''s nöd gnueg anderi Arbet gäbt z''machä!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_AskEveryoneNo', 'dl_npc_runa', 'You mean, I''m special? Hah, now I''m flattered.', 'Dann bin ich also speziell? Da fühl ich mich ja gleich geschmeichelt.', 'Dänn bin ich also speziell? Da fühl ich mich ja grad gschmeichlet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_IJustWantedToTalk', 'dl_npc_runa', 'Not everyone has time for a little chitchat. There''s still so much work to be done!', 'Nicht jeder hat Zeit für ein kleines Schwätzchen. Es gibt immer noch so viel Arbeit zu erledigen!', 'Nöd jedä hät Ziit für es Schwätzli. Es git immer no so viel Arbet z''erledigä!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Job', 'dl_npc_runa', 'Hm. We do have a problem with disappearing sheep. If you want to help, talk to my husband, Ivo.', 'Hm. Wir haben ein Problem mit Schafen, die verschwinden. Wenn du uns hier helfen willst, sprich mit meinem Mann, Ivo.', 'Hm. Mir händ es Problem mit Schöfli, wo verschwindet. Wänn du eus hälfe wottsch, red mit em Ivo, min Ma.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Job2', 'dl_npc_runa', 'Other than that, I could need some help with a delivery to Gandria. ', 'Ausserdem könnte ich etwas Hilfe mit einer Lieferung nach Gandria gebrauchen.', 'Usserdem chönt ich echli Hilf mit erä Lieferig nach Gandria bruchä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_SheepDone', 'dl_npc_runa', 'Well done. I hope you already got your reward from Tristan.', 'Gut gemacht. Ich hoffe, du hast dir deine Belohnung schon von Tristan geholt.', 'Guet gmacht. Ich hoff, du häsch dir diini Belohnig scho vom Tristan gholt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Delivery', 'dl_npc_runa', 'The last time I was in the market in Gandria, some guy called Bjarne bought all of my Physalis.', 'Letztes Mal, als ich auf dem Markt in Gandria war, hat mir ein Typ namens Bjarne all meine Physalis abgekauft.', 'Letschtmal, wo ich ufem Märt in Gandria gsi bin, hät mir en Typ namens Bjarne all miini Physalis abkauft.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Delivery2', 'dl_npc_runa', 'He then asked me to bring him even more - in exchange, he would give me a very precious pendant.', 'Er wollte noch mehr davon - als Gegenleistung würde er mir einen sehr wertvollen Anhänger überlassen.', 'Er hät sogar no meh vo dem wellä – als Gägeleistig würd er mir dänn en ganz wertvolle Ahänger geh.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Delivery3', 'dl_npc_runa', 'I would go myself... But at the moment, the situation with the paladins in Gandria is a bit tense.', 'Ich würde ja selbst gehen... Aber momentan ist die Situation mit den Paladinen in Gandria etwas angespannt.', 'Ich würd ja sälber gah... Aber momentan isch d''Situation mit dä Paladin in Gandria echli agspannt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_RunasDealStart', 'dl_npc_runa', 'Good! Here you go. Don''t forget to bring me the pendant!', 'Gut! Hier hast du sie. Vergiss nicht, mir den Anhänger zu bringen!', 'Guet! Da häsch sie. Vergiss nöd, mir dä Ahänger z’bringä!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_WhoBjarne', 'dl_npc_runa', 'Hm. I don''t really remember how he looked like. But he sure was handsome and likeable.', 'Hm. Ich mag mich nicht wirklich daran erinnern, wie er ausgesehen hat. Aber er war ziemlich hübsch und sympathisch.', 'Hm. Ich chan mich nöd würklich dra erinnerä, wie er usgseh hät. Aber er isch zimlich hübsch und sympathisch gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Paladins', 'dl_npc_runa', 'They should be there to protect us, but they only serve their "god" and not the people of Admantris.', 'Sie sollten uns eigentlich beschützen, aber sie dienen nur ihrem "Gott" und nicht den Bürgern von Admantris.', 'Sie söttet eus eigentlich beschützä, aber sie diened nur ihrem "Gott" und nöd dä Lüüt vo Admantris.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Paladins2', 'dl_npc_runa', 'I really don''t know who thought it would be a good idea to put them in charge.', 'Ich weiss wirklich nicht, wer es für eine gute Idee gehalten hat, die unser Land führen zu lassen.', 'Ich weiss würklich nöd, wer dänkt hät, es seig e gueti Idee, wänn die euses Land füehred.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_WhoMage', 'dl_npc_runa', 'No, definitely not. I prefer not to talk to mages.', 'Nein, definitiv nicht. Ich ziehe es vor, nicht mit Magiern zu sprechen.', 'Nei, definitiv nöd. Ich zieh''s vor, nöd mit Magier z''redä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TalkingMage', 'dl_npc_runa', 'But you''re talking to one right now...', 'Aber du redest gerade mit einem...', 'Aber du redsch grad mit eim...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyHate', 'dl_npc_runa', 'What is your problem with mages?', 'Was hast du gegen Magier?', 'Wa hesch denn gege Magier?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_TalkingMage', 'dl_npc_runa', 'Whaaat?', 'Waaaas?', 'Waaaas?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_WhyHate', 'dl_npc_runa', 'Why would you even ask! They''re elitist monsters, that''s why.', 'Was für eine Frage! Das sind alle elitäre Monster, mehr gibt''s dazu nicht zu sagen.', 'Was für ä Frag! Das sind alli elitäri Monster, meh gits da däzue nöd z''säge.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Pendant', 'dl_npc_runa', 'It''s an anti-magic pendant. It protects from curses and other magic attacks. Exactly what I need!', 'Es ist ein antimagischer Anhänger. Er schützt gegen Flüche und andere magische Attacken - genau, was ich brauche!', 'Es isch en antimagische Ahänger. Er schützt gäge Flüech und anderi magischi Attackene – genau, was ich bruch!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Physalis', 'dl_npc_runa', 'It''s a nightshade plant which we grow on our farm. It''s pretty sweet, but eating too much can be poisonous.', 'Ein Nachtschattengewächs, das auf unserem Bauernhof wächst. Es ist ziemlich süss, aber zu viel davon kann giftig sein.', 'Es Nachtschattägwächs, wo uf eusem Buurehof wachst. Es isch ziemlich süess, aber z''viel dävo chan giftig si.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_WhyProtect', 'dl_npc_runa', 'Because that''s the only way we can stand a chance when they attack... you better get a pendant, too.', 'Weil das unsere einzige Überlebenschance ist, wenn sie uns angreifen... du solltest dir besser auch einen Anhänger zu tun.', 'Will das eusi einzigi Überläbenschance isch, wenn sie eus agriifed... Du söttsch dir besser au sonen Ahänger bsorgä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Paranoid', 'dl_npc_runa', 'Paranoid? Me? Ha! Just wait, you''ll see...', 'Paranoid? Ich? Ha! Einfach mal abwarten, du wirst schon sehen...', 'Paranoid? Ich? Ha! Eifach mal abwartä, wirsch dänn scho gseh...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_GotPendant', 'dl_npc_runa', 'Oooh! (Runa seizes the pendant)', 'Oooh! (Runa reisst den Anhänger an sich)', 'Oooh! (D''Runa riist de Ahänger a sich)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_GotPendant2', 'dl_npc_runa', 'Thank you so much! Now, I''m finally safe from this wretched magic.', 'Vielen Dank! Jetzt bin ich endlich sicher vor dieser elenden Magie.', 'Tuusig Dank! Jetzt bin ich äntli sicher vor derä elendä Magie.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_GotPendant3', 'dl_npc_runa', 'Here, take these goods from our farm as your reward.', 'Hier, nimm dieser Waren von unserem Hof als deine Belohnung.', 'Da, nimm die Waar vo eusem Hof als diini Belohnig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_EvilMage', 'dl_npc_runa', 'Be gone, filthy mage!', 'Verschwinde von hier, dreckiger Magier!', 'Verschwind vo da, dräckigä Magier!');
