INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Hi', 'dl_npc_runa', 'Hi.', 'Hey.', 'Hey.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Hi', 'dl_npc_runa', 'Oh, hello there! If you want to buy something from our farm, talk to my son Tristan.', 'Oh, hallo! Wenn du hier bist, um etwas von unserem Bauernhof zu kaufen, rede mit meinem Sohn, Tristan.', 'Oh, hoi! Wennd do bisch, zum öppis vo üsem Buurehof z''chaufe, gang und red mit mim Sohn, em Tristan.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_runa', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Job', 'dl_npc_runa', 'Do you have a job for me?', 'Hast du Arbeit für mich?', 'Hesch Arbet für mich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_SheepDone', 'dl_npc_runa', 'I took care of the sheep thief.', 'Ich hab mich um den Schafsdieb gekümmert.', 'Ich ha mi um de Schöflidieb kümmeret.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Delivery', 'dl_npc_runa', 'Tell me more about that delivery.', 'Erzähl mir mehr von dieser Lieferung.', 'Verzell mer me vo dere Lieferig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RunasDealStart', 'dl_npc_runa', 'Okay, give me the Physalis, I''ll take them to Bjarne.', 'Okay, gib mir die Physalis, ich werde sie Bjarne bringen.', 'Okay, gib mer die Physalis, ich wird si em Bjarne bringe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoBjarne', 'dl_npc_runa', 'Who is that "Bjarne"?', 'Wer ist dieser "Bjarne"?', 'Wer isch de "Bjarne"?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Paladins', 'dl_npc_runa', 'What is your problem with the paladins?', 'Was habt ihr denn für Probleme mit den Paladinen?', 'Wa hender denn für Problem mit de Paladin?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoMage', 'dl_npc_runa', 'Is this Bjarne a mage?', 'Ist dieser Bjarne ein Magier?', 'Isch de Bjarne en Magier?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Pendant', 'dl_npc_runa', 'What is this "precious pendant" exactly?', 'Was ist denn dieser "wertvoller Anhänger" genau?', 'Wa isch denn de "wertvoll Ahänger" genau?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Physalis', 'dl_npc_runa', 'What exactly are "Physalis"?', 'Was genau sind "Physalis"?', 'Was genau sind die "Physalis"?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyProtect', 'dl_npc_runa', 'Why do you need protection against magic?', 'Wieso brauchst du Schutz gegen Magie?', 'Weso bruchsch du Schutz gege Magie?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Paranoid', 'dl_npc_runa', 'I think, you''re a bit too paranoid...', 'Ich glaube, du bist ein bisschen paranoid...', 'Ich glaub, du bisch bitz paranoid...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotPendant', 'dl_npc_runa', 'I got your pendant. Here.', 'Ich habe deinen Anhänger. Hier.', 'Ich ha din Ahänger. Do.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_WhoAreYou', 'dl_npc_runa', 'I''m Runa and I''m working as a farmer here. From time to time, I also help out in the River Inn.', 'Ich bin Runa und ich arbeite als Bäuerin hier. Manchmal helfe ich auch in der Taverne am Fluss etwas aus.', 'Ich bi d''Runa und ich schaff als Büürin do. Mengsmol hilfi au ide Taverne am Fluss chli uus. ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_WhoAreYou2', 'dl_npc_runa', 'And you? Are you just running around and ask everyone who they are?', 'Und du? Rennst du einfach hier rum und fragst jeden, wer er sei?', 'Und du? Rennsch du eifach do chli ume und frögsch jede, wer er seg?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AskEveryoneYes', 'dl_npc_runa', 'Yes...', 'Ja...', 'Jo...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AskEveryoneNo', 'dl_npc_runa', 'No.', 'Nein.', 'Nei.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IJustWantedToTalk', 'dl_npc_runa', 'I just wanted to have a little chat.', 'Ich wollte doch nur ein bisschen reden.', 'Ich ha doch nur chli rede wele.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_AskEveryoneYes', 'dl_npc_runa', 'Ah... the youth of today... as if there wasn''t enough other work to do.', 'Ach... die Jugend von heute... als ob es nicht genügend andere Arbeit zu tun gäbe.', 'Ach... d''Jugend vo hüt... als obs nid gnueg anderi Arbet z''tue gäbt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_AskEveryoneNo', 'dl_npc_runa', 'You mean, I''m special? Hah, now I''m flattered.', 'Dann bin ich also speziell? Da fühl ich mich ja gleich geschmeichelt.', 'Denn bini also speziell? Do fühli mi jo grad gschmeichlet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_IJustWantedToTalk', 'dl_npc_runa', 'Not everyone has time for a little chitchat. There''s still so much work to be done!', 'Nicht jeder hat Zeit für ein kleines Schwätzchen. Es gibt immer noch so viel Arbeit zu erledigen!', 'Nid jede het Ziit für es chlises Schwätzli. Es git immer no so viel Arbet z''erledige!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Job', 'dl_npc_runa', 'Hm. We do have a problem with disappearing sheep. If you want to help, talk to my husband, Ivo.', 'Hm. Wir haben ein Problem mit Schafen, die verschwinden. Wenn du uns hier helfen willst, sprich mit meinem Mann, Ivo.', 'Hm. Mir hend es Problem mit Schöfli, wo verschwindet. Wennd üs do helfe wetsch, red mitem Ivo, mim Ma.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Job2', 'dl_npc_runa', 'Other than that, I could need some help with a delivery to Gandria. ', 'Ausserdem könnte ich etwas Hilfe mit einer Lieferung nach Gandria gebrauchen.', 'Usserdem chönti chli Hilf mit enere Lieferig noch Gandria bruche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_SheepDone', 'dl_npc_runa', 'Well done. I hope you already got your reward from Tristan.', 'Gut gemacht. Ich hoffe, du hast dir deine Belohnung schon von Tristan geholt.', 'Guet gmacht. Ich hoff, du hesch der dini Belohnit scho vom Tristan gholt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Delivery', 'dl_npc_runa', 'The last time I was in the market in Gandria, some guy called Bjarne bought all of my Physalis.', 'Letztes Mal, als ich auf dem Markt in Gandria war, hat mir ein Typ namens Bjarne all meine Physalis abgekauft.', 'Letztmol, woni ufem Markt i Gandria gsi bi, het mer en Typ namens Bjarne all mini Physalis abkauft.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Delivery2', 'dl_npc_runa', 'He then asked me to bring him even more - in exchange, he would give me a very precious pendant.', 'Er wollte noch mehr davon - als Gegenleistung würde er mir einen sehr wertvollen Anhänger überlassen.', 'Er het sogar no me devo wele - als Gegeleistig würer mir denn en ganz wertvolle Ahänger gä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Delivery3', 'dl_npc_runa', 'I would go myself... But at the moment, the situation with the paladins in Gandria is a bit tense.', 'Ich würde ja selbst gehen... Aber momentan ist die Situation mit den Paladinen in Gandria etwas angespannt.', 'Ich wür jo selber go... Aber momentan isch d''Situation mit de Paladin in Gandria chli agspannt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_RunasDealStart', 'dl_npc_runa', 'Good! Here you go. Don''t forget to bring me the pendant!', 'Gut! Hier hast du sie. Vergiss nicht, mir den Anhänger zu bringen!', 'Guet! Do hesch si. Vergiss nid, mer de Ahänger z''bringe!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_WhoBjarne', 'dl_npc_runa', 'Hm. I don''t really remember how he looked like. But he sure was handsome and likeable.', 'Hm. Ich mag mich nicht wirklich daran erinnern, wie er ausgesehen hat. Aber er war ziemlich hübsch und sympathisch.', 'Hm. ich cha mich nid würkli dra erinnere, we er usgse het. Aber er isch zimli hübsch und sympathisch gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Paladins', 'dl_npc_runa', 'They should be there to protect us, but they only serve their "god" and not the people of Admantris.', 'Sie sollten uns eigentlich beschützen, aber sie dienen nur ihrem "Gott" und nicht den Bürgern von Admantris.', 'Si sötted üs eigentlich beschütze, aber si diened nur ihrem "Gott" und nid de Lüt vo Admantris.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Paladins2', 'dl_npc_runa', 'I really don''t know who thought it would be a good idea to put them in charge.', 'Ich weiss wirklich nicht, wer es für eine gute Idee gehalten hat, die unser Land führen zu lassen.', 'Ich weiss würkli nid, wer denkt het, es seg e gueti Idee, wenn die üses Land führed.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_WhoMage', 'dl_npc_runa', 'No, definitely not. I prefer not to talk to mages.', 'Nein, definitiv nicht. Ich ziehe es vor, nicht mit Magiern zu sprechen.', 'Nei, definitiv nid. Ich zie''s vor, nid mit Magier z''rede.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TalkingMage', 'dl_npc_runa', 'But you''re talking to one right now...', 'Aber du redest gerade mit einem...', 'Aber du redsch grad mit eim...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyHate', 'dl_npc_runa', 'What is your problem with mages?', 'Was hast du gegen Magier?', 'Wa hesch denn gege Magier?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_TalkingMage', 'dl_npc_runa', 'Whaaat?', 'Waaaas?', 'Waaaas?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_WhyHate', 'dl_npc_runa', 'Why would you even ask! They''re elitist monsters, that''s why.', 'Was für eine Frage! Das sind alle elitäre Monster, mehr gibts dazu nicht zu sagen.', 'Was für e Frog! Das sind alli elitäri Monster, me gits do dezue nid z''sege.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Pendant', 'dl_npc_runa', 'It''s an anti-magic pendant. It protects from curses and other magic attacks. Exactly what I need!', 'Es ist ein antimagischer Anhänger. Er schützt gegen Flüche und andere magische Attacken - genau, was ich brauche!', 'Es isch en antimagische Ahänger. Er schützt geg Flüech und anderi magischi Attackene - genau, wani bruch!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Physalis', 'dl_npc_runa', 'It''s a nightshade plant which we grow on our farm. It''s pretty sweet, but eating too much can be poisonous.', 'Ein Nachtschattengewächs, das auf unserem Bauernhof wächst. Es ist ziemlich süss, aber zuviel davon kann giftig sein.', 'Es Nachtschattegwächs, wo uf üsem Buurehof wachst. Es isch zimli süess, aber z''viel devo cha giftig si.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_WhyProtect', 'dl_npc_runa', 'Because that''s the only way we can stand a chance when they attack... you should better get a pendant, too.', 'Weil das unsere einzige Überlebenschance ist, wenn sie uns angreifen... du solltest dir besser auch einen Anhänger zutun.', 'Well das üsi einzig Überlebenschance isch, wenn si üs agriifed... Du sötsch der besser au sonen Ahänger bsorge.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_Paranoid', 'dl_npc_runa', 'Paranoid? Me? Ha! Just wait, you''ll see...', 'Paranoid? Ich? Ha! Einfach mal abwarten, du wirst schon sehen...', 'Paranoid? Ich? Ha! Eifach mol abwarte, wirsch denn scho gse...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_GotPendant', 'dl_npc_runa', 'Oooh! (Runa seizes the pendant)', 'Oooh! (Runa reisst den Anhänger an sich)', 'Oooh! (D''Runa riist de Ahänger a sich)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_GotPendant2', 'dl_npc_runa', 'Thank you so much! Now, I''m finally safe from this magic.', 'Vielen Dank! Jetzt bin ich endlich sicher vor dieser Magie.', 'Tusig Dank! Jetz bini endli sicher vor dere Magie.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_GotPendant3', 'dl_npc_runa', 'Here, take these goods from our farm as your reward.', 'Hier, nimm dieser Waren von unserem Hof als deine Belohnung.', 'Do, nimm die War vo üsem Hof als dini Belohnig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Runa_EvilMage', 'dl_npc_runa', 'Be gone, filthy mage!', 'Verschwinde von hier, dreckiger Magier!', 'Verschwind vo do, dreckige Magier!');
