INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_ANewFace', 'dl_npc_stephen', 'And again, a new face in the city. As if we hadn''t already enough troublemakers here. ', 'Und wieder ein neues Gesicht in der Stadt. Als hätten wir nicht schon genug Störenfriede hier.', 'Und wider es neus Gsicht ide Stadt. Als hettemer nid scho gnueg Störefried da.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_YouShouldKnowRules', 'dl_npc_stephen', 'You should know the rules of the city, if you don''t want to end up jailed.', 'Du solltest die Regeln dieser Stadt kennen, wenn du nicht im Gefängnis landen willst. ', 'Du sötsch d''Regle vo dere Stadt kenne, wend nid wetsch hinter Gitter cho.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ExplainRules', 'dl_npc_stephen', 'Explain me the rules of the city.', 'Erklär mir die Regeln der Stadt.', 'Erklär mer d''Regle vode Stadt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_stephen', 'Who are you?', 'Wer bist du eigentlich?', 'Wer bisch du überhaupt?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_choice_NoTime', 'dl_npc_stephen', 'I don''t have time for this.', 'Ich habe keine Zeit für das.', 'Ich han grad ke Ziit für das.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Lloyd', 'dl_npc_stephen', 'I need to talk to some "Lloyd" here... (Show the letter)', 'Ich muss hier mit so einem "Lloyd" reden... (Brief zeigen)', 'Ich mu da mit somne "Lloyd" rede... (Brief zeige)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CU', 'dl_npc_stephen', 'See you.', 'Bis später.', 'Bis spöter.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_Rules1', 'dl_npc_stephen', 'Well, first, you should know that the Order of the Eternal Light is in charge here. Therefore, respect us and follow our orders as long as you''re here, or it won''t be a very pleasant stay in Gandria.', 'Nun, zuerst solltest du wissen, dass der Orden des Ewigen Lichts hier regiert - also respektiere uns und unsere Gesetze, sonst wird das ein nicht sehr angenehmer Aufenthalt für dich in Gandria.', 'Zersch mal sött der klar si, dass de Orde vom Ewige Liecht da regiert - also halt dich besser a üsi Gsetz, susch wird das en nid sehr agnehme Ufenthalt für dich in Gandria.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_Rules2', 'dl_npc_stephen', 'Behave well if you go into a stranger''s house - we don''t like thieves here. Most citizens of Gandria use observer spells to avoid being robbed.', 'Benimm dich wenn du ein fremdes Haus betrittst - Wir mögen keine Diebe hier. Die meisten Bürger hier haben Beobachter-Zauber, um Diebstähle zu verhindern.', 'Benimm dich wend ines fremds Huus gasch - Mer gsehnd Diebe nid gern da. Di meiste Bürger hend Beobachter-Zauber, wo verhinderet, das öpper öppis chluppet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_Rules3', 'dl_npc_stephen', 'And, last but not least, we don''t really like seeing other spells than those which originate from the high divine art. You can hang for practicing Necromancy.', 'Und, zum Schluss: Wir sehen es nicht sonderlich gerne, wenn jemand Zauber wirkt, die nicht zur hohen Kunst der heiligen Magie gehören. Wenn wir dich bei Nekromantie erwischen, kannst du aufgeknöpft werden.', 'Und, zum Schluss: Mir gsehnds nid bsunders gern, wenn öpper mit Zauber hantiert, wo nid zude hoche heilige Schuel ghöred. Wennd bi Nekromantie verwütscht wirsch, chasch ufknöpft werde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_Okay', 'dl_npc_stephen', 'Okay.', 'Okay.', 'Okay.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_WhoAreYou', 'dl_npc_stephen', 'I''m Stephen, paladin and vigilante of Gandria and so to say directly the right hand of our Commander, Lloyd. Don''t mess with me or my city, understood?', 'Ich bin Stephen, Paladin und Ordnungshüter von Gandria, sozusagen die rechte Hand von unserem Kommandanten, Lloyd. Leg dich nicht mit mir oder meiner Stadt an, verstanden?', 'Ich bi de Stephen, Paladin und Gsetzeshüeter vo Gandria, sozsege grad di recht Hand vom Kommandant Lloyd. Leg dich nid mit mir oder minere Stadt ah, verstande? ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_choice_Understood', 'dl_npc_stephen', 'Understood.', 'Verstanden.', 'Verstande.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_MaybeUnderstood', 'dl_npc_stephen', 'I''ll try.', 'Ich werde es versuchen.', 'Ich wirds probiere.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotUnderstood', 'dl_npc_stephen', '... (Mischievous grin)', '... (Schelmisch grinsen)', '... (Schelmisch grinse)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_IKeepEyeOnYou', 'dl_npc_stephen', 'I''ll keep an eye on you, boy.', 'Ich werde dich im Auge behalten, Junge.', 'Dich wirdi im Aug phalte, Bürschtli.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_DontTrouble', 'dl_npc_stephen', 'Well then... Don''t make trouble!', 'Na dann... Mach bloss keinen Ärger!', 'Ja dänn... Mach bloss ken Ärger!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Stephen_Laughter', 'dl_npc_stephen', '(Stephen takes the letter and then starts laughing uncontrollably)', '(Stephen nimmt den Brief und beginnt, unbeherrscht zu lachen)', '(De Stephen nimmt de Brief und fangt denn völlig unkontrolliert ah lache.)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Stephen_PieceOfPaper', 'dl_npc_stephen', 'And you thought this piece of paper will bring you to him? That''s truly ridiculous.', 'Und du hast gedacht, dieser Papierfetzen wird dich zu ihm bringen? Das ist lächerlich.', 'Und du hesch würkli denkt, dass de Papierfetze dich zu ihm bringe wird? Da isch lächerlich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Urgent', 'dl_npc_stephen', 'But it''s urgent!', 'Aber es ist dringend!', 'Aber es isch dringend!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Inina', 'dl_npc_stephen', 'But this letter is from the High Priestess Inina!', 'Aber dieser Brief ist von der Hohepriesterin Inina!', 'Aber de Brief isch vode Hohepriesterin Inina!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Intimidate', 'dl_npc_stephen', 'Just let me to him and no one gets hurt.', 'Lass mich einfach zu ihm und niemand wird verletzt.', 'Lah mich eifach zu ihm und niemert wird verletzt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Stephen_Urgent', 'dl_npc_stephen', 'Sure it is. But there are other urgent things too. Like what you''ll have to do to get your letter back.', 'Natürlich. Aber es gibt auch andere dringende Sachen. So wie das, was du für mich zu erledigen hast, wenn du deinen Brief wieder willst.', 'Natürlich. Aber es git au anderi dringendi Sache. So we da, wa du für mich erledige muesch, wend din Brief wider wetsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Stephen_Inina', 'dl_npc_stephen', 'Good for you. But you can''t talk to her either until you''ve taken care of my tiny problem. Then you''ll get your letter back.', 'Schön für dich. Aber du kannst auch nicht mit ihr sprechen bis du eine kleine Sache für mich erledigt hast. Dann wirst du deinen Brief zurück kriegen.', 'Schön für dich. Aber du chasch au nid mit ihre rede bis du e chlini Sach für mich erledigt hesch. Denn bechunsch au din Brief wieder.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Stephen_Intimidate', 'dl_npc_stephen', 'Ah, you''re a tough boy aren''t you. But I think I''ll have to keep your letter for a while - until you''ve taken care of some problem of mine...', 'Ah, du bist ein harter Junge, nicht wahr. Aber ich glaube, ich muss deinen Brief noch ein bisschen behalten - bis du eine Sache für mich erledigt hast.', 'Ah, du bisch en herte Siech, he. Aber ich mu din Brief glaub na bitz phalte - bis du so e Sach für mich erledigt hesch...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TellmeQuest', 'dl_npc_stephen', '(Sigh) Okay, what should I do for you?', '(Seufz) Okay, was soll ich für dich tun?', '(Süüfz) Okay, wa söll ich für dich mache?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotHelmet', 'dl_npc_stephen', 'I got your helmet, now give me that letter back.', 'Ich habe deinen Helm, gib mir jetzt meinen Brief wieder.', 'Ich ha din Helm, gibmer ez min Brief wider.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Stephen_HelmetQuest', 'dl_npc_stephen', 'I got a little bit drunk last night and lost my helmet to some shady guys in the tavern. I''m pretty sure that they belong to the dirty thief guild.', 'Ich habe letzte Nacht ein bisschen über den Durst getrunken und meinen Helm an ein paar zwielichtige Gestalten in der Taverne verloren. Ich bin mir ziemlich sicher, dass sie zur dreckigen Diebesgilde gehören.', 'Ich ha letzt Nacht chli zviel trunke und ha min Helm a paar komischi Gstalte ide Taverne verlore. Bimer zimli sicher dass si zude dreckige Diebesgilde ghöred.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Stephen_HelmetQuest2', 'dl_npc_stephen', 'I need someone that doesn''t look like he belongs to the order, someone like you, to get my helmet back. If you succeed, I''ll give you your piece of paper back.', 'Ich brauche jemanden der nicht aussieht, als gehöre er zum Orden, jemanden wie dich, der mir meinen Helm wiederbesorgt. Wenn du es schaffst, gebe ich dir dein Stück Papier wieder.', 'Ich bruch öpper wo nid usgseht als würer zum Orde ghöre, öpper we dich, womer min Helm wider holt. Wend das anebringsch, chasch din Papierfötzel wider ha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Stephen_GotHelmet', 'dl_npc_stephen', 'Very good! Give it to me!', 'Sehr gut! Gib ihn mir!', 'Sehr guet! Giben mir!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FirstLetter', 'dl_npc_stephen', 'Give me the letter first.', 'Gib mir zuerst den Brief.', 'Gibmer zersch de Brief.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GiveHelmet', 'dl_npc_stephen', 'Okay, here you go.', 'Okay, da hast du ihn.', 'Okay, da heschen.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Stephen_GivesLetter', 'dl_npc_stephen', 'Okay okay, here you go. You can speak with Commander Lloyd. And now give me my helmet!', 'Okay okay, da hast du ihn wieder. Du kannst jetzt von mir aus mit Kommandant Lloyd sprechen. Und jetzt gib mir meinen Helm!', 'Okay okay, da heschen wider. Du chasch ez vo mir us mitem Kommandant Lloyd rede. Und ez gimmer min Helm!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoHelmet', 'dl_npc_stephen', 'You wish.', 'Hättest du wohl gern.', 'Hetsch wohl gern.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Stephen_Angry', 'dl_npc_stephen', 'Grr, you little... You''ll pay for this! If Lloyd hears about this...', 'Grr, du kleiner... Dafür wirst du noch bezahlen! Wenn Lloyd davon erfährt...', 'Grr, du chliine... Dafür wirsch na zahle! Wenn de Lloyd devo ghört...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CommanderLloyd', 'dl_npc_stephen', 'It''s "Commander" Lloyd.', 'Es heisst "Kommandant" Lloyd.', 'Es heisst "Kommandant" Lloyd.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_DontDrinkMuch', 'dl_npc_stephen', 'Well, it''s your fault if you drink too much.', 'Es ist dein Fehler wenn du zu viel trinkst.', 'Es isch dis Problem wenn du zviel trinksch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Stephen_GotoLloyd', 'dl_npc_stephen', 'Perfect. Here is your letter. You can now go and see Commander Lloyd.', 'Perfekt. Hier ist dein Brief. Du kannst jetzt zu Kommandant Lloyd gehen.', 'Perfekt. Da isch din Brief. Du chasch ez zum Kommandant Lloyd.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Stephen_GetOut', 'dl_npc_stephen', 'Just get out of my sight!', 'Geh mir einfach aus den Augen!', 'Ez gang eifach! Ich wett di nümme gse!');