INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_ANewFace', 'dl_npc_steven', 'And again, a new face in the city. As if we hadn''t enough troublemakers here already. ', 'Und wieder ein neues Gesicht in der Stadt. Als hätten wir nicht schon genug Störenfriede hier.', 'U widr äs nöis Gsicht ir Stadt. Aus hättmr nid scho gnue Störäfridä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_YouShouldKnowRules', 'dl_npc_steven', 'You should know the rules of the city, if you don''t want to end up jailed.', 'Du solltest die Regeln dieser Stadt kennen, wenn du nicht im Gefängnis landen willst. ', 'Du sötisch d''Reglä vodr Stadt kennä, wedä nid id Chischte wotsch cho.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ExplainRules', 'dl_npc_steven', 'Explain the rules of the city to me.', 'Erklär mir die Regeln der Stadt.', 'Erklär mer d''Regle vode Stadt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_steven', 'Who are you?', 'Wer bist du eigentlich?', 'Wer bisch du überhaupt?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoTime', 'dl_npc_steven', 'I don''t have time for this.', 'Ich habe keine Zeit für das.', 'Ich han grad ke Ziit für das.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Lloyd', 'dl_npc_steven', 'I need to talk to some "Lloyd" here... (Show the letter)', 'Ich muss hier mit so einem "Lloyd" reden... (Brief zeigen)', 'Ich mu da mit somne "Lloyd" rede... (Brief zeige)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CU', 'dl_npc_steven', 'See you.', 'Bis später.', 'Bis spöter.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Rules1', 'dl_npc_steven', 'Well, first, you should know that the Order of the Eternal Light is in charge here. Therefore; respect us and follow our orders, or you won''t have a very pleasant stay in Gandria.', 'Nun, zuerst solltest du wissen, dass der Orden des Ewigen Lichts hier regiert - also respektiere uns und unsere Gesetze, sonst wird das ein nicht sehr angenehmer Aufenthalt für dich in Gandria.', 'Zersch sötter klar si, ds dr Ordä vom ewigä Liächt hie regiert - auso haut di besser a üsi Gsetz, süsch wird das ä unagnähme Ufenthaut für di z''Gandria. ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Rules2', 'dl_npc_steven', 'Behave well if you go into a stranger''s house - we don''t like thieves here. Most citizens of Gandria use observer spells to avoid being robbed.', 'Benimm dich wenn du ein fremdes Haus betrittst - Wir mögen keine Diebe hier. Die meisten Bürger hier haben Beobachter-Zauber, um Diebstähle zu verhindern.', 'Benimm di wet ines Huus geisch - Mir gseh hie nid gärn Röiber. Die meischtä Bürger hei Beobachtigs-Zouber, wo vrhinderä, ds öpper öppis stibitzt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Rules3', 'dl_npc_steven', 'And, last but not least: We don''t like to see other spells than those which originate from the high divine art.', 'Und, zum Schluss: Wir sehen es nicht sonderlich gerne, wenn jemand Zauber wirkt, die nicht zur hohen Kunst der heiligen Magie gehören.', 'U zum Schluss, Mir heis nid u huerä gärn, wenn öpper mit Zouber umäfiguretlät, wo nid zur höche heilige Schuu ghört.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Rules4', 'dl_npc_steven', 'You will hang for practicing Necromancy.', 'Wenn wir dich bei Nekromantie erwischen, wirst du aufgeknüpft.', 'Wedä bir Nekromantie vrwütscht wirsch, wirsch ghängt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_Okay', 'dl_npc_steven', 'Okay.', 'Okay.', 'Okay.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_WhoAreYou', 'dl_npc_steven', 'I''m Steven, paladin and vigilante of Gandria. I am, so to speak, the right hand of our Commander, Lloyd. Don''t mess with me or my city, understood?', 'Ich bin Steven, Paladin und Ordnungshüter von Gandria, sozusagen die rechte Hand von unserem Kommandanten, Lloyd. Leg dich nicht mit mir oder meiner Stadt an, verstanden?', 'I bi dr Steven, Paladin und dr Tschugger vo Gandria, sozsägä die rächti Hang vom Kommandant Lloyd. Leg di nid mit mir odr mirä Stadt ah, vrstangä? ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Understood', 'dl_npc_steven', 'Understood.', 'Verstanden.', 'Verstande.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_MaybeUnderstood', 'dl_npc_steven', 'I''ll try.', 'Ich werde es versuchen.', 'Ich wirds probiere.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotUnderstood', 'dl_npc_steven', '... (Mischievous grin)', '... (Schelmisch grinsen)', '... (Schelmisch grinse)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_IKeepEyeOnYou', 'dl_npc_steven', 'I''ll keep an eye on you, boy.', 'Ich werde dich im Auge behalten, Junge.', 'Giu, mir heidi im Oug, Bürschtli.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_DontTrouble', 'dl_npc_steven', 'Well then... Don''t make trouble!', 'Na dann... Mach bloss keinen Ärger!', 'Ja dämfau.. Mach eifach ke Seich!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Laughter', 'dl_npc_steven', '(Steven takes the letter and then starts laughing uncontrollably)', '(Steven nimmt den Brief und beginnt, unbeherrscht zu lachen)', '(Dr Steven nimmt dr Brief und faht vöuig unkontrolliert a lachä)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_PieceOfPaper', 'dl_npc_steven', 'And you thought this piece of paper will bring you to him? That''s truly ridiculous.', 'Und du hast gedacht, dieser Papierfetzen wird dich zu ihm bringen? Das ist lächerlich.', 'U du hesch würklech dänkt, ds dr Papierfötzu di zu ihm füehrt? Ds isch ja lächerlech.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Urgent', 'dl_npc_steven', 'But it''s urgent!', 'Aber es ist dringend!', 'Aber es isch dringend!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Inina', 'dl_npc_steven', 'But this letter is from the High Priestess Inina!', 'Aber dieser Brief ist von der Hohepriesterin Inina!', 'Aber de Brief isch vode Hohepriesterin Inina!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Intimidate', 'dl_npc_steven', 'Just let me to him and no one gets hurt.', 'Lass mich einfach zu ihm und niemand wird verletzt.', 'Lah mich eifach zu ihm und niemert wird verletzt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Urgent', 'dl_npc_steven', 'Sure it is. But there are other urgent things too. Like what you''ll have to do to get your letter back.', 'Natürlich. Aber es gibt auch andere dringende Sachen. So wie das, was du für mich zu erledigen hast, wenn du deinen Brief wieder willst.', 'Abr äs git oh angeri pressanti Sachä. So wiä ds, wo du für mi muesch machä, wedä dr Brief widr zrügg wotsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Inina', 'dl_npc_steven', 'Good for you. But you can''t talk to her either until you''ve taken care of my little problem. Then you''ll get your letter back.', 'Schön für dich. Aber du kannst auch nicht mit ihr sprechen, bis du eine kleine Sache für mich erledigt hast. Dann wirst du deinen Brief zurückkriegen.', 'Schön für di. Abr du chasch oh nid mit ihrä plöiderlä bis du ä chlini Sach für mi erledigt hesch. De brchunsch di Brief oh widr.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Intimidate', 'dl_npc_steven', 'Ah, you''re a tough boy aren''t you. But I think I''ll have to keep your letter for a while - until you''ve taken care of some problem of mine...', 'Ah, du bist ein harter Junge, nicht wahr. Aber ich glaube, ich muss deinen Brief noch ein bisschen behalten - bis du eine Sache für mich erledigt hast.', 'Ah, du bisch ä hertä Chrieger, gäu. Aber i muess di Brief gloub no äs müü phautä - bis du so ä Sach für mi erledigt hesch... ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TellmeQuest', 'dl_npc_steven', '(Sigh) Okay, what should I do for you?', '(Seufz) Okay, was soll ich für dich tun?', '(Süüfz) Okay, wa söll ich für dich mache?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotHelmet', 'dl_npc_steven', 'I got your helmet, now give me that letter back.', 'Ich habe deinen Helm, gib mir jetzt meinen Brief wieder.', 'Ich ha din Helm, gibmer ez min Brief wider.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_UnlockKeyBuy', 'dl_npc_steven', 'I''m looking for an anti-magic crystal...', 'Ich suche einen antimagischen Kristall...', 'Ich suech en antimagische Kristall...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_BuyCrystal', 'dl_npc_steven', 'Sell me that crystal.', 'Verkauf mir diesen Kristall.', 'Verchauf mer de Kristall.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_StartKey', 'dl_npc_steven', '(Start an attempt to steal Steven''s tower key)', '(Starte einen Versuch, Steven''s Turmschlüssel zu stehlen)', '(En Versuech starte, em Steven sin Turmschlüssel z''chlaue)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_HelmetQuest', 'dl_npc_steven', 'I got a little bit drunk last night and lost my helmet to some shady guys in the tavern.', 'Ich habe letzte Nacht ein bisschen über den Durst getrunken und meinen Helm an ein paar zwielichtige Gestalten in der Taverne verloren.', 'I ha letscht Nacht chly z''viu gsoffä u ha mi Heum a äs paar quirligi Sieche idr Tavernä vrlore.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_HelmetQuest2', 'dl_npc_steven', 'I''m pretty sure that they belong to the dirty thief guild.', 'Ich bin mir ziemlich sicher, dass sie zur dreckigen Diebesgilde gehören.', 'Bimr fei echly ds die zu dä dräckige Röibergiudä ghörä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_HelmetQuest3', 'dl_npc_steven', 'I need someone that doesn''t look like they belongs to the order, someone like you, to get my helmet back.', 'Ich brauche jemanden der nicht aussieht, als gehöre er zum Orden, jemanden wie dich, der mir meinen Helm wiederbesorgt.', 'I bruchä öpper vo wnid usgseht aus würder zudä Ordä ghörä, öpper wie di, womr mi Heum widr hout.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_HelmetQuest4', 'dl_npc_steven', 'If you succeed, I''ll give you your piece of paper back.', 'Wenn du es schaffst, gebe ich dir dein Stück Papier wieder.', 'We das härä bringsch, chasch di Papierfötzu widr ha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_GotHelmet', 'dl_npc_steven', 'Very good! Give it to me!', 'Sehr gut! Gib ihn mir!', 'Sehr guet! Gibnä mir!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FirstLetter', 'dl_npc_steven', 'Give me the letter first.', 'Gib mir zuerst den Brief.', 'Gibmer zersch de Brief.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GiveHelmet', 'dl_npc_steven', 'Okay, here you go.', 'Okay, da hast du ihn.', 'Okay, da heschen.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_GivesLetter', 'dl_npc_steven', 'Okay okay, here you go. You can speak with Commander Lloyd. And now give me my helmet!', 'Okay okay, da hast du ihn wieder. Du kannst jetzt von mir aus mit Kommandant Lloyd sprechen. Und jetzt gib mir meinen Helm!', 'Okee Oke, da heschnä widr. Du chasch ize vo mir us mitm Kommandant Lloyd redä. U iz gimer mi Heum!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoHelmet', 'dl_npc_steven', 'You wish.', 'Hättest du wohl gern.', 'Hetsch wohl gern.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_ThanksHelmet', 'dl_npc_steven', 'Hehe. Thank you.', 'Hehe. Danke.', 'Hehe. Merci.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Angry', 'dl_npc_steven', 'Grr, you little... You''ll pay for this! If Lloyd hears about this...', 'Grr, du kleiner... Dafür wirst du noch bezahlen! Wenn Lloyd davon erfährt...', 'Grr, du chliinä... Drfür wirsch du büässä! Wedr Lloyd drvo ghört...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CommanderLloyd', 'dl_npc_steven', 'It''s "Commander" Lloyd.', 'Es heisst "Kommandant" Lloyd.', 'Es heisst "Kommandant" Lloyd.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_DontDrinkMuch', 'dl_npc_steven', 'Well, it''s your fault if you drink too much.', 'Es ist dein Fehler, wenn du zu viel trinkst.', 'Es isch dis Problem, wenn du zviel suffsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_GotoLloyd', 'dl_npc_steven', 'Perfect. Here is your letter. You can now go and see Commander Lloyd.', 'Perfekt. Hier ist dein Brief. Du kannst jetzt zu Kommandant Lloyd gehen.', 'Perfekt. Da isch di Brief. Du chasch iz zum Kommandant Lloyd.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_UnlockKeyBuy', 'dl_npc_steven', 'You''re lucky. Actually, I do have one here... but it''s not cheap. Why do you need one anyway?', 'Ja, wie es der Zufall will, hab ich einen hier... er ist aber nicht billig. Für was brauchst du ihn denn?', 'Ja, per Zuefau hani eine do... er isch aber nid biuig. U für was wetschne denn?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CrystalForCurse', 'dl_npc_steven', 'I want to break a curse with it.', 'Ich will damit einen Fluch brechen.', 'Ich wett demit en Fluech breche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CrystalProtection', 'dl_npc_steven', 'I need protection against necromancy.', 'Ich muss mich gegen Nekromantie schützen.', 'Ich mu mi gege Nekromantie schütze.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CrystalNoneOf', 'dl_npc_steven', 'That''s none of your business.', 'Das geht dich nichts an.', 'Das gaht di nüt ah.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_CrystalBuyOk', 'dl_npc_steven', 'That''s a good reason, indeed. If you want, I can sell one to you for 100 gold.', 'Das ist wirklich ein guter Grund. Wenn du willst, kann ich dir einen für 100 Gold verkaufen.', 'Das isch würkli en guete Grund. Wennd wetsch, chani dir eine für 100 Gold verchaufe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_CrystalBuyNOk', 'dl_npc_steven', 'Hmpf! I don''t think I can sell you one then. Go ask someone else.', 'Hmpf! Ich glaube nicht, dass ich dir einen verkaufen kann. Geh, und frag jemand anderen.', 'Hmpf! Ich glaub nid, dass ich dir eine verchaufe cha. Gang, und frög öpper andersch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_BuyCrystal', 'dl_npc_steven', 'Hehehe. You made a really good deal today!', 'Hehehe. Da hast du wirklich ein Schnäppchen gemacht!', 'Hehehe. Das isch ja würkli es Schnäppli gsi, gäu!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_StealBird', 'dl_npc_steven', 'Hey, look! A bird!', 'Schau mal! Ein Vogel!', 'Lueg mal! En Vogel!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_StealTellMe', 'dl_npc_steven', 'Tell me something about yourself.', 'Erzähl mal was von dir.', 'Verzell mol chli öppis vo dir.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_StealEternal', 'dl_npc_steven', 'So, what exactly is that "Eternal Light"?', 'Was ist jetzt dieses "Ewige Licht" genau?', 'Was isch ez das "Ewig Liecht" genau?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_StealBird', 'dl_npc_steven', 'Ehh... What? We''re in the barracks here!', 'Ehh... Was? Wir sind in der Kaserne hier!', 'Ehh... Was? Mer sind ide Kaserne do!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TryStealBird2', 'dl_npc_steven', 'But it just flew through that door!', 'Aber er flog gerade durch diese Tür!', 'Aber er isch grad dur die Türe gfloge!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TrySteal', 'dl_npc_steven', '(Try to steal Steven''s tower key)', '(Versuch, Steven''s Turmschlüssel zu stehlen)', '(Probier, em Steven sin Schlüssel z''chlaue)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_TryStealBird2', 'dl_npc_steven', 'Dude... are you kidding me?', 'Alter... verarschst du mich etwa?', 'Alte... verarschisch du mich grad?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_FailSteal', 'dl_npc_steven', '(Your attempt to steal the tower key failed)', '(Dein Versuch, den Turmschlüssel zu stehlen, schlug fehl)', '(Din Versuech, de Turmschlüssel z''chlaue, het gfailt)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_StealTellMe', 'dl_npc_steven', 'Why would I... ah, never mind. You must be an admirer of mine.', 'Wieso sollte ich... ah, ja klar. Du musst wohl ein Bewunderer von mir sein.', 'Weso sötti... ah, isch klar. Du bisch dämfou e Fan vu mer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_OfCourseYouAre', 'dl_npc_steven', 'Yes, of course!', 'Ja, natürlich!', 'Jo, natürli!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_StealTellMe2', 'dl_npc_steven', 'Hehe. Now, listen. I''m currently one of the mightiest paladins in Gandria. I''m sure, Lloyd will chose me as his successor!', 'Hehe. Na, dann hör mal zu. Ich bin momentan einer der mächtigsten Paladine hier in Gandria. Ich bin mir sicher, dass Lloyd mich zu seinem Nachfolger bestimmen wird.', 'Hehe. Jo, denn los mau zue. I bi momentan eine vudr mächtigste Paladin i ganz Gandria. I gloube sogar, dass de Lloyd mi aus Nochfolger vonem bestimme wird.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IThinkYouAre', 'dl_npc_steven', 'Yes, I fully understand.', 'Ja, das glaub ich dir sofort.', 'Jo, das glaubi sofort.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IThinkYouNot', 'dl_npc_steven', 'You wish.', 'Das glaubst du ja selbst nicht.', 'Das glaubsch jo selber nid.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_IThinkYouAre', 'dl_npc_steven', 'Yes... The whole city will be under my command! (Laughs)', 'Ja... Die ganze Stadt wird unter meinem Befehl stehen! (Lacht)', 'Jo... Di ganz Stadt wird unger mim Befeu sto! (Lacht)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WholeCityWill', 'dl_npc_steven', 'Oh yes, it will!', 'Oh ja, das wird sie!', 'Oh jo, das wird si!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_WholeCityWill', 'dl_npc_steven', 'Wait... you''ve never been that nice before...', 'Warte... so nett warst du bisher nicht...', 'Wart... so nett bisch ez ämu nid gsi...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_SucceedSteal', 'dl_npc_steven', '(Your attempt to steal the tower key succeeded!)', '(Dein Versuch, den Turmschlüssel zu stehlen, hat funktioniert!)', '(Din Versuech, de Turmschlüssel z''chlaue, het klappt!)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_IThinkYouNot', 'dl_npc_steven', 'Hah! You''ll see.', 'Hah! Du wirst schon sehen.', 'Hah! Du wirsch scho gse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_StealEternal', 'dl_npc_steven', 'It''s the purest form of energy - and our source of magic.', 'Es ist die purste Form der Energie - und unsere Quelle der Magie.', 'Es isch di purschti Form vor Energie - u üseri Queue vor Magie.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GoOn', 'dl_npc_steven', '(Go on...)', '(Weiter...)', '(Wiiter...)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_StealEternal2', 'dl_npc_steven', 'It''s the only magic that has it''s own will. It chooses its mages.', 'Es ist die einzige Magie, die ihren eigenen Willen hat. Sie erwählt ihre Magier.', 'Es isch di einzig Magie, wo ihre eiget Wille het. Si erwählt ihri Magier.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Unlucky', 'dl_npc_steven', 'And the ones who are not chosen, are just unlucky?', 'Und diejenigen, die nicht erwählt wurden, hatten einfach Pech?', 'Und die, wo nid erwählt worde sind, hend eifach Pech?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Unlucky', 'dl_npc_steven', 'They''re unworthy, yes. But as long as you''re a mage, you can still come back to the pure divine magic.', 'Die sind unwürdig, ja. Aber solange man ein Magier ist, kann man trotzdem noch zur puren heiligen Magie zurückkehren.', 'Die si unwürig, jo. Aber solang me e Magier isch, chamä trotzdem no zudr purä heiligä Magie zruggcho.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HowDivine', 'dl_npc_steven', 'And how does that work?', 'Und wie funktioniert das?', 'Und we funktioniert das?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_HowDivine', 'dl_npc_steven', 'Hm... so many questions. Are you trying to distract me? ', 'Hm... so viele Fragen. Versuchst du etwa, mich abzulenken?', 'Hm... so viu Frogä. Versuechsch öppä, mi abzlänkä?');
