INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_ANewFace', 'dl_npc_steven', 'And again, a new face in the city. As if we hadn''t already enough troublemakers here. ', 'Und wieder ein neues Gesicht in der Stadt. Als hätten wir nicht schon genug Störenfriede hier.', 'U widr äs nöis Gsicht ir Stadt. Aus hättmr nid scho gnue Störäfridä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_YouShouldKnowRules', 'dl_npc_steven', 'You should know the rules of the city, if you don''t want to end up jailed.', 'Du solltest die Regeln dieser Stadt kennen, wenn du nicht im Gefängnis landen willst. ', 'Du sötisch d''Reglä vodr Stadt kennä, wedä nid id Chischte wotsch cho.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ExplainRules', 'dl_npc_steven', 'Explain the rules of the city to me.', 'Erklär mir die Regeln der Stadt.', 'Erklär mer d''Regle vode Stadt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_steven', 'Who are you?', 'Wer bist du eigentlich?', 'Wer bisch du überhaupt?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoTime', 'dl_npc_steven', 'I don''t have time for this.', 'Ich habe keine Zeit für das.', 'Ich han grad ke Ziit für das.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Lloyd', 'dl_npc_steven', 'I need to talk to some "Lloyd" here... (Show the letter)', 'Ich muss hier mit so einem "Lloyd" reden... (Brief zeigen)', 'Ich mu da mit somne "Lloyd" rede... (Brief zeige)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CU', 'dl_npc_steven', 'See you.', 'Bis später.', 'Bis spöter.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_Rules1', 'dl_npc_steven', 'Well, first, you should know that the Order of the Eternal Light is in charge here. Therefore, respect us and follow our orders as long as you''re here, or it won''t be a very pleasant stay in Gandria.', 'Nun, zuerst solltest du wissen, dass der Orden des Ewigen Lichts hier regiert - also respektiere uns und unsere Gesetze, sonst wird das ein nicht sehr angenehmer Aufenthalt für dich in Gandria.', 'Zersch sötter klar si, ds dr Ordä vom ewigä Liächt hie regiert - auso haut di besser a üsi Gsetz, süsch wird das ä unagnähme Ufenthaut für di z''Gandria. ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_Rules2', 'dl_npc_steven', 'Behave well if you go into a stranger''s house - we don''t like thieves here. Most citizens of Gandria use observer spells to avoid being robbed.', 'Benimm dich wenn du ein fremdes Haus betrittst - Wir mögen keine Diebe hier. Die meisten Bürger hier haben Beobachter-Zauber, um Diebstähle zu verhindern.', 'Benimm di wet ines Huus geisch - Mir gseh hie nid gärn Röiber. Die meischtä Bürger hei Beobachtigs-Zouber, wo vrhinderä, ds öpper öppis stibitzt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_Rules3', 'dl_npc_steven', 'And, last but not least, we don''t really like seeing other spells than those which originate from the high divine art. You can hang for practicing Necromancy.', 'Und, zum Schluss: Wir sehen es nicht sonderlich gerne, wenn jemand Zauber wirkt, die nicht zur hohen Kunst der heiligen Magie gehören. Wenn wir dich bei Nekromantie erwischen, kannst du aufgeknöpft werden.', 'U zum Schluss, Mir heis nid u huerä gärn, wenn öpper mit Zouber umäfiguretlät, wo nid zur höche heilige Schuu ghört. Wedä bir Nekromantie vrwütscht wirsch, chasch ghängt wärdä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_Okay', 'dl_npc_steven', 'Okay.', 'Okay.', 'Okay.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_WhoAreYou', 'dl_npc_steven', 'I''m Steven, paladin and vigilante of Gandria and so to say directly the right hand of our Commander, Lloyd. Don''t mess with me or my city, understood?', 'Ich bin Steven, Paladin und Ordnungshüter von Gandria, sozusagen die rechte Hand von unserem Kommandanten, Lloyd. Leg dich nicht mit mir oder meiner Stadt an, verstanden?', 'I bi dr Steven, Paladin und dr Tschugger vo Gandria, sozsägä die rächti Hang vom Kommandant Lloyd. Leg di nid mit mir odr mirä Stadt ah, vrstangä? ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Understood', 'dl_npc_steven', 'Understood.', 'Verstanden.', 'Verstande.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_MaybeUnderstood', 'dl_npc_steven', 'I''ll try.', 'Ich werde es versuchen.', 'Ich wirds probiere.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotUnderstood', 'dl_npc_steven', '... (Mischievous grin)', '... (Schelmisch grinsen)', '... (Schelmisch grinse)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_IKeepEyeOnYou', 'dl_npc_steven', 'I''ll keep an eye on you, boy.', 'Ich werde dich im Auge behalten, Junge.', 'Giu, mir heidi im Oug, Bürschtli.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_DontTrouble', 'dl_npc_steven', 'Well then... Don''t make trouble!', 'Na dann... Mach bloss keinen Ärger!', 'Ja dämfau.. Mach eifach ke Seich!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Laughter', 'dl_npc_steven', '(Steven takes the letter and then starts laughing uncontrollably)', '(Steven nimmt den Brief und beginnt, unbeherrscht zu lachen)', '(Dr Steven nimmt dr Brief und faht vöuig unkontrolliert a lachä)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_PieceOfPaper', 'dl_npc_steven', 'And you thought this piece of paper will bring you to him? That''s truly ridiculous.', 'Und du hast gedacht, dieser Papierfetzen wird dich zu ihm bringen? Das ist lächerlich.', 'U du hesch würklech dänkt, ds dr Papierfötzu di zu ihm füehrt? Ds isch ja lächerlech.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Urgent', 'dl_npc_steven', 'But it''s urgent!', 'Aber es ist dringend!', 'Aber es isch dringend!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Inina', 'dl_npc_steven', 'But this letter is from the High Priestess Inina!', 'Aber dieser Brief ist von der Hohepriesterin Inina!', 'Aber de Brief isch vode Hohepriesterin Inina!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Intimidate', 'dl_npc_steven', 'Just let me to him and no one gets hurt.', 'Lass mich einfach zu ihm und niemand wird verletzt.', 'Lah mich eifach zu ihm und niemert wird verletzt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Urgent', 'dl_npc_steven', 'Sure it is. But there are other urgent things too. Like what you''ll have to do to get your letter back.', 'Natürlich. Aber es gibt auch andere dringende Sachen. So wie das, was du für mich zu erledigen hast, wenn du deinen Brief wieder willst.', 'Abr äs git oh angeri pressanti Sachä. So wiä ds, wo du für mi muesch machä, wedä dr Brief widr zrügg wotsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Inina', 'dl_npc_steven', 'Good for you. But you can''t talk to her either until you''ve taken care of my tiny problem. Then you''ll get your letter back.', 'Schön für dich. Aber du kannst auch nicht mit ihr sprechen bis du eine kleine Sache für mich erledigt hast. Dann wirst du deinen Brief zurück kriegen.', 'Schön für di. Abr du chasch oh nid mit ihrä plöiderlä bis du ä chlini Sach für mi erledigt hesch. De brchunsch di Brief oh widr.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Intimidate', 'dl_npc_steven', 'Ah, you''re a tough boy aren''t you. But I think I''ll have to keep your letter for a while - until you''ve taken care of some problem of mine...', 'Ah, du bist ein harter Junge, nicht wahr. Aber ich glaube, ich muss deinen Brief noch ein bisschen behalten - bis du eine Sache für mich erledigt hast.', 'Ah, du bisch ä hertä Chrieger, gäu. Aber i muess di Brief gloub no äs müü phautä - bis du so ä Sach für mi erledigt hesch... ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TellmeQuest', 'dl_npc_steven', '(Sigh) Okay, what should I do for you?', '(Seufz) Okay, was soll ich für dich tun?', '(Süüfz) Okay, wa söll ich für dich mache?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotHelmet', 'dl_npc_steven', 'I got your helmet, now give me that letter back.', 'Ich habe deinen Helm, gib mir jetzt meinen Brief wieder.', 'Ich ha din Helm, gibmer ez min Brief wider.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_HelmetQuest', 'dl_npc_steven', 'I got a little bit drunk last night and lost my helmet to some shady guys in the tavern. I''m pretty sure that they belong to the dirty thief guild.', 'Ich habe letzte Nacht ein bisschen über den Durst getrunken und meinen Helm an ein paar zwielichtige Gestalten in der Taverne verloren. Ich bin mir ziemlich sicher, dass sie zur dreckigen Diebesgilde gehören.', 'I ha letscht Nacht chly z''viu gsoffä u ha mi Heum a äs paar quirligi Sieche idr Tavernä vrlore. Bimr fei echly ds die zu dä dräckige Röibergiudä ghörä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_HelmetQuest2', 'dl_npc_steven', 'I need someone that doesn''t look like he belongs to the order, someone like you, to get my helmet back. If you succeed, I''ll give you your piece of paper back.', 'Ich brauche jemanden der nicht aussieht, als gehöre er zum Orden, jemanden wie dich, der mir meinen Helm wiederbesorgt. Wenn du es schaffst, gebe ich dir dein Stück Papier wieder.', 'I bruchä öpper vo wnid usgseht aus würder zudä Ordä ghörä, öpper wie di, womr mi Heum widr hout. We das härä bringsch, chasch di Papierfötzu widr ha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_GotHelmet', 'dl_npc_steven', 'Very good! Give it to me!', 'Sehr gut! Gib ihn mir!', 'Sehr guet! Gibnä mir!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FirstLetter', 'dl_npc_steven', 'Give me the letter first.', 'Gib mir zuerst den Brief.', 'Gibmer zersch de Brief.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GiveHelmet', 'dl_npc_steven', 'Okay, here you go.', 'Okay, da hast du ihn.', 'Okay, da heschen.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_GivesLetter', 'dl_npc_steven', 'Okay okay, here you go. You can speak with Commander Lloyd. And now give me my helmet!', 'Okay okay, da hast du ihn wieder. Du kannst jetzt von mir aus mit Kommandant Lloyd sprechen. Und jetzt gib mir meinen Helm!', 'Okee Oke, da heschnä widr. Du chasch ize vo mir us mitm Kommandant Lloyd redä. U iz gimer mi Heum!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoHelmet', 'dl_npc_steven', 'You wish.', 'Hättest du wohl gern.', 'Hetsch wohl gern.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_ThanksHelmet', 'dl_npc_steven', 'Hehe. Thank you.', 'Hehe. Danke.', 'Hehe. Merci.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_Angry', 'dl_npc_steven', 'Grr, you little... You''ll pay for this! If Lloyd hears about this...', 'Grr, du kleiner... Dafür wirst du noch bezahlen! Wenn Lloyd davon erfährt...', 'Grr, du chliinä... Drfür wirsch du büässä! Wedr Lloyd drvo ghört...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CommanderLloyd', 'dl_npc_steven', 'It''s "Commander" Lloyd.', 'Es heisst "Kommandant" Lloyd.', 'Es heisst "Kommandant" Lloyd.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_DontDrinkMuch', 'dl_npc_steven', 'Well, it''s your fault if you drink too much.', 'Es ist dein Fehler wenn du zu viel trinkst.', 'Es isch dis Problem wenn du zviel trinksch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Steven_GotoLloyd', 'dl_npc_steven', 'Perfect. Here is your letter. You can now go and see Commander Lloyd.', 'Perfekt. Hier ist dein Brief. Du kannst jetzt zu Kommandant Lloyd gehen.', 'Perfekt. Da isch di Brief. Du chasch iz zum Kommandant Lloyd.');
