INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Greeting', 'dl_npc_syrah', 'Well hello there! You look like you could use some potions made by the infamous alchemist Syrah. A love potion or a hair dye - there''s nearly nothing I''m not capable of brewing.', 'Na hallo! Du siehst aus als könntest du ein paar Tränke von der berühmt-berüchtigten Alchemistin Syrah gebrauchen. Ob Liebestrank oder Haarfarbe - es gibt fast nichts was ich nicht zusammenbrauen könnte.', 'Ja hallo! Du gsesch us als chöntisch du paar Tränk vode berüemt-berüchtigte Alchemistin Syrah bruche. Ob Liebestrank oder Haarfarb - es git fast nüt woni nid chönt zememischle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_InvisRecipe', 'dl_npc_syrah', 'Oh, hello. There''s a certain recipe missing from my basement. Do you know something about it?', 'Oh, hallo. Sag mal... Es fehlt ein bestimmtes Rezept aus meinem Keller. Weisst du etwas über diese Sache?', 'Oh hallo. Du, seg emal... Es fehlt es gwüsses Rezept us mim Chäller. Weisch du öppis über die Sach?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IBroughtItBack', 'dl_npc_syrah', 'Yes. I got it back for you. (Give the recipe)', 'Ja. Ich hab es dir zurück gebracht. (Rezept geben)', 'Ja. Ich has der zrugg bracht. (Rezept gä)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IKnowNothing', 'dl_npc_syrah', 'No, sorry.', 'Nein, sorry.', 'Nei, sorry.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_IBroughtItBack', 'dl_npc_syrah', 'Wow, thank you. You know, that really means a lot to me. I won''t ask where you got that though. (Winks)', 'Wow, danke. Weisst du, das bedeutet mir wirklich ne Menge. Ich frag gar nicht erst, wie du das geschafft hast. (Zwinkert)', 'Wow, merci. Weisch, da bedüütet mer würkli viel. Ich frög gar nid erst, wed da becho hesch. (Zwinkeret)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_IKnowNothing', 'dl_npc_syrah', 'What a pity.', 'Schade.', 'Schad.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mark', 'dl_npc_syrah', 'Can you remove this curse? (Show the mark)', 'Kannst du diesen Fluch aufheben? (Mal zeigen)', 'Chasch du de Fluech wegmache? (Zeiche zeige)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Sewers', 'dl_npc_syrah', 'How can I get to the sewers?', 'Wie komme ich in die Kanalisation?', 'We chumi id Kanalisation?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_MarkSeen', 'dl_npc_syrah', 'Where have you already seen this mark?', 'Wo hast du dieses Mal schon mal gesehen? ', 'Wo hesch das Zeiche schomal gse?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_MarkQuestDone', 'dl_npc_syrah', 'I got what you wanted. (Give ingredients)', 'Ich habe was du brauchst. (Zutaten geben)', 'Ich han was du bruchsch. (Zuetate gä)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FavourQuestStart', 'dl_npc_syrah', 'What kind of favour should that be?', 'Was für ein Gefallen soll das sein?', 'Was füren Gfalle söl das si?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CanYouBrew', 'dl_npc_syrah', 'Can you brew something for me?', 'Kannst du mir etwas brauen?', 'Chaschmer öppis zemebraue?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Trade', 'dl_npc_syrah', 'Show me your potions.', 'Zeig mir deine Tränke.', 'Zeig mer dini Tränk.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Mark', 'dl_npc_syrah', '(Syrah eyes the mark carefully) Hm. I''ve seen this mark before. Definitely a tracking curse. But it''s one of the strongest I''ve ever seen.', '(Syrah betrachtet das Mal sorgfältig) Hm. Ich habe dieses Mal schon mal gesehen. Ganz klar ein Aufspür-Zauber. Aber einer der stärksten, die ich je gesehen habe.', '(Syrah luegt s''Zeiche vorsichtig ah) Hm. Ich ha da Zeiche schomal gse. Ganz klar en Ufspür-Zauber. Aber eine vode stärchste woni je gseh han.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Mark2', 'dl_npc_syrah', 'That''s not going to be easy. But if you help me to gather some ingredients, I think I can try to remove or at least weaken the curse.', 'Das wird nicht einfach. Aber wenn du mir hilfst, die Zutaten zu besorgen, kann ich versuchen, den Fluch zu entfernen oder zumindest zu schwächen.', 'Da wird nid eifach. Aber wennd mer hilfsch zum d''Zuetate z''bsorge chani versueche, de Fluech z''enferne oder mindestens z''schwäche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Mark3', 'dl_npc_syrah', '(Syrah pulls a green book out of her shelf and browses in it) I only need some bones, some blood of a bat and the heart of a fire rat. I think you can find all of this in the sewers.', '(Syrah zieht ein grünes Buch aus ihrem Regal und blättert darin) Ich brauche nur ein paar Knochen, ein wenig Blut einer Fledermaus und das Herz einer Feuerratte. Ich denke, das kannst du alles in der Kanalisation von Gandria bekommen.', '(D''Syrah zieht es grüens Buech us irem Regal und blätteret drin) Ich bruch nur es paar Chnoche, e chli Bluet vonere Fledermuus und es Herz vonere Füürratte. Ich denk da sötsch alles ide Kanalisation vo Gandria becho.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Sewers', 'dl_npc_syrah', 'Some houses have a basement with a connection to the sewers. I think there''s also a door near the harbour that leads down.', 'Ein paar Häuser haben in ihrem Keller eine Verbindung zur Kanalisation. Ich glaube, es gibt auch eine Türe in der Nähe des Hafens die dort runter führt.', 'Es paar Hüser hend i ihrem Chäller e Verbindig zude Kanalisation. Ich glaub es git au e Türe ide Nöchi vom Hafe wo det abe führt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_MarkSeen', 'dl_npc_syrah', 'Hm, let me think. There was this strange guy in the Basilisk Inn, he had the same or a similar mark. But no one could understand what he wanted to say, he only talked rubbish.', 'Hm, lass mich nachdenken. Da war dieser komische Typ im Gasthaus zum Basilisken, der hatte ein ähnliches oder das gleiche Zeichen. Es hat ihn aber niemand wirklich verstanden, er hat nur sinnloses Geschwatz von sich gegeben.', 'Hm, lah mich überlege. Da isch de komisch Typ im Gasthuus zum Basilisk gsi, de het es ähnlichs oder es gliche Zeiche gha. Es het aber niemert würkli verstande waner wet sege, er het nur komisches Zügs babbelet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_MarkQuestDone', 'dl_npc_syrah', 'Ah, perfect. Now let''s get started.', 'Ah, perfekt. Dann lass uns anfangen.', 'Ah, perfekt. Denn fömmer mal ah.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_MarkQuestDone2', 'dl_npc_syrah', '(Syrah places the bones in a circle around the fire rat''s heart.) Come.', '(Syrah ordnet die Knochen kreisförmig um das Feuerrattenherz an.) Komm.', '(D''Syrah ordnet d''Chnoche kreisförmig ums Füürratteherz ah.) Chum.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_OkayMark', 'dl_npc_syrah', 'Let''s get this over with.', 'Bringen wir''s hinter uns.', 'Bringemers hinter üs.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotOkayMark', 'dl_npc_syrah', 'I''m not sure if this is a good idea.', 'Ich weiss nicht ob das eine gute Idee ist.', 'Ich weiss nid ob da so e gueti Idee isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_RelieveCurse', 'dl_npc_syrah', '(Syrah grabs your arm and drags you into the circle.)', '(Syrah packt dich am Arm und zieht dich in den Kreis.)', '(D''Syrah packt dich am Arm und zieht dich in Kreis.)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_RelieveCurse2', 'dl_npc_syrah', '(She takes the blood and pours it over the mark, while murmuring some words.)', '(Sie nimmt das Blut und lässt es über das Mal rinnen, während sie ein paar Worte murmelt.)', '(Si nimmts Bluet und lahts über s''Zeiche rinne während si öppis murmlet.)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_RelieveCurse3', 'dl_npc_syrah', 'See? I think it worked. I don''t feel much of this strange magic anymore. ', 'Siehst du? Ich denke, es hat funktioniert. Ich spüre nicht mehr viel von dieser seltsamen Magie.', 'Gsesch. Ich glaub es het funktioniert. Ich gspür nüm viel vo dere komische Magie.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_RelieveCurse4', 'dl_npc_syrah', 'Hm. It''s still visible. You''ll have to seek out who did this to you. But now you can find him before he finds you. (Giggles)', 'Hm. Es ist immer noch sichtbar. Du solltest denjenigen ausfindig machen, der dir das angetan hat. Doch jetzt kannst du ihn finden, bevor er es tut. (Kichert)', 'Hm. Me gsehts immerna. Du sötsch de sueche, wo dir das ahto het. Und ez chasch ihn finde, bevor er s''tuet. (Gigglet)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_DoMeAFavour', 'dl_npc_syrah', 'But now, as I''ve done you a favour, you should do me one too, I think.', 'Aber jetzt wo ich dir einen Gefallen getan habe, solltest du mir auch einen machen.', 'Aber jetz, woni dir en Gfalle gmacht ha, sötsch mer au eine mache.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotOkayMark2', 'dl_npc_syrah', 'I''m only trying to help you! You want to be relieved from your curse, right? Now come here.', 'Ich versuch doch nur, dir zu helfen! Du willst von diesem Fluch befreit werden, oder? Jetzt komm her.', 'Ich probier doch nur, dir z''helfe! Du wetsch doch vo dem Fluech befreit si, oder? Ez chum.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_FavourQuestStart', 'dl_npc_syrah', 'There''s a graveyard just outside the city, in the marshlands, where people are buried that were not welcome in the city - mostly criminals.', 'Es gibt einen Friedhof im Sumpfland direkt vor der Stadt. Dort liegen die Leute begraben, die in der Stadt nicht erwünscht waren - vor allem Verbrecher.', 'Es git en Fridhof im Sumpfland direkt vor de Stadt, wo d''Lüt begrabe sind wo ide Stadt nid willkomme gsi sind - vor allem Verbrecher.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_FavourQuestStart2', 'dl_npc_syrah', 'I need a special flower, the cinderbloom, for one of my potions. It only grows on graves of people who have died in a fire. ', 'Ich brauche eine spezielle Blume, die Aschenblüte, aus der Krypta dort. Sie wächst nur auf dem Grab von Leuten, die in einem Feuer umgekommen sind.', 'Ich bruch e spezielli Blueme, d''Zündelblueme. Si wachst nur uf de Gräber vo Lüt, wo im Füür gstorbe sind.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_FavourQuestStart3', 'dl_npc_syrah', 'I''m sure that you can find that flower there - mages who "misuse" magic are often burnt at the stake and whatever remains will be taken to this crypt.', 'Ich bin mir sicher dass du diese Blume dort finden kannst - Magier, die Magie "missbrauchen" werden meistens auf dem Scheiterhaufen verbrennt und was auch immer übrig bleibt, wird in diese Krypta geschafft.', 'Ich bimer sicher, dass du die Blueme det finde chasch - Magier wo Magie "missbruched" werded meistens ufem Schiiterhufe verbrennt und wa au immer übrig bliibt wird i die Krypa verfrachtet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_FavourQuestStart4', 'dl_npc_syrah', 'Oh, and if you happen to meet the grave digger Morton, he''s a good friend of mine. Just tell him I sent you, and he''ll let you in.', 'Oh, und wenn du dem Totengräber Morton begegnen solltest, sag ihm einfach, dass ich dich geschickt habe, und er wird dich hinein lassen.', 'Oh, und wennd em Totegräber Morton begegne sötsch, segem eifach dass ich dich gschickt han, denn sötter dich ieloh.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_BrewSomething', 'dl_npc_syrah', 'Sure, if you got the ingredients?', 'Sicher, wenn du die Zutaten hast?', 'Sicher, wennd d''Zuetate hesch?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LesserHealingPotion', 'dl_npc_syrah', '$fo_lesserhealingpotion$ (1 $fo_healingherb$, 1 $fo_water$)', '$fo_lesserhealingpotion$ (1 $fo_healingherb$, 1 $fo_water$)', '$fo_lesserhealingpotion$ (1 $fo_healingherb$, 1 $fo_water$)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_MediumHealingPotion', 'dl_npc_syrah', '$fo_mediumhealingpotion$ (2 $fo_healingherb$, 1 $fo_water$)', '$fo_mediumhealingpotion$ (2 $fo_healingherb$, 1 $fo_water$)', '$fo_mediumhealingpotion$ (2 $fo_healingherb$, 1 $fo_water$)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GreaterHealingPotion', 'dl_npc_syrah', '$fo_greaterhealingpotion$ (3 $fo_healingherb$, 1 $fo_water$)', '$fo_greaterhealingpotion$ (3 $fo_healingherb$, 1 $fo_water$)', '$fo_greaterhealingpotion$ (3 $fo_healingherb$, 1 $fo_water$)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ElixirFire', 'dl_npc_syrah', '$pe_elixirfire$ (1 $fo_redhat$, 1 $fo_stormweed$, 1 $fo_wine$)', '$pe_elixirfire$ (1 $fo_redhat$, 1 $fo_stormweed$, 1 $fo_wine$)', '$pe_elixirfire$ (1 $fo_redhat$, 1 $fo_stormweed$, 1 $fo_wine$)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ElixirLight', 'dl_npc_syrah', '$pe_elixirlight$ (1 $fo_glowingshroom$, 1 $fo_stormweed$, 1 $fo_wine$)', '$pe_elixirlight$ (1 $fo_glowingshroom$, 1 $fo_stormweed$, 1 $fo_wine$)', '$pe_elixirlight$ (1 $fo_glowingshroom$, 1 $fo_stormweed$, 1 $fo_wine$)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ElixirShadow', 'dl_npc_syrah', '$pe_elixirshadow$ (1 $fo_caveberry$, 1 $fo_stormweed$, 1 $fo_wine$)', '$pe_elixirshadow$ (1 $fo_caveberry$, 1 $fo_stormweed$, 1 $fo_wine$)', '$pe_elixirshadow$ (1 $fo_caveberry$, 1 $fo_stormweed$, 1 $fo_wine$)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ElixirIce', 'dl_npc_syrah', '$pe_elixirice$ (1 $fo_windrose$, 1 $fo_stormweed$, 1 $fo_wine$)', '$pe_elixirice$ (1 $fo_windrose$, 1 $fo_stormweed$, 1 $fo_wine$)', '$pe_elixirice$ (1 $fo_windrose$, 1 $fo_stormweed$, 1 $fo_wine$)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ComeBackLater', 'dl_npc_syrah', 'I''ll come back later [BACK]', 'Ich komme später wieder [ZURÜCK]', 'Ich chume spöter wider [ZRUGG]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_HereYouGo', 'dl_npc_syrah', 'Thanks. Here you go.', 'Danke. Hier, für dich.', 'Danke. Da, nimms.');
