INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Greeting', 'dl_npc_syrah', 'Well hello there! You look like you could use some potions made by the infamous alchemist Syrah. A love potion or a hair dye - there''s nearly nothing I''m not capable of brewing.', 'Na hallo! Du siehst aus als könntest du ein paar Tränke von der berühmt-berüchtigten Alchemistin Syrah gebrauchen. Ob Liebestrank oder Haarfarbe - es gibt fast nichts, was ich nicht zusammenbrauen könnte.', 'Jo sali! Du gsesch us als chönntisch du es paar Tränk v dr berüemt-berüchtigte Alchemischtin Syrah bruuche. Ob Liebestrank oder Hoorfarb - es git fascht nüt woni nid chönt zämmämischlä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_InvisRecipe', 'dl_npc_syrah', 'Oh, hello. There''s a certain recipe missing from my basement. Do you know something about it?', 'Oh, hallo. Sag mal... Es fehlt ein bestimmtes Rezept aus meinem Keller. Weisst du etwas über diese Sache?', 'Oh hallo. Du, sag mol... Es fehlt es bestimmts Rezept us mim Käller. Weisch du öppis über die Sach?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IBroughtItBack', 'dl_npc_syrah', 'Yes. I got it back for you. (Give the recipe)', 'Ja. Ich hab es dir zurück gebracht. (Rezept geben)', 'Ja. Ich has der zrugg bracht. (Rezept gä)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IKnowNothing', 'dl_npc_syrah', 'No, sorry.', 'Nein, sorry.', 'Nei, sorry.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_IBroughtItBack', 'dl_npc_syrah', 'Wow, thank you. You know, that really means a lot to me. I won''t ask where you got that though. (Winks)', 'Wow, danke. Weisst du, das bedeutet mir wirklich ne Menge. Ich frag gar nicht erst, wie du das geschafft hast. (Zwinkert)', 'Wow, merci. Weisch, das bedütet mr wirklig viel. Ich froog gar nid erscht, wied da übercho hesch. (Zwinkeret)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_IKnowNothing', 'dl_npc_syrah', 'What a pity.', 'Schade.', 'Schad.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mark', 'dl_npc_syrah', 'Can you remove this curse? (Show the mark)', 'Kannst du diesen Fluch aufheben? (Mal zeigen)', 'Chasch du de Fluech wegmache? (Zeiche zeige)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Sewers', 'dl_npc_syrah', 'How can I get to the sewers?', 'Wie komme ich in die Kanalisation?', 'We chumi id Kanalisation?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_BrewQuestDone', 'dl_npc_syrah', 'I got your ingredients. (Give ingredients)', 'Ich habe deine Zutaten. (Zutaten geben)', 'Ich ha dini Zuetate. (Zuetate gä)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AntimagicPendant', 'dl_npc_syrah', 'I got this anti-magic pendant here, would this work?', 'Ich habe diesen antimagischen Anhänger hier, würde das funktionieren?', 'Ich ha de antimagisch Ahänger do, wür das funktioniere?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_MarkQuestDone', 'dl_npc_syrah', 'I got what you wanted. (Give crystal)', 'Ich habe, was du brauchst. (Kristall geben)', 'Ich han was du bruchsch. (Kristall gä)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FavourQuestStart', 'dl_npc_syrah', 'What kind of favour should that be?', 'Was für ein Gefallen soll das sein?', 'Was füren Gfalle söl das si?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CompleteFavor', 'dl_npc_syrah', 'I got the cinderbloom for you.', 'Ich habe die Aschenblüte für dich.', 'Ich han d''Zündelblueme für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NecromancersFound', 'dl_npc_syrah', 'So you''re a necromancer.', 'Du bist also eine Nekromantin.', 'Du bisch also e Nekromantin.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Thea', 'dl_npc_syrah', 'Are you looking for an apprentice?', 'Suchst du einen Lehrling?', 'Suechsch en Lehrling?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Sack', 'dl_npc_syrah', 'Can I get some hair dye from you?', 'Kann ich dann ein Haarfärbemittel haben?', 'Chani denn es Hoorfärbemittel ha?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CanYouBrew', 'dl_npc_syrah', 'Can you brew something for me?', 'Kannst du mir etwas brauen?', 'Chaschmer öppis zemebraue?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Trade', 'dl_npc_syrah', 'Show me your potions.', 'Zeig mir deine Tränke.', 'Zeig mer dini Tränk.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Mark', 'dl_npc_syrah', '(Syrah eyes the mark carefully) Hm. Definitely a tracking curse. But it''s one of the strongest I''ve ever seen.', '(Syrah betrachtet das Mal sorgfältig) Hm. Ganz klar ein Aufspür-Zauber. Aber einer der stärksten, die ich je gesehen habe.', '(Syrah luegt s''Zeiche vorsichtig ah) Hm. Ganz klar e Uffspür-Zauber. Aber eine vode stärkschtä woni je gseh ha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Mark2', 'dl_npc_syrah', 'That''s not going to be easy. If I had an anti-magic crystal, I could try to remove or at least weaken the curse.', 'Das wird nicht einfach. Wenn ich einen antimagischen Kristall hätte, könnte ich versuchen, den Fluch zu entfernen oder zumindest zu schwächen.', 'Das wird nid eifach. Wenni en antimagische Kristall hett, chani versueche, dr Fluech z''entferne oder mindestens z''schwäche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Mark3', 'dl_npc_syrah', 'But it''s not easy to get such a crystal. I know that the clerics use them to restrain the magic of their prisoners.', 'Aber es ist nicht einfach, an einen solchen Kristall zu kommen. Ich weiss, dass die Kleriker sie verwenden, um die Magie ihrer Gefangenen im Zaum zu halten.', 'Aber es isch nid eifach, a sonen Kristall z''cho. Ich weiss, dass d''Kleriker so Kristall bruched, zum d''Magie vo ihrne Gfangene im Zaum zhalte.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Mark4', 'dl_npc_syrah', 'I''m pretty sure they have one in their tower. Hm... but it is locked.', 'Ich bin mir ziemlich sicher, dass sie einen in ihrem Turm haben. Hm... aber der ist verschlossen.', 'Ich bimer zimli sicher, dass si ein i ihrem Turm hend. Hm. Aber de isch vrschlosse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Mark5', 'dl_npc_syrah', 'You could try to steal the tower key from a paladin... or - if that''s not your way of solving problems - just buy a crystal.', 'Du könntest versuchen, den Turmschlüssel von einem Paladin zu stehlen... oder - wenn das nicht deine Art ist, kannst du den Kristall vermutlich auch kaufen.', 'Du chöntsch probiere, de Turmschlüssel vomne Paladin z''chlaue... oder - wenn das nid so dini Art isch, chasch wahrschinli au en Kristall chaufe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Sewers', 'dl_npc_syrah', 'Some houses have a basement with a connection to the sewers. I think there''s also a door near the harbour that leads down.', 'Ein paar Häuser haben in ihrem Keller eine Verbindung zur Kanalisation. Ich glaube, es gibt auch eine Türe in der Nähe des Hafens, die dort runter führt.', 'Ä paar Hüüser hei in ihrem Käller e Verbindig zu dr Kanalisation. I glaub es git au e Düüre in dr Nöchi vom Hafe wo dört aabä füehrt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_BrewQuestDone', 'dl_npc_syrah', 'Thank you so much, that''s perfect! I''ll brew potions for you now if you bring me some herbs.', 'Vielen Dank, das ist perfekt! Ich werde dir nun Tränke brauen, wenn du mir Kräuter bringst.', 'Jä danke, das isch perfekt! Ich wirde der jetz Tränkli braue, wennd mer d''Chrütli drzue bringsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_AntimagicPendant', 'dl_npc_syrah', 'That''s an interesting pendant you got there. It''s strong, but not strong enough to remove your curse, I''m afraid.', 'Das ist ein interessanter Anhänger, den du da hast. Er ist mächtig, aber es reicht nicht, um deinen Fluch aufzuheben.', 'Das isch en interessante Ahänger, wod da hesch. Er isch mächtig, aber nid mächtig gnueg, zum din Fluech ufhebe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_AntimagicPendant2', 'dl_npc_syrah', 'We need a bigger crystal.', 'Wir brauchen einen grösseren Kristall.', 'Mer bruched en grössere Kristall.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_MarkQuestDone', 'dl_npc_syrah', 'Ah, perfect. Now let''s get started.', 'Ah, perfekt. Dann lass uns anfangen.', 'Ah, perfekt. Denn föngemer mol ah.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_MarkQuestDone2', 'dl_npc_syrah', '(Syrah uses the crystal to draw a glowing circle on the floor) Come.', '(Syrah zeichnet mit dem Kristall ein glühender Kreis auf den Boden) Komm.', '(D''Syrah zeichnet mitem Kristall en glüehende Chreis uf de Bode) Chumm.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_OkayMark', 'dl_npc_syrah', 'Okay...', 'Okay...', 'Okay...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotOkayMark', 'dl_npc_syrah', 'I''m not sure whether this is a good idea.', 'Ich weiss nicht, ob das eine gute Idee ist.', 'Ich weiss nid ob da so e gueti Idee isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_RelieveCurse', 'dl_npc_syrah', '(Syrah grabs your arm and drags you into the circle.)', '(Syrah packt dich am Arm und zieht dich in den Kreis.)', '(D''Syrah packt di am Arm und zieht di in Kreis.)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_RelieveCurse2', 'dl_npc_syrah', '(She takes the crystal and presses it onto the mark, while murmuring some words.)', '(Sie nimmt den Kristall und drückt ihn auf das Mal, während sie ein paar Worte murmelt.)', '(Sie nimmt de Kristall und druckt ihn ufs Zeiche, während sie öppis murmelet.)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_RelieveCurse3', 'dl_npc_syrah', 'See? I think it worked. I don''t feel much of this strange magic anymore. ', 'Siehst du? Ich denke, es hat funktioniert. Ich spüre nicht mehr viel von dieser seltsamen Magie.', 'Gsehsch. I glaub s''het klappt. I gspür nümm viel vo dere komische Magie.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_RelieveCurse4', 'dl_npc_syrah', 'Hm. It''s still visible. You''ll have to seek out who did this to you. But now you can find him before he finds you. (Giggles)', 'Hm. Es ist immer noch sichtbar. Du solltest denjenigen ausfindig machen, der dir das angetan hat. Doch jetzt kannst du ihn finden, bevor er es tut. (Kichert)', 'Hm. Me gsehts immer no. Du söttsch dä sueche, wo dir das aadoo het. Und jetzt chaschen finde, bevor er s''macht. (Giigelet)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_DoMeAFavour', 'dl_npc_syrah', 'But now, as I''ve done you a favour, you should do me one too, I think.', 'Aber jetzt, wo ich dir einen Gefallen getan habe, solltest du mir auch einen tun.', 'Aber jetz, woni dir ä Gfalle do ha, söttsch mr au eine due.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_NotOkayMark2', 'dl_npc_syrah', 'I''m only trying to help you! You want to be relieved from your curse, right? Now come here.', 'Ich versuch doch nur, dir zu helfen! Du willst von diesem Fluch befreit werden, oder? Jetzt komm her.', 'I brobier doch nummä, dir z''hälfe! Du willsch doch vo dem Fluech befreit si, oder? Jetzt chumm.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_FavourQuestStart', 'dl_npc_syrah', 'There''s a graveyard just outside the city, in the marshlands, where people are buried that were not welcome in the city - mostly criminals.', 'Es gibt einen Friedhof im Sumpfland direkt vor der Stadt. Dort liegen die Leute begraben, die in der Stadt nicht erwünscht waren - vor allem Verbrecher.', 'Es git ä Friedhof im Sumpfland diräkt vor dr Stadt, wo d''Lüt begrabe si wo in dr Stadt nid willkomme gsi si - vor allem Verbrächer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_FavourQuestStart2', 'dl_npc_syrah', 'I need a special flower, the cinderbloom, for one of my potions. It only grows on graves of people who have died in a fire. ', 'Ich brauche eine spezielle Blume, die Aschenblüte, aus der Krypta dort. Sie wächst nur auf den Gräbern von Leuten, die in einem Feuer umgekommen sind.', 'I bruch e speziells Bliemli, s''Zündelbliemli. Si waggst nur uf de Gräber vo Lüt, wo im Fyyr gstorbe si.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_FavourQuestStart3', 'dl_npc_syrah', 'I''m sure that you can find that flower there - mages who "misuse" magic are often burnt at the stake and whatever remains will be taken to this crypt.', 'Ich bin mir sicher, dass du diese Blume dort finden kannst - Magier, die Magie "missbrauchen", werden meistens auf dem Scheiterhaufen verbrannt und was auch immer übrig bleibt, wird in diese Krypta geschafft.', 'I bimmer sicher, dassd s''Bliemli dört finde chasch - Magier wo Magie "missbruchä" wärde meistens ufem Schitterhuffe verbrennt und was immer übrig blibt wird in die Krypta verfrachtet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_FavourQuestStart4', 'dl_npc_syrah', 'Oh, and if you happen to meet the grave digger Morton, he''s a good friend of mine. Just tell him I sent you, and he''ll let you in.', 'Oh, und wenn du dem Totengräber Morton begegnen solltest, sag ihm einfach, dass ich dich geschickt habe, und er wird dich hinein lassen.', 'Oh, und wennd em Totegräber Morton begägne sötsch, sagem eifach dass ich di gschickt ha, denn sötter di iineloh.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_CompleteFavor', 'dl_npc_syrah', 'Thank you so much, that''s very kind of you. Here, take a potion for your effort.', 'Danke vielmals, das ist sehr lieb von dir. Nimm noch einen Trank für deine Mühen.', 'Merci vielmool, das isch sehr lieb vo dir. Nimm nonä Trank für dini Müeh.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_NecromancersFound', 'dl_npc_syrah', 'Hehe. Glad to hear you''ve found Luiz and his crew. Keep it to yourself and you''ll make some really good friends.', 'Hihi. Schön, dass du Luiz und seine Crew gefunden hast. Behalt es für dich und du wirst dir ein paar wirklich gute neue Freunde machen.', 'Hihi. Schön, dass dr Luiz und sini Crew gfunde hesch. Bhalts eifach für di und du wirsch es paar wirklig gueti neui Kollege ha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Thea', 'dl_npc_syrah', 'Do you know something about herbs and potions then?', 'Kennst du dich denn mit Kräutern und Tränken aus?', 'Kennsch di denn mit Krüttli und Tränk us?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_Thea', 'dl_npc_syrah', 'No, not me, I''m asking for a girl called Thea. She''s looking for a job.', 'Nein, nicht ich. Ich frage für ein Mädchen namens Thea. Sie sucht Arbeit.', 'Nei, nid ich. Ich frög für es Meitli namens Thea, si suecht Arbet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Thea2', 'dl_npc_syrah', 'Ah ok. Yes, you can tell her she can start here, as long as she''s not totally clumsy.', 'Ah okay. Ja, du kannst ihr sagen, sie kann hier beginnen, solange sie nicht völlig tollpatschig ist.', 'Ah okay. Jo, du chasch ere sagä, si cha do aafoh, solang sie nid komplett tollpatschig isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Sack', 'dl_npc_syrah', '(Giggles) I was joking. But if you''re so unhappy with your hair, I might have something for you.', '(Kichert) Ich habe gescherzt. Aber wenn du so unglücklich mit deinen Haaren bist, könnte ich etwas für dich haben.', '(Gigglet) Ich hanen Witz gmacht. Aber wennd so unglücklich mit dine Hoore bisch, hani vilicht öppis für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_Sack2', 'dl_npc_syrah', 'Here, it''ll solve your problem.', 'Hier, er wird dein Problem lösen.', 'Do, er wird dis Problem löse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_BrewSomething', 'dl_npc_syrah', 'Sure, if you got the ingredients?', 'Sicher, wenn du die Zutaten hast?', 'Sicher, wennd d''Zuetate hesch?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LesserHealingPotion', 'dl_npc_syrah', 'fo_lesserhealingpotion,fo_healingherb,1,fo_water,1', 'fo_lesserhealingpotion,fo_healingherb,1,fo_water,1', 'fo_lesserhealingpotion,fo_healingherb,1,fo_water,1');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_MediumHealingPotion', 'dl_npc_syrah', 'fo_mediumhealingpotion,fo_healingherb,2,fo_water,1', 'fo_mediumhealingpotion,fo_healingherb,2,fo_water,1', 'fo_mediumhealingpotion,fo_healingherb,2,fo_water,1');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GreaterHealingPotion', 'dl_npc_syrah', 'fo_greaterhealingpotion,fo_healingherb,3,fo_water,1', 'fo_greaterhealingpotion,fo_healingherb,3,fo_water,1', 'fo_greaterhealingpotion,fo_healingherb,3,fo_water,1');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ElixirFire', 'dl_npc_syrah', 'pe_elixirfire,fo_redhat,1,fo_stormweed,1,fo_wine,1', 'pe_elixirfire,fo_redhat,1,fo_stormweed,1,fo_wine,1', 'pe_elixirfire,fo_redhat,1,fo_stormweed,1,fo_wine,1');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ElixirLight', 'dl_npc_syrah', 'pe_elixirlight,fo_glowingshroom,1,fo_stormweed,1,fo_wine,1', 'pe_elixirlight,fo_glowingshroom,1,fo_stormweed,1,fo_wine,1', 'pe_elixirlight,fo_glowingshroom,1,fo_stormweed,1,fo_wine,1');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ElixirShadow', 'dl_npc_syrah', 'pe_elixirshadow,fo_caveberry,1,fo_stormweed,1fo_wine,1', 'pe_elixirshadow,fo_caveberry,1,fo_stormweed,1fo_wine,1', 'pe_elixirshadow,fo_caveberry,1,fo_stormweed,1fo_wine,1');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ElixirIce', 'dl_npc_syrah', 'pe_elixirice,fo_windrose,1,fo_stormweed,1,fo_wine,1', 'pe_elixirice,fo_windrose,1,fo_stormweed,1,fo_wine,1', 'pe_elixirice,fo_windrose,1,fo_stormweed,1,fo_wine,1');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ComeBackLater', 'dl_npc_syrah', 'I''ll come back later [BACK]', 'Ich komme später wieder [ZURÜCK]', 'Ich chume spöter wider [ZRUGG]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_HereYouGo', 'dl_npc_syrah', 'Thanks. Here you go.', 'Danke. Hier, für dich.', 'Merci. Do, nimms.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_BrewQuestStart', 'dl_npc_syrah', 'Hm. Yes, I could. For you, it''s even free... if you do me a small favour.', 'Hm. Ja, das könnte ich. Es wird dich nicht mal etwas kosten... wenn du mir einen kleinen Gefallen machst.', 'Hm. Jo, das chönti. Es wär denn sogar gratis für di... wennd mer en chliine Gfalle duesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_BrewQuestStart', 'dl_npc_syrah', 'I still need some ingredients for a potion that I wanted to brew.', 'Mir fehlen noch ein paar Zutaten für einen Trank, den ich mir brauen wollte.', 'Mir fehled na paar Zuetate für es Tränkli woni mr ha wele braue.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_BrewQuestStart3', 'dl_npc_syrah', 'I only need some bones, some blood of a bat and the heart of a fire rat. I think you can gather all of this from the nasty creatures in the sewers.', 'Ich brauche nur ein paar Knochen, ein wenig Blut einer Fledermaus und das Herz einer Feuerratte. Ich denke, das kannst du alles von den garstigen Kreaturen in der Kanalisation von Gandria bekommen.', 'Ich bruch nur es paar Knoche, e glei weeneli Bluet vonere Fläädermuus und es Härz vonere Fyyrratte. I glaub das söttisch alles vo dä Viecher in dr Kanalisation vo Gandria becho.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Syrah_BrewQuestStart4', 'dl_npc_syrah', 'If you manage to get me that, I''ll brew extraordinary potions from the herbs you bring me!', 'Wenn du es schaffst, mir das zu holen, werde ich dir aussergewöhnliche Tränke aus den Kräutern brauen, die dur mir bringst.', 'Wenn du''s schaffsch, mir das z''hole, wirdi der ussergwöhnlichi Tränkli us dene Chrüüter, wod mer bringsch, braue.');
