INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Thea_Hey2', 'dl_npc_thea2', 'I wouldn''t be here without you. Here, take this herb, it''s very rare and might be useful for you. (Winks)', 'Ohne dich wäre ich nicht hier. Da, nimm dieses Kraut, es ist sehr selten und könnte nützlich sein. (Zwinkert)', 'Ohni di wäri nid da. Hiä, nimm das Chrüttli, äs isch sehr säutä u chönnt nützlech für di sii. (Zwinkeret)');
