INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_Talked', 'dl_npc_velius', 'Finally. I waited so long for this.', 'Endlich. Darauf habe ich lange gewartet.', 'Endlich. Do druf hani lang gwartet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Finally', 'dl_npc_velius', 'Ehm... You waited for what?', 'Ähm... Auf was denn?', 'Ähm... Uf was denn?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Murderer', 'dl_npc_velius', 'You... just murdered the king?!', 'Du... hast gerade den König ermordet?!', 'Du... hesch grad de König ermordet?!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Eyes', 'dl_npc_velius', 'What''s the matter with the kings''s eyes?', 'Was ist eigentlich mit den Augen des Königs los?', 'Wa isch eigentlich mit de Auge vom König los?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_KnowMe', 'dl_npc_velius', 'I assume, you know me?', 'Ich nehme mal an, du kennst mich?', 'Ich nimm mal ah, du kennsch mich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Friends', 'dl_npc_velius', 'What did you do to the other mages?', 'Was hast du mit den anderen Magiern gemacht?', 'Was hesch mit de andere Magie gmacht?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mark', 'dl_npc_velius', 'Is that your work? (Show the mark)', 'Ist das dein Werk? (Das Mal zeigen)', 'Isch das dis Werk? (Es Zeiche zeige)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Escape', 'dl_npc_velius', 'How exactly did I escape?', 'Wie genau bin ich denn entkommen?', 'Wie bini denn gnau entcho?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Track', 'dl_npc_velius', 'So you could track me - why didn''t you catch me earlier?', 'Wenn du mich aufspüren konntest - wieso hast du mich nicht früher gefangen?', 'Wenn du mich hesch chöne ufspüre - weso hesch mi nid früener gfange?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_OldKing', 'dl_npc_velius', 'What did you do to the old king?', 'Was hast du mit dem alten König gemacht?', 'Was hesch mitem alte König gmacht?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Plan', 'dl_npc_velius', 'You don''t need a king? What are your plans then?', 'Du brauchst keinen König? Was sind denn deine Pläne?', 'Du bruchsch ken König? Wa sind denn dini Plän?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyMe', 'dl_npc_velius', 'Why do you need exactly me for your plans?', 'Wieso brauchst du genau mich für deine Pläne?', 'Weso bruchsch du genau mich für dini Plän?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FightStart', 'dl_npc_velius', 'What exactly are those anti-magic crystals?', 'Was sind diese Antimagiekristalle genau?', 'Wa sind die Antimagiekristall genau?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Bitter', 'dl_npc_velius', 'Not all magic is bad. Why do you hate it so much?', 'Nicht alle Magie ist schlecht. Wieso hasst du sie so sehr?', 'Nid alli Magie isch schlecht. Weso hassisch si so sehr?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AntiMagic', 'dl_npc_velius', 'What did you steal from the elementalists'' temple?', 'Was hast du aus dem Tempel der Elementarmagier gestohlen?', 'Wa hesch usem Elementarmagiertempel klaut?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Creatures', 'dl_npc_velius', 'So... those challenges weren''t there to hinder me?', 'Also... diese Hindernisse waren nicht da, um mich aufzuhalten?', 'Also... die Hindernis sind nid do gsi, zum mich ufhalte?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Illusion', 'dl_npc_velius', 'Five magic types? I thought there were only four...', 'Fünf Magiearten? Ich dachte, es wären nur vier...', 'Füf Magiearte? Ich ha denkt, es sind nur vier...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Fight', 'dl_npc_velius', 'I''ve heard enough. Let''s get this over with. [ATTACK VELIUS] ', 'Ich habe genug gehört. Bringen wir''s hinter uns. [VELIUS ANGREIFEN]', 'Ich ha gnueg ghört. Bringemer''s hinter üs. [DE VELIUS AAGRIFE]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Finally', 'dl_npc_velius', 'For you. To fall directly into my trap. (Grins)', 'Auf dich. Dass du direkt in meine Falle läufst. (Grinst)', 'Uf dich. Dass du direkt i mini Falle laufsch. (Grinst)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Murderer', 'dl_npc_velius', 'No. He''s just sleeping. But, he nearly eliminated my most precious subject -', 'Nein. Er schläft nur. Aber er hat beinahe mein wertvollstes Subjekt eliminiert -', 'Nei. Er schloft bloss. Aber er hett fast mis wertvollst Subjekt eliminiert -');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Murderer2', 'dl_npc_velius', 'You.', 'Dich.', 'Dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Murderer3', 'dl_npc_velius', 'But now, that I got you... there''s no need for a king anymore.', 'Aber jetzt, wo ich dich habe... braucht es keinen König mehr.', 'Aber jetz, wo ich dich ha... bruchts ken König me.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Eyes', 'dl_npc_velius', 'I couldn''t hide that, right. He''s been under my control for too long.', 'Das konnte ich nicht verbergen, was. Er ist schon zu lange unter meiner Kontrolle.', 'Das hani nid verberge chöne, he. Er isch scho z''lang unter minere Kontrolle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_KnowMe', 'dl_npc_velius', 'Yes, I do. The subject that escaped.', 'Ja, dem ist so. Das Subjekt, das entkommen ist.', 'Ja, das isch so. Es Subjekt, wo entcho isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_KnowMe2', 'dl_npc_velius', 'But you''ve been never really free. I always knew exactly where you were and what you did.', 'Aber du warst niemals wirklich frei. Ich wusste immer genau, wo du warst und was du getan hast.', 'Aber du bisch nie würkli frei gsi. Ich ha immer gnau gwüsst, wo du bisch und wa du machsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_KnowMe3', 'dl_npc_velius', 'My curse has fulfilled its purpose.', 'Mein Fluch hat seinen Zweck erfüllt.', 'Min Fluech het sin Zweck erfüllt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Friends', 'dl_npc_velius', 'Most of them didn''t survive it. The ones who did, went insane.', 'Die meisten haben es nicht überlebt. Diejenigen, die es geschafft haben, sind durchgedreht.', 'Di meiste hends nid überlebt. Die, wos gschafft hend, sind duredrüllt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Friends2', 'dl_npc_velius', 'But after all these experiments, I finally know what I need.', 'Aber nach all diesen Experimenten weiss ich endlich, was ich brauche.', 'Aber noch all dene Experiment weissi endlich, wani brucht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Friends3', 'dl_npc_velius', 'Mages with a strong affinity to a magic type. The three I got now are perfect.', 'Magier mit einer starken Affinität zu einer Magieart. Die drei, die ich jetzt habe, sind perfekt.', 'Magier mit enere starche Affinität zunere Magierart. Die drei, woni ez ha, sind perfekt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Mark', 'dl_npc_velius', '(Smiles) Of course it is.', '(Lächelt) Natürlich ist es das.', '(Lächlet) Natürli isches das.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Mark2', 'dl_npc_velius', 'It made my subjects easier to track - the few, that managed to escape.', 'Meine Subjekte waren damit einfacher aufzuspüren - die wenigen, denen die Flucht gelang.', 'Mini Subjekt hani demit eifacher chöne ufspüre - di wenige, wo entcho sind.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Escape', 'dl_npc_velius', 'Well, you really surprised me.', 'Nun, du hast mich wirklich überrascht.', 'Ja, du hesch mi würkli überrascht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Escape2', 'dl_npc_velius', 'I extracted your magic the same way as I did with many other mages before.', 'Ich habe deine Magie auf die selbe Art extrahiert, wie auch schon bei vielen anderen Magiern zuvor.', 'Ich ha dini Magie uf di gliich Art extrahiert, we au scho mit vilne andere Magiere vorene.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Escape3', 'dl_npc_velius', 'But somehow, it didn''t work as expected.', 'Aber es hat irgendwie nicht wie erwartet funktioniert.', 'Aber es het irgendwie nid we erwartet funktioniert.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Escape4', 'dl_npc_velius', 'You managed to disappear before my very eyes. ', 'Du hast es geschafft, direkt vor meinen Augen zu verschwinden.', 'Du heschs gschafft, direkt vor mine Auge z''verschwinde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Escape5', 'dl_npc_velius', 'But now, here you are...', 'Aber jetzt bist du ja hier...', 'Aber jetz bisch ja do...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Track', 'dl_npc_velius', 'You weren''t ready. Your magic wasn''t ready.', 'Du warst nicht bereit. Deine Magie war nicht bereit.', 'Du bisch nid parat gsi. Dini Magie isch nid parat gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Track2', 'dl_npc_velius', 'Also, as you''ve lost your memory, you''ve never been a threat to my secrets.', 'Und da du dein Gedächtnis verloren hast, warst du niemals eine Bedrohung für meine Geheimnisse.', 'Und well du dis Gedächtnis verlore hesch, bisch nie e Bedrohig für mini Gheimnis gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_OldKing', 'dl_npc_velius', 'Hn. The old king. I thought he''d be a suitable subject for the divine magic.', 'Hn. Der alte König. Ich hätte gedacht, er wäre ein geeignetes Subjekt für die heilige Magie.', 'Hn. De alt König. Ich ha denkt, er seg es geignets Subjekt für di heilig Magie.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_OldKing2', 'dl_npc_velius', 'But I was wrong. He wasn''t strong enough. He didn''t make it.', 'Aber da lag ich falsch. Er war nicht stark genug. Er hat es nicht geschafft.', 'Aber do hani mi g''irrt. Er isch nid starch gnueg gsi. Er hets nid gschafft.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Plan', 'dl_npc_velius', 'You see what''s happening in this world, right?', 'Dir ist bewusst, was in dieser Welt passiert, ja?', 'Dir isch bewusst, wa i dere Welt passiert, gell?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Plan2', 'dl_npc_velius', 'Mages are dominating the commoners. Just because you are born without magic, you''re inferior.', 'Die Magier beherrschen die Gewöhnlichen. Nur weil jemand ohne Magie geboren wurde, ist er weniger wert.', 'D''Magier beherrsched di Gwöhnliche. Nur well öpper ohni Magie gebore worde isch, ischer weniger wert.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Plan3', 'dl_npc_velius', 'But soon, this age will be history. All magic will perish from this world. FOREVER!', 'Aber bald wird dieses Zeitalter Geschichte sein. Alle Magie wird aus dieser Welt verschwinden. FÜR IMMER!', 'Aber bald wird da Ziitalter verbi si. Alli Magie wird us dere Welt verschwinde. FÜR IMMER!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_WhyMe', 'dl_npc_velius', 'I need affine mages from all magic types. Elementalists are very rare.', 'Ich brauche affine Magier von allen Magietypen. Elementarmagier sind sehr selten.', 'Ich bruch affini Magier vo allne Magietype. Elementarmagier sind sehr selte.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_WhyMe2', 'dl_npc_velius', 'As I first caught you, you were very weak. Too weak for my purposes.', 'Als ich dich zuerst gefangen habe, warst du sehr schwach. Zu schwach für meine Zwecke.', 'Wo ich dich zersch gfange ha, bisch sehr schwach gsi. Zu schwach für mini Zweck.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_WhyMe3', 'dl_npc_velius', 'I needed a full-fledged elementalist. Only such a mage could overcome the challenges I put in your way.', 'Ich brauchte einen vollwertigen Elementarmagier. Nur so ein Magier konnte die Hindernisse überwinden, die ich dir in den Weg gestellt habe.', 'Ich ha en vollwertige Elementarmagier brucht. Nur so en Magier het die Hindernis chöne überwinde, woni der in Weg gstellt ha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_WhyMe4', 'dl_npc_velius', 'You mastered the elements and found me. That''s proof enough that your magic is strong enough now.', 'Du hast die Elemente gemeistert und mich gefunden. Das ist Beweis genug, dass deine Magie nun stark genug ist.', 'Du hesch d''Element gmeisteret und mich gfunde. Da isch Bewiis gnueg, dass dini Magie ez starch gnueg isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_FightStart', 'dl_npc_velius', 'Condensed anti-magic. The one I''m planning to create now will be powerful enough to take the magic from every mage in this world.', 'Kondensierte Antimagie. Derjenige, den ich jetzt erschaffen werde, wird stark genug sein, die Magie jedes Magiers in dieser Welt zu nehmen.', 'Kondensierti Antimagie. De, woni ez erschaffe wird, wird mächtig gnueg si, zum alli Magie vo jedem Magier i dere Welt z''ne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_FightStart2', 'dl_npc_velius', 'I''m going to create a better world for everyone. You understand why I need your magic for that, right?', 'Ich werde eine bessere Welt für alle erschaffen. Du verstehst, wieso ich deine Magie dafür brauche, ja?', 'Ich wird e besseri Welt für alli erschaffe. Du verstosch, weso ich dini Magie für das bruch, gell?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_KillMe', 'dl_npc_velius', 'No. If that means you''re going to kill me...', 'Nein. Wenn das bedeutet, dass du mich dafür töten musst...', 'Nei. Wenn das bedütet, dass du mich defür töte muesch...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YoureRight', 'dl_npc_velius', 'Well, I kinda understand you. But it''s still not the right thing to do.', 'Ich verstehe dich ja ein bisschen. Aber es ist nicht der richtige Weg.', 'Ich verstand di jo es bitzli. Aber es isch nid de richtig Weg.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YoureVeryRight', 'dl_npc_velius', 'Yes. Magic only brings destruction. Just look at you.', 'Ja. Magie bringt nur Zerstörung. Du bist selbst das beste Beispiel.', 'Ja. Magie bring nur Zerstörig. Du bisch s''best Bispiil defür.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_KillMe', 'dl_npc_velius', 'Mages survive the process of annihilating their magic with anti-magic, but not extracting it, I''m sorry.', 'Magier überleben den Prozess, wenn man ihre Magie mit Antimagie auslöscht, aber nicht, wenn man sie extrahiert. Tut mir leid.', 'Magier überlebed de Prozess, wennme ihri Magie mit Antimagie uslöscht, aber nid, wenme si extrahiert. Tuet mer leid.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_KillMe2', 'dl_npc_velius', 'It''s for a greater cause. I''ll make it quick if you don''t struggle.', 'Es ist für eine grössere Sache. Es wird kurz und schmerzlos sein, wenn du dich nicht wehrst.', 'Es isch für e grösseri Sach. Es wird churz und schmerzlos si, wennd dich nid wehrsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_YoureRight', 'dl_npc_velius', 'That''s your opinion. But it''s worth nothing.', 'Das ist deine Meinung. Die ist aber sowieso nicht viel wert.', 'Da isch dini Meinig. Die isch aber eh nid viel wert.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_YoureRight2', 'dl_npc_velius', 'You won''t have anyone to tell it anymore.', 'Du wirst sie nämlich niemandem mehr erzählen können.', 'Du wirsch si nämli niemertem me verzelle chöne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_YoureVeryRight', 'dl_npc_velius', 'The same goes for you. No one should be this powerful. It only brings pain.', 'Dasselbe gilt auch für dich. Niemand sollte so mächtig sein. Es bringt nur Schmerz.', 'Es glich gilt au für dich. Niemert söt so mächtig si. Es bringt nur Schmerz.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Bitter', 'dl_npc_velius', 'You''re right. Magic is not bad, but what people do with it is.', 'Du hast recht. Magie ist nicht schlecht, aber was die Leute mit ihr anstellen, schon.', 'Du hesch recht. Magie isch nid schlecht, aber was d''Lüt mit ihre astelled, scho.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Bitter2', 'dl_npc_velius', 'I was born into a family of commoners. I know what it feels like to be treated like dirt.', 'Ich wurde in eine Familie von Gewöhnlichen geboren. Ich weiss, wie es sich anfühlt, wie Dreck behandelt zu werden.', 'Ich bi inere Familie vo Gwöhnliche gebore worde. Ich weiss, we''s sich afühlt, we Dreck behandlet z''werde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Bitter3', 'dl_npc_velius', 'I had to find a way to take away this power from all mages. And I did.', 'Ich musste einen Weg finden, diese Macht den Magiern zu nehmen. Und das habe ich jetzt.', 'Ich ha en Weg finde möse, die Macht de Magier wegzne. Und das hani ez.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_AntiMagic', 'dl_npc_velius', 'I needed more information on anti-magic to develop the perfect anti-magic crystal.', 'Ich brauchte mehr Information über Antimagie, um den perfekten Antimagiekristall zu entwickeln.', 'Ich ha me Informatione über Antimagie brucht, zum de perfekt Antimagiekristall z''entwickle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_AntiMagic2', 'dl_npc_velius', 'Now I got it. Inverting and combining magic from affine mages from all five magic types will result in the perfect crystal.', 'Jetzt habe ich es. Wenn ich die Magie von affinen Magiern von allen Magietypen invertiere und kombiniere, kann ich den Kristall damit erschaffen.', 'Jetz hanis. Weni d''Magie vo affine Magier vo allne füf Magietype kombinier und invertier, chani de perfekt Kristall erschaffe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Creatures', 'dl_npc_velius', 'They were there to test you. Khajag. Alastor. Who do you think summoned them?', 'Sie waren da, um dich zu testen. Khajag. Alastor. Was dachtest du, wer sie erschaffen hat?', 'Si sind do gsi, zum dich teste. Khajag. Alastor. Wa hesch denn denkt, wer si erschaffe het?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotYou', 'dl_npc_velius', 'Well, certainly not someone as weak as you.', 'Nun, sicher keiner, der so schwach ist wie du.', 'Naja, sicher kene, wo so nüt druf het we du.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YouDid', 'dl_npc_velius', 'Okay, now, when I think about it...', 'Okay, jetzt, wo ich so drüber nachdenke...', 'Jo ez, woni so drüber nodenk...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_NotYou', 'dl_npc_velius', 'Oh. You''ll see...', 'Oh. Warte mal schön ab...', 'Oh. Wart mal schön ab...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_YouDid', 'dl_npc_velius', 'They were great creatures, weren''t they. Holding back weaklings and letting only the best mages pass...', 'Grossartige Kreaturen, nicht wahr. Sie haben die Schwächlinge aufgehalten und Magier wie dich durchgelassen...', 'Grossartigi Kreature, gell. Si hend Schwächling ufghalte und Magier we dich dureglo...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Illusion', 'dl_npc_velius', 'Magic is a spectrum, like colours. The magic type I call Illusion was discovered by myself recently.', 'Magie ist ein Spektrum, wie Farben. Die Magieart, die ich Illusion getauft habe, wurde erst kürzlich von mir selbst entdeckt.', 'Magie isch es Spektrum, we Farbe. D''Magieart, woni Illusion tauft ha, isch erst chürzlich vo mir entdeckt worde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Illusion2', 'dl_npc_velius', 'A very powerful type of magic. The power of controlling and deceiving people shouldn''t be misused.', 'Eine sehr mächtige Art von Magie. Die Macht, Leute zu kontrollieren und zu täuschen, sollte nicht in falsche Hände fallen.', 'E sehr mächtigi Art vo Magie. D''Macht, Lüüt z''kontrolliere und z''tüsche, söt nid i falschi Händ falle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Illusion3', 'dl_npc_velius', 'That''s why I kept it a secret.', 'Deshalb habe ich dieses Geheimnis gehütet.', 'Drum hani das Gheimnis ghüetet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Fight', 'dl_npc_velius', 'As you wish. Your sacrifice won''t be in vain.', 'Wie du willst. Dein Opfer wird nicht vergebens sein.', 'Wed wetsch. Dis Opfer wird nid vergäbens sii.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Velius_Beta', 'dl_npc_velius', '(To be continued...)', '(Fortsetzung folgt...)', '(Fortsetzig folgt...)');
