INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_NotTalked', 'dl_npc_vincent2', 'Hey you, psst! You look like you could use some gold. Interested in a ... special job?', 'Hey du, psst! Du siehst aus, als könntest du etwas Gold gut gebrauchen. Interessiert an einem ... speziellen Job?', 'Hey du, psst! Du gsesch us, als chöntsch chli Gold guet bruuche. Interessiert amne ... spezielle Job?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_DoYourDuty', 'dl_npc_vincent2', 'You remember that little deal we had in the tavern? You''d better do your job soon.', 'Erinnerst du dich an diese kleine Abmachung, die wir in der Taverne hatten? Du machst jetzt besser deine Arbeit.', 'Erinnerisch dich a die chliini Abmachig womer ide Taverne gmacht hend? Gang ez besser und mach dini Arbet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_TaskFulfilled', 'dl_npc_vincent2', 'Oh, it''s you. I know that you have carried out my task. Very good. You might be of use.', 'Oh, du bist es. Deine Aufgabe hast du offensichtlich erfüllt. Sehr gut. Du könntest noch von Nutzen sein.', 'Oh, du bisches. Du hesch dini Ufgab offesichtlich erledigt. Sehr guet. Du chöntsch na bruuchbar si.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_FurtherJob', 'dl_npc_vincent2', 'Interested in another job?', 'Interesse an einem weiteren Job?', 'Interesse amne nöchste Job?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_YouAgainGood', 'dl_npc_vincent2', 'Hey. Good to see you here. My plan has worked out. Interested in another job?', 'Hey. Schön, dich hier zu sehen. Mein Plan ist aufgegangen. Interesse an einem weiteren Job?', 'Hey. Guet bisch du ide Stadt. Min Plan isch ufgange. Interesse amne wiitere Job?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_Disgruntled', 'dl_npc_vincent2', 'Oh... you again. Y''know, I''d offered you an awesome job, if you hadn''t been so agressive. If you want another chance, it will cost you something.', 'Oh... du schon wieder. Weisst du, ich hätte dir einen unschlagbar guten Job angeboten, wenn du nicht so aggressiv gewesen wärst... Wenn du eine zweite Chance willst, kostet es dich was.', 'Oh... du scho wieder. Weisch, ich het der en unschlagbar guete Job abote, wenn du nid so aggressiv gsi wärsch... Wennd e zweiti Chance wetsch, chosteds für dich chli me.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_NotCooperated', 'dl_npc_vincent2', 'Oh look. The boy who wasn''t interested. Maybe a second chance for a job, huh?', 'Na sieh mal an. Der Junge, der nicht interessiert war. Wie wär''s mit einer zweiten Chance für einen Job?', 'Ja lueg mal da. De Bueb wo nid interessiert gsi isch. Wie wärs mitere zweite Chance für en Job?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatDeal', 'dl_npc_vincent2', 'Tell me more about that job.', 'Erzähl mir mehr über diesen Job.', 'Verzell mer me vo dem Job.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GimmeKey', 'dl_npc_vincent2', 'Give me that key, I''ll do it.', 'Gib mir diesen Schlüssel, ich machs.', 'Gib mer de Schlüssel, ich machs.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotInterested', 'dl_npc_vincent2', 'I''m not interested.', 'Nicht interessiert.', 'Interessiert mi nid.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_ExplainDeal', 'dl_npc_vincent2', '(Smiles and pulls out a golden key) I got this small key here. It belongs to a treasure chest. I could give it to you, if you want...', '(Lächelt und holt einen goldenen Schlüssel hervor) Ich habe diesen kleinen Schlössel hier. Er gehört zu einer Schatzkiste. Ich könnte ihn dir geben...', '(Lächlet und holt en goldige Schlüssel füre) Ich ha da sonen chliine Schlüssel. Er ghört zunere Schatzchiste. Ich chönten dir gä...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_WhereCatch', 'dl_npc_vincent2', 'And where is the catch?', 'Und wo ist der Haken an der Sache?', 'Und wo isch de Hooge ade Sach?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_TheCatch', 'dl_npc_vincent2', 'Hehe, I knew you would ask. Well, the treasure chest doesn''t belong to me, so to speak - it belongs to the Elder Rhendal.', 'Hehe, ich wusste, dass du das fragen würdest. Nungut. Die Schatzkiste gehört nicht mir, wenn man es genau nimmt - sie gehört dem Dorfältesten Rhendal.', 'Hehe, ich ha gwüsst, dass du das froge wirsch. Ja denn. Also die Schatzchiste ghört nid mir, wenmes gnau nimmt - si ghört em Dorfälteste Rhendal.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_KeyCost150', 'dl_npc_vincent2', 'I had to steal the key from him and it wasn''t easy, because he was fully awake. It wouldn''t have been a problem if you did what I told you before... Now, to compensate for my troubles, it will cost you 150 golden coins.', 'Ich musste ihm den Schlüssel entwenden, während er wach war - hättest du meinen ersten Auftrag ausgeführt, wäre das kein Problem gewesen. Jetzt kostet er dich 150 Goldstücke, als Ausgleich für meine Mühen.', 'Ich ha ihm de Schlüssel abluchse möse, während er wach gsi isch. Hetsch min erste Uftrag erfüllt, wer das ke Problem gsi. Aber jetzt kosted dich das 150 Goldstück, zum mini Müe kompensiere.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_Treasure', 'dl_npc_vincent2', 'But I only want you to get a special blue stone from the Elder''s chest for me. You can keep the rest for yourself.', 'Aber ich will nur, dass du mir einen speziellen blauen Stein aus seiner Truhe holst. Den Rest darfst du behalten.', 'Aber ich wett nur, dass du mir en spezielle blaue Stei us de True holsch. De Rest chasch selber phalte.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_KeyCost100', 'dl_npc_vincent2', 'I had to steal the key from him and it wasn''t easy, because he was fully awake. It wouldn''t have been a problem if you did what I told you before... Now, to compensate for my troubles, it will cost you 100 golden coins.', 'Ich musste ihm den Schlüssel entwenden, während er wach war - hättest du meinen ersten Auftrag ausgeführt, wäre das kein Problem gewesen. Jetzt kostet er dich 100 Goldstücke, als Ausgleich für meine Mühen.', 'Ich ha ihm de Schlüssel abluchse möse, während er wach gsi isch. Hetsch min erste Uftrag erfüllt, wer das ke Problem gsi. Aber jetzt kosted dich das 100 Goldstück, zum mini Müe kompensiere.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_KeyCost50', 'dl_npc_vincent2', 'I had to steal the key from him. So, a small fee of 50 golden coins for the key would be a good compensation.', 'Ich musste ihm den Schlüssel stehlen. Deshalb muss ich eine kleine Entschädigung von 50 Goldstücken für den Schlüssel verlangen.', 'Ich ha ihm de Schlüssel möse abluchse. Drum wer e chliini Entschädigung vo 50 Goldstück für de Schlüssel agmesse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_KeyCost0', 'dl_npc_vincent2', 'You remember the stuff I mixed into the schnapps you gave him? It was a sleeping powder. Getting the key from him was a mere child''s play.', 'Erinnerst du dich an den Schnaps, den du ihm gegeben hast? Ich habe etwas Schlafpulver reingemixt... ihm den Schlüssel zu entwenden war das reinste Kinderspiel.', 'Erinnerisch dich an Schnaps, wod ihm gä hesch? Ich han es Schlofpulver dri ta. Ihm de Schlüssel abzluchse isch bubieifach gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_StartElderQuest', 'dl_npc_vincent2', 'A good choice. Here you go.', 'Eine gute Wahl. Hier hast du ihn.', 'E gueti Wahl. Da heschen.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_ChestIsObserved', 'dl_npc_vincent2', 'The chest is in a secret room in the Elder''s house. Oh, and it''s most likely guarded by observer spells. Just make sure that they won''t catch you.', 'Die Kiste ist in einem Geheimraum im Haus des Ältesten. Oh, und sie ist wahrscheinlich von Beobachter-Zauber beschützt. Pass also auf, dass die dich nicht erwischen.', 'D''Chischte isch imne Gheimruum vom Huus vom Älteste. Oh, und si wird wahrschinli vo Beobachter-Zauber bewacht. Pass also uf, dass die dich nid verwütsched.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_NotEnoughGold', 'dl_npc_vincent2', 'Come back when you have enough gold.', 'Komm wieder wenn du genug Gold hast.', 'Chum wider wend gnueg Gold hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_Pity', 'dl_npc_vincent2', 'What a pity.', 'Wie schade.', 'Sehr schad.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ObserverSpells', 'dl_npc_vincent2', 'What are "observer spells"?', 'Was sind "Beobachter-Zauber"?', 'Was sind "Beobachter-Zauber"?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FoundChest', 'dl_npc_vincent2', 'I got the gem from the Elder''s chest...', 'Ich habe den Edelstein aus der Truhe des Ältesten...', 'Ich han de Edelstei us de True vom Älteste...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CU', 'dl_npc_vincent2', 'See you later.', 'Bis später.', 'Bis spöter.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_ObserverSpells', 'dl_npc_vincent2', 'A nasty form of magic. People place it in their homes so that thieves won''t be able to steal something. But they can''t see everything... (grins). But if they see you stealing something, they will put you in jail. And this won''t be a nice experience, I can tell you that.', 'Eine hässliche Form der Magie. Die Leute stellen sie in ihre Häuser, damit niemand etwas entwenden kann. Aber diese Zauber sehen auch nicht alles... (grinst) Aber wenn sie dich erwischen, kommst du ins Gefängnis, und das ist nicht gerade angenehm, kann ich dir sagen.', 'E hässlichi Form vode Magie. D''Lüt dönd die i iri Hüser demit niemert öppis chluppe cha. Aber die Zauber gsehnd au nid alles (grinst). Aber wenn si dich verwütsched, denn steckeds dich id Chiste... und da isch nid grad ahgnehm, da chani der sege. ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_OpenedTheChest', 'dl_npc_vincent2', 'Hehe, I knew you''d succeed. But I don''t want that stone anyway, you can keep it as your reward.', 'Hehe, ich wusste dass du es schaffen würdest. Aber ich will diesen Stein gar nicht, du kannst ihn behalten - als deine Belohnung.', 'Hehe, ich ha gwüsst dases schaffsch. Aber ich wett de Stei gar nid... Chaschen phalte - als dini Belohnig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_StealingFitsYou', 'dl_npc_vincent2', 'Taking belongings from other people doesn''t seem to be a big deal for you. I could teach you how to get to even more valuable things.', 'Es scheint dir ja nicht viel auszumachen, die Habseligkeiten anderer Leute zu nehmen. Ich könnte dir beibringen, wie du an noch wertvollere Dinge kommen kannst.', 'Es schiint dir ja nid viel uszmache, d''Habseligkeite vo andere Lüt zne. Ich chönt dir bibringe wied no a wertvolleri Sache cho chasch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TeachMe', 'dl_npc_vincent2', 'Teach me.', 'Bring es mir bei.', 'Brings mer bi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_TeachUnlock', 'dl_npc_vincent2', 'Of course. Just read this scroll and I can offer your more work in this... domain. (Grins) ', 'Natürlich. Lies einfach diese Schriftrolle und ich kann dir einen weiteren Auftrag anbieten, falls du noch an Arbeit in diesem... Bereich interessiert bist. (Grinst)', 'Natürlich. Lies eifach die Schriftrolle und ich chan dir na en wiitere Job abüte, fallsd immer na a Arbet i dem... Bereich interessiert bisch. (Grinst)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_SecondQuest', 'dl_npc_vincent2', 'I''m ready for your new job.', 'Ich bin bereit für den neuen Auftrag.', 'Ich bi parat für de neu Job.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_SecondQuest', 'dl_npc_vincent2', 'Good. This time, the stakes are a little bit higher. We know that a certain mage Syrah knows how to brew a potion that can render you invisible.', 'Gut. Dieses Mal steht ein bisschen mehr auf dem Spiel. Wir wissen, dass eine gewisse Magierin Syrah weiss, wie man einen Trank braut, der einen unsichtbar macht.', 'Guet. Damol staht chli me ufem Spiel. Mir wüssed, dass e gwüssi Magierin Syrah weiss, weme en Trank zemebraut, wo eim unsichbar macht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_SecondQuest2', 'dl_npc_vincent2', 'A very useful ability, isn''t it. The only problem is, that she won''t tell us how it works. She leaves us no choice but to abstract the recipe from her.', 'Eine äusserst nützliche Fähligkeit, nicht wahr. Das einzige Problem ist, dass sie uns dieses Wissen vorenthalten will. Sie lässt uns also keine andere Wahl, wir müssen dieses Rezept ohne ihre Einwilligung nehmen. ', 'E zimli nützlichi Fähigkeit, gell. Es einzig Problem isch dass si üs umsverrecke nid sege wett wes gaht. Also blibt üs ke anderi Wahl als das Rezept halt eifach so z''ne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_SecondQuest3', 'dl_npc_vincent2', 'There must be a way to get into her basement - by taking a shortcut through the sewers. Find this way and get that recipe for us. If you manage to do that, you''ll maybe get to know what "us" really means.', 'Es muss einen Weg geben, in ihren Keller zu kommen - wenn man eine Abkürzung über die Kanalisation nimmt. Finde diesen Weg und bring uns dieses Rezept. Wenn dir das gelingt, werde ich dir vielleicht erzählen, wer denn "uns" überhaupt ist.', 'Es mun en Weg ge, in ihre Chäller z''cho, wenme en Abchürzig überd Kanalisation nimmt. Find de Weg und bring üs das Rezept. Wenn du das schaffsch, den chani der vilicht verzelle, wer "üs" überhaupt isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent2_Disappointed', 'dl_npc_vincent2', 'Hm. I trusted you and you failed me.', 'Hm. Ich habe dir wohl umsonst vertraut.', 'Hm. Ich ha der glaub umesuscht vertraut.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IGotRecipe', 'dl_npc_vincent2', 'I got the recipe for you.', 'Ich habe das Rezept für dich.', 'Ich hans Rezept für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_SecondQuestDone', 'dl_npc_vincent2', 'Very well. I knew you have talent. Now, if you''d like to learn more of this useful magic, take this key and find the door it opens.', 'Sehr gut. Ich wusste, dass du Talent hast. Falls du noch mehr dieser nützlichen Zauber lernen willst, nimm diesen Schlüssel und finde die Tür, die er öffnet.', 'Sehr guet. Ich ha gwüsst, dass du Talent hesch. Falls du na me vo dere nützliche Magie lerne wetsch, nimm de Schlüssel und find d''Türe, woner ufmacht.');
