INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_Welcome', 'dl_npc_vincent3', 'So you''ve finally found us. Welcome to the the hideout of the Shadow Stalkers.', 'Du hast also den Weg zu uns gefunden. Willkommen im Versteck der Schattenläufer.', 'Du hesch also de Weg zu üs gfunde. Willkomme im Versteck vode Schatteläufer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CantJoin', 'dl_npc_vincent3', 'You''ve already made your decision. What a waste...', 'Du hast deine Entscheidung bereits getroffen. Was für eine Verschwendung...', 'Du hesch dini Entscheidig scho troffe. Was füre Verschwendig...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_vincent3', 'Who are the "Shadow Stalkers"?', 'Wer sind die "Schattenläufer"?', 'Wer sind d''"Schatteläufer"?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyHiding', 'dl_npc_vincent3', 'Why are you hiding?', 'Wieso versteckt ihr euch?', 'Weso verstecked ihr eu?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_InformClerics', 'dl_npc_vincent3', 'What if I just inform the clerics about your little hideout?', 'Was wenn ich den Klerikern einfach von eurem kleinen Versteck erzähle?', 'Was wenn ich de Kleriker eifach vo eurem chline Versteck verzell?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoInGuild', 'dl_npc_vincent3', 'Who belongs to this guild?', 'Wer gehört alles zur Gilde?', 'Wer ghört alles zude Gilde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_OneGuild', 'dl_npc_vincent3', 'What if I want to join another guild?', 'Was ist, wenn ich einer anderen Gilde beitreten will?', 'Was isch, weni enere andere Gilde biitrette wet?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatIfJoin', 'dl_npc_vincent3', 'Why should I join you?', 'Wieso sollte ich euch beitreten?', 'Weso söti eu biitrette?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IWantJoin', 'dl_npc_vincent3', 'I want to join you.', 'Ich möchte euch beitreten.', 'Ich wett eu biitrette.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhoAreYou', 'dl_npc_vincent3', 'We are a guild that focuses on twilight magic and the benefits it brings us.', 'Wir sind eine Gilde, die sich mit Dämmerungsmagie beschäftigt und mit den Vorteilen, die sie uns bringt.', 'Mir sind e Gilde wo sich mit Dämmerigsmagie beschäftigt und de Vorteil, wos üs bringt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhoAreYou2', 'dl_npc_vincent3', 'You seem to have an affinity to this type of magic, as you''ve mastered the art of summoning a picklock incredibly fast.', 'Du scheinst eine Affinität für diese Magie zu besitzen, da du unglaublich schnell gelernt hast, wie man einen Dietrich beschwört.', 'Du hesch ebe glaub e Affinität für die Magie, welld unglaublich schnell glernt hesch, weme en Dietrich beschwört.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhoAreYou3', 'dl_npc_vincent3', 'Joining us could uncover your hidden talents...', 'Uns beizutreten könnte deine verborgenen Talente zum Vorschein bringen...', 'Üs biiztrete chönt dini verborgene Talent fürebringe...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhyHiding', 'dl_npc_vincent3', 'You see what''s going on in this city, right? Mages who don''t study "divine" magic are frowned upon.', 'Dir ist schon klar was in dieser Stadt passiert? Magier die keine "heilige" Magie studieren, sind nicht gerne gesehen.', 'Dir isch scho klar wa i dere Stadt passiert? Magier wo kei "heiligi" Magie studiered sind nid so gern gse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhyHiding2', 'dl_npc_vincent3', 'Twilight and elemental magic are tolerated though in contrast to necromancy - but still, the paladins don''t like what we''re doing with our magic.', 'Dämmerungs- und Elementarmagie werden jedoch toleriert, im Gegensatz zu Nekromantie. Trotzdem mögen die Paladine nicht, was wir mit unserer Magie anstellen.', 'Dämmerigs- und Elementarmagie werded aber toleriert, im Gegesatz zu Nekromantie. D''Paladin hend aber trotzdem nid gern wa mir mit üsere Magie so astelled.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_InformClerics', 'dl_npc_vincent3', 'By the time the clerics would arrive here, we''d be gone and you - well, your body - would be floating around in the sewers.', 'Bis die Kleriker hier unten wären, wären wir schon lange weg und du - beziehungsweise deine Leiche - würde in den Kanälen treiben.', 'Bis d''Kleriker da unne wäred, wäred mir scho lang über alli Berge und du - also dini Leiche - wür ufem Abwasser triibe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhoInGuild', 'dl_npc_vincent3', 'Do I look like I''m going to tell you that? Maybe if you''d join us.', 'Sehe ich so aus als würde ich dir das erzählen? Vielleicht, wenn du uns beitrittst.', 'Gse ich so us als würi dir das verzelle? Vilicht wend üs bitrittsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_OneGuild', 'dl_npc_vincent3', 'You''d be missing a great opportunity. But that''s your choice.', 'Du würdest etwas verpassen. Aber das ist deine Wahl.', 'Du würsch öppis verpasse. Aber das isch dini Wahl.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhatIfJoin', 'dl_npc_vincent3', 'You''ll get to learn twilight magic and besides that, you''re going to get rich.', 'Du wirst Dämmerungsmagie lernen und nebenbei noch reich werden.', 'Du wirsch Dämmerigsmagie lerne und usserdem na riich werde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_Join', 'dl_npc_vincent3', 'Good. You want to become a brother of the Shadow Stalkers, pledge your loyality to the guild and learn the ways of the shadows?', 'Gut. Willst du ein Bruder der Schattenläufer werden, schwörst der Gilde deine Treue und lernst die Wege der Schatten? ', 'Guet. Wetsch du en Brueder vode Schatteläufer werde, schwörsch dini Treui zude Gilde und lernsch d''Wäg vode Schatte?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IJoin', 'dl_npc_vincent3', 'Yes. [JOIN THE SHADOW STALKERS]', 'Ja. [DEN SCHATTENLÄUFERN BEITRETEN]', 'Jo. [DE SCHATTELÄUFER BIITRETE]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Rethink', 'dl_npc_vincent3', 'I need more time to think about this.', 'Ich muss das nochmal überdenken.', 'Ich mun do nomol drüber nadenke.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_DeclareThief', 'dl_npc_vincent3', 'You won''t regret this. Welcome in our guild, shadow stalker.', 'Das wirst du nicht bereuen. Willkommen in unserer Gilde, Schattenläufer.', 'Das wirsch nid bereue. Willkomme i üsere Gilde, Schatteläufer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TeachSpells', 'dl_npc_vincent3', 'Can you teach me some spells?', 'Kannst du mir Zauber beibringen?', 'Chasch du mir Zauber bibringe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TeachMore', 'dl_npc_vincent3', 'Can you teach me more spells?', 'Kannst du mir noch mehr Zauber beibringen?', 'Chasch mer na me Zauber bibringe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HowCanIHelp', 'dl_npc_vincent3', 'How can I support you?', 'Wie kann ich euch unterstützen?', 'Wie chani eu unterstütze?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HowElseHelp', 'dl_npc_vincent3', 'What else can I do to demonstrate my skills?', 'Was kann ich sonst noch tun um meine Fähigkeiten unter Beweis zu stellen?', 'Was chani susch na mache zum mini Fähigkeite unter Bewiis stelle?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CathedralFinished', 'dl_npc_vincent3', 'I got the candleholders.', 'Ich habe die Kerzenständer.', 'Ich han d''Chärzeständer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CompleteLloydsPlan', 'dl_npc_vincent3', 'I''ve talked to Lloyd...', 'Ich habe mit Lloyd gesprochen...', 'Ich ha mitem Lloyd gredt...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_FirstSpell', 'dl_npc_vincent3', 'Yes, I will teach you. Take this scroll and learn how to summon a deadly trap.', 'Ja, kann ich. Nimm diese Schriftrolle und lerne wie man eine tödliche Falle beschwört.', 'Ja, chani. Nimm die Schriftrolle und lern weme e tödlichi Falle beschwört.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_NoSpellReputation', 'dl_npc_vincent3', 'Maybe later. First, you need to show us that you can handle this kind of magic.', 'Vielleicht später. Erst musst du uns zeigen, dass du auch mit dieser Magie umgehen kannst.', 'Vilicht spöter. Zersch muesch üs mal zeige das au öppis chasch demit afange.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_LloydsPlan', 'dl_npc_vincent3', 'You know the leader of the clerics, Lloyd, don''t you? He was acting strangely lately, it seems like he''s planning something.', 'Du kennst den Anführer der Kleriker, Lloyd, nicht wahr? Er hat sich in letzter Zeit seltsam verhalten, es scheint als plane er etwas.', 'Du kennsch de Aführer vode Kleriker, de Lloyd, gell? Er het sich i letzter Ziit komisch beno, es schiint als würer öppis plane.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_LloydsPlan2', 'dl_npc_vincent3', 'We need someone who can talk to him and maybe even work for him. If you find out what he''s up to, come back and report.', 'Wir brauchen jemanden, der mit ihm sprechen kann und vielleicht auch für ihn arbeitet. Falls du herausfindest, was er vor hat, komm wieder und berichte uns davon.', 'Mir bruched öpper, wo mit ihm rede chan und vilicht sogar für ihn schafft. Fallsd usefindsch, was er vor het, chum wieder und verzell üs devo.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CathedralThief', 'dl_npc_vincent3', 'The clerics have made a big mistake when they tried to exorcise a dangerous grimoire about necromancy.', 'Die Kleriker haben einen grossen Fehler gemacht, als sie ein gefährliches Buch über Nekromantie bannen wollten.', 'D''Kleriker hend en grosse Fähler gmacht wo si es gföhrlichs Buech über Nekromantie hend wele banne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CathedralThief2', 'dl_npc_vincent3', 'The book is running amok in their cathedral. A great opportunity for us though.', 'Das Buch läuft in ihrer Kathedrale Amok. Das ist allerdings eine gute Chance für uns.', 'Es Buech lauft i ihrere Kathedrale Amok. Das isch allerdings e gueti Chance für üs.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CathedralThief3', 'dl_npc_vincent3', 'While they''re distracted, it should be mere child''s play to steal the precious candleholders from their cathedral.', 'Während sie abgelenkt sind, sollte es ein Kinderspiel sein, die wertvollen Kerzenständer aus ihrer Kathedrale zu stehlen.', 'Während si abglenkt sind, söts es Chinderspiel si, di wertvolle Chärzeständer us ihrere Kathedrale z''chluppe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CathedralThief4', 'dl_npc_vincent3', 'Bring us those golden candleholders and I''ll teach you more about our magic.', 'Bring uns diese goldenen Kerzenständer und ich werde dich mehr über unsere Magie lehren.', 'Bring üs die goldige Cherzeständer und ich wird der meh vo üsere Magie biibringe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CathedralFinished', 'dl_npc_vincent3', 'Well done. Taking them when everyone else was distracted was a well-thought-out idea, wasn''t it.', 'Gut gemacht. Sie dann zu nehmen, während alle anderen abgelenkt waren, war eine gut durchdachte Idee, nicht wahr.', 'Guet gmacht. Si denn z''neh wenn all andere abglenkt sind isch scho e saumässig gueti Idee gsi, gell.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CathedralFinished2', 'dl_npc_vincent3', 'You''ve shown me that you are able to use our magic. Take this scroll as a reward and learn how to be one with the shadows.', 'Du hast mir gezeigt, dass du fähig bist, unsere Magie zu benutzen. Nimm diese Spruchrolle als Belohnung und lerne, wie man eins mit den Schatten wird.', 'Du hesch mer zeigt, dassd fähig bisch, üsi Magie z''bruuche. Nimm die Schriftrolle als Belohnig und lern, weme eis mit de Schatte wird.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CompleteLloydsPlan2', 'dl_npc_vincent3', '... Some "V." is abducting mages. Lloyd is trying to find out who that is.', '... Ein gewisser "V." entführt Magier. Lloyd versucht herauszufinden, wer das ist.', '... En gwüsse "V." entführt Magier. De Lloyd probiert usezfinde, wer das isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CompleteLloydsPlan', 'dl_npc_vincent3', 'Some "V.", eh. (Grins) Well, it''s not me, I can assure you that. Thank you for the report.', 'Ein "V.", he. (Grinst) Nun, ich bin es nicht, das kann ich dir sagen. Danke für den Bericht.', 'En "V.", he. (Grinst) Ich bis aber nid, das chani der sege. Danke für de Bricht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CompleteLloydsPlan2', 'dl_npc_vincent3', 'We''ll be a lot more vigilantly now. I''m only worried about Koray, Nuray''s brother.', 'Wir werden nun um einiges wachsamer sein. Ich mache mir nur Sorgen um Koray, Nuray''s Bruder.', 'Mir werded um einiges wachsamer sii. Ich mach mer bloss chli Sorge um de Koray, de Nuray ihre Brüeder.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CompleteLloydsPlan3', 'dl_npc_vincent3', 'He went to the marshlands recently. If you see him, tell Nuray.', 'Er ist kürzlich ins Sumpfland gegangen. Berichte Nuray davon, falls du ihn sehen solltest.', 'Er isch letztins is Sumpfland gange. Verzell doch de Nuray devo, fallsen gse sötsch.');
