INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_Welcome', 'dl_npc_vincent3', 'So you''ve finally found us. Welcome to the the hideout of the Shadow Stalkers.', 'Du hast also den Weg zu uns gefunden. Willkommen im Versteck der Schattenläufer.', 'Du häsch also dä Wäg zu üs gfundä. Willkomme im Versteck vodä Schatteläufer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CantJoin', 'dl_npc_vincent3', 'You''ve already made your decision. What a waste...', 'Du hast deine Entscheidung bereits getroffen. Was für eine Verschwendung...', 'Du häsch dini Entscheidig scho troffä. Was füre Verschwändig...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_vincent3', 'Who are the "Shadow Stalkers"?', 'Wer sind die "Schattenläufer"?', 'Wer sind d''"Schatteläufer"?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyHiding', 'dl_npc_vincent3', 'Why are you hiding?', 'Wieso versteckt ihr euch?', 'Weso verstecked ihr eu?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_InformClerics', 'dl_npc_vincent3', 'What if I just inform the clerics about your little hideout?', 'Was wenn ich den Klerikern einfach von eurem kleinen Versteck erzähle?', 'Was wenn ich de Kleriker eifach vo eurem chline Versteck verzell?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoInGuild', 'dl_npc_vincent3', 'Who belongs to this guild?', 'Wer gehört alles zur Gilde?', 'Wer ghört alles zude Gilde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_OneGuild', 'dl_npc_vincent3', 'What if I want to join another guild?', 'Was ist, wenn ich einer anderen Gilde beitreten will?', 'Was isch, weni enere andere Gilde biitrette wet?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatIfJoin', 'dl_npc_vincent3', 'Why should I join you?', 'Wieso sollte ich euch beitreten?', 'Weso söti eu biitrette?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IWantJoin', 'dl_npc_vincent3', 'I want to join you.', 'Ich möchte euch beitreten.', 'Ich wett eu biitrette.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhoAreYou', 'dl_npc_vincent3', 'We are a guild that focuses on twilight magic and the benefits it brings us.', 'Wir sind eine Gilde, die sich mit Dämmerungsmagie beschäftigt und mit den Vorteilen, die sie uns bringt.', 'Mir sind e Gildä wo sich mit Dämmerigsmagie beschäftigt und dä Vorteil, wos üs bringt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhoAreYou2', 'dl_npc_vincent3', 'You seem to have an affinity to this type of magic, as you''ve mastered the art of summoning a picklock incredibly fast.', 'Du scheinst eine Affinität für diese Magie zu besitzen, da du unglaublich schnell gelernt hast, wie man einen Dietrich beschwört.', 'Du häsch ebe glaub e Affinität für die Magie, welld unglaublich schnell glernt häsch, wieme en Dietrich beschwört.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhoAreYou3', 'dl_npc_vincent3', 'Joining us could uncover your hidden talents...', 'Uns beizutreten könnte deine verborgenen Talente zum Vorschein bringen...', 'Üs biizträtä chönt dini verborgänä Talent fürebringe...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhyHiding', 'dl_npc_vincent3', 'You see what''s going on in this city, right? Mages who don''t study "divine" magic are frowned upon.', 'Dir ist schon klar, was in dieser Stadt passiert? Magier die keine "heilige" Magie studieren, sind nicht gerne gesehen.', 'Isch dir eigentlich klar, was i däre Stadt passiert? Magier wo kei "heiligi" Magie studiered sind nöd so gärn gse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhyHiding2', 'dl_npc_vincent3', 'Twilight and elemental magic are tolerated though in contrast to necromancy - but still, the paladins don''t like what we''re doing with our magic.', 'Dämmerungs- und Elementarmagie werden jedoch toleriert, im Gegensatz zu Nekromantie. Trotzdem mögen die Paladine nicht, was wir mit unserer Magie anstellen.', 'Dämmerigs- und Elementarmagie werdäd abo toleriert, im Gegesatz zu Nekromantie. D''Paladin hend abo trotzdäm nöd gern wa mir mit üsere Magie so astelläd.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_InformClerics', 'dl_npc_vincent3', 'By the time the clerics would arrive here, we''d be gone and you - well, your body - would be floating around in the sewers.', 'Bis die Kleriker hier unten wären, wären wir schon lange weg und du - beziehungsweise deine Leiche - würde in den Kanälen treiben.', 'Bis d''Kleriker do unnä sind, sind mir scho lang über alli Bärgä und du - also dini Leichä - wür ufem Abwasser triibe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhoInGuild', 'dl_npc_vincent3', 'Do I look like I''m going to tell you that? Maybe if you''d join us.', 'Sehe ich so aus als würde ich dir das erzählen? Vielleicht, wenn du uns beitrittst.', 'Gse ich so us als wür i dir das verzellä? Vilicht wenn üs biträtte tuäsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_OneGuild', 'dl_npc_vincent3', 'You''d be missing a great opportunity. But that''s your choice.', 'Du würdest etwas verpassen. Aber das ist deine Wahl.', 'Du würsch öppis verpassä. Abo das isch dini Wahl.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_WhatIfJoin', 'dl_npc_vincent3', 'You''ll get to learn twilight magic and besides that, you''re going to get rich.', 'Du wirst Dämmerungsmagie lernen und nebenbei noch reich werden.', 'Du wirsch Dämmerigsmagie lerne und usserdäm no huerä riich werdä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_Join', 'dl_npc_vincent3', 'Good. You want to become a brother of the Shadow Stalkers, pledge your loyality to the guild and learn the ways of the shadows?', 'Gut. Willst du ein Bruder der Schattenläufer werden, schwörst der Gilde deine Treue und lernst die Wege der Schatten? ', 'Guet. Wötsch du en Brüädo vodä Schatteläufer werdä, schwörsch dini Treui zudä Gildä und lernsch d''Wäg vodä Schatte?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IJoin', 'dl_npc_vincent3', 'Yes. [JOIN THE SHADOW STALKERS]', 'Ja. [DEN SCHATTENLÄUFERN BEITRETEN]', 'Jo. [DE SCHATTELÄUFER BIITRETE]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Rethink', 'dl_npc_vincent3', 'I need more time to think about this.', 'Ich muss das nochmal überdenken.', 'Ich mun do nomol drüber nadenke.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_DeclareThief', 'dl_npc_vincent3', 'You won''t regret this. Welcome in our guild, shadow stalker.', 'Das wirst du nicht bereuen. Willkommen in unserer Gilde, Schattenläufer.', 'Das wirsch du nöd bereue. Willkomme i üsere Gildä, Schatteläufer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TeachSpells', 'dl_npc_vincent3', 'Can you teach me some spells?', 'Kannst du mir Zauber beibringen?', 'Chasch du mir Zauber bibringe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TeachMore', 'dl_npc_vincent3', 'Can you teach me more spells?', 'Kannst du mir noch mehr Zauber beibringen?', 'Chasch mer na me Zauber bibringe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HowCanIHelp', 'dl_npc_vincent3', 'How can I support you?', 'Wie kann ich euch unterstützen?', 'Wie chani eu unterstütze?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HowElseHelp', 'dl_npc_vincent3', 'What else can I do to demonstrate my skills?', 'Was kann ich sonst noch tun um meine Fähigkeiten unter Beweis zu stellen?', 'Was chani susch na mache zum mini Fähigkeite unter Bewiis stelle?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CathedralFinished', 'dl_npc_vincent3', 'I got the candleholders.', 'Ich habe die Kerzenständer.', 'Ich han d''Chärzeständer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CompleteLloydsPlan', 'dl_npc_vincent3', 'I''ve talked to Lloyd...', 'Ich habe mit Lloyd gesprochen...', 'Ich ha mitem Lloyd gredt...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YashaStart', 'dl_npc_vincent3', 'Do you have another job for me?', 'Hast du noch einen Auftrag für mich?', 'Hesch na en Uftrag für mich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YashaComplete', 'dl_npc_vincent3', 'I found the heart of thunder.', 'Ich habe das Donnerherz.', 'Ich hans Donnerherz.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_VeliusFound', 'dl_npc_vincent3', 'I found this letter from Koray... (show letter)', 'Ich habe diesen Brief von Koray gefunden... (Brief zeigen)', 'Ich ha de Brief vom Koray gfunde... (Brief zeige)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_FirstSpell', 'dl_npc_vincent3', 'Yes, I will teach you. Take this scroll and learn how to summon a deadly trap.', 'Ja, kann ich. Nimm diese Schriftrolle und lerne wie man eine tödliche Falle beschwört.', 'Jo, chani. Nimm die Schriftrolle und lern wieme e tödlichi Falle beschwöre tuet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_NoSpellReputation', 'dl_npc_vincent3', 'Maybe later. First, you need to show us that you can handle this kind of magic.', 'Vielleicht später. Erst musst du uns zeigen, dass du auch mit dieser Magie umgehen kannst.', 'Vilicht spöto. Zersch muesch üs mol zeige, dass au öppis chasch dämit afangä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_NoMoreSpells', 'dl_npc_vincent3', 'No, I''ve taught you everything I know.', 'Nein, ich habe dir alles beigebracht, was ich weiss.', 'Nei, ich ha dir alles biibrocht, wani weiss.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_NoMoreSpells2', 'dl_npc_vincent3', 'But that doesn''t mean that there are no more twilight spells out there. They just need to be discovered.', 'Aber das heisst nicht, dass es da draussen keine weiteren Dämmerungs-Zauber mehr gibt. Man muss sie nur finden.', 'Abo das will nöd heisse, dases döt usse ke Dämmerigs-Zaubo me git. Me muä si nur findä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_LloydsPlan', 'dl_npc_vincent3', 'You know the leader of the clerics, Lloyd, don''t you? He was acting strangely lately, it seems like he''s planning something.', 'Du kennst den Anführer der Kleriker, Lloyd, nicht wahr? Er hat sich in letzter Zeit seltsam verhalten, es scheint als plane er etwas.', 'Du kennsch dä Afühäro vodä Kleriker, dä Lloyd, gäll? Er hät sich i letschter Ziit komisch beno, es schiint als würor öppis plane.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_LloydsPlan2', 'dl_npc_vincent3', 'We need someone who can talk to him and maybe even work for him. If you find out what he''s up to, come back and report.', 'Wir brauchen jemanden, der mit ihm sprechen kann und vielleicht auch für ihn arbeitet. Falls du herausfindest, was er vor hat, komm wieder und berichte uns davon.', 'Mir bruched öppor, wo mit ihm redä chan und vilicht sogar für ihn schaffä tuet. Fallsd usefindsch, was er vor het, chum wiedär und verzell üs dävo.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CathedralThief', 'dl_npc_vincent3', 'The clerics have made a big mistake when they tried to exorcise a dangerous grimoire about necromancy.', 'Die Kleriker haben einen grossen Fehler gemacht, als sie ein gefährliches Buch über Nekromantie bannen wollten.', 'D''Kleriker händ en grosse Fähler gmacht wo si es gföhrlichs Buech über Nekromantie hend wöle bannä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CathedralThief2', 'dl_npc_vincent3', 'The book is running amok in their cathedral. A great opportunity for us though.', 'Das Buch läuft in ihrer Kathedrale Amok. Das ist allerdings eine gute Chance für uns.', 'Es Buech lauft i ihrerä Kathedrale Amok. Das isch allerdings e gueti Chance für üs.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CathedralThief3', 'dl_npc_vincent3', 'While they''re distracted, it should be mere child''s play to steal the precious candleholders from their cathedral.', 'Während sie abgelenkt sind, sollte es ein Kinderspiel sein, die wertvollen Kerzenständer aus ihrer Kathedrale zu stehlen.', 'Während si abglenkt sind, söts es Chindärspiel si, di wertvolle Chärzeständäo us ihrerä Kathedrale z''chluppä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CathedralThief4', 'dl_npc_vincent3', 'Bring us those golden candleholders and I''ll teach you more about our magic.', 'Bring uns diese goldenen Kerzenständer und ich werde dich mehr über unsere Magie lehren.', 'Bring üs die goldige Cherzeständär und ich wird do meh vo üserä Magie biibringe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CathedralFinished', 'dl_npc_vincent3', 'Well done. Taking them when everyone else was distracted was a well-thought-out idea, wasn''t it.', 'Gut gemacht. Sie dann zu nehmen, während alle anderen abgelenkt waren, war eine gut durchdachte Idee, nicht wahr.', 'Guet gmacht. Si dänn z''neh wenn alli andäre abglenkt sind isch scho e saumässig gueti Idee gsi, gäll.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CathedralFinished2', 'dl_npc_vincent3', 'You''ve shown me that you are able to use our magic. Take this scroll as a reward and learn how to be one with the shadows.', 'Du hast mir gezeigt, dass du fähig bist, unsere Magie zu benutzen. Nimm diese Spruchrolle als Belohnung und lerne, wie man eins mit den Schatten wird.', 'Du häsch mo zeigt, dassd fähig bisch, üsi Magie z''bruuchä. Nimm die Schriftrolle als Belohnig und lärn, wieme eis mit dä Schattä wird.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_CompleteLloydsPlan', 'dl_npc_vincent3', '... Some "V." is abducting mages. Lloyd is trying to find out who that is.', '... Ein gewisser "V." entführt Magier. Lloyd versucht herauszufinden, wer das ist.', '... En gwüsse "V." entführt Magier. De Lloyd probiert usezfinde, wer das isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CompleteLloydsPlan', 'dl_npc_vincent3', 'Some "V.", eh. (Grins) Well, it''s not me, I can assure you that. Thank you for the report.', 'Ein "V.", he. (Grinst) Nun, ich bin es nicht, das kann ich dir sagen. Danke für den Bericht.', 'En "V.", hä. (Grinst) Ich bis abo nöd, das chani dir sege. Danke für dä Bricht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CompleteLloydsPlan2', 'dl_npc_vincent3', 'We''ll be a lot more vigilantly now. I''m only worried about Koray, Nuray''s brother.', 'Wir werden nun um einiges wachsamer sein. Ich mache mir nur Sorgen um Koray, Nuray''s Bruder.', 'Mir werdäd um einiges wachsamer sii. Ich mach mo bloss chli Sorgä um dä Koray, dä Nuray ihre Brüedor.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_CompleteLloydsPlan3', 'dl_npc_vincent3', 'He went to the marshlands recently. If you see him, tell Nuray.', 'Er ist kürzlich ins Sumpfland gegangen. Berichte Nuray davon, falls du ihn sehen solltest.', 'Er isch letschti is Sumpfland gangä. Verzell doch dä Nuray dävo, fallsen gse sötsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_YashaStart', 'dl_npc_vincent3', 'Yes, actually I do have one for you. A dangerous one, indeed...', 'Ja, ich habe tatsächlich einen für dich. Allerdings ein ziemlich gefährlicher...', 'Jo, ich han würkli eine für dich. Allerdings en zimli gföhrlichä...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_YashaStart2', 'dl_npc_vincent3', 'Listen. There''s an abandoned temple in the Highlands of Admantris, and they say a demon is haunting it.', 'Hör zu. Es gibt einen verlassenen Tempel im Hochland von Admantris und man erzählt sich, ein Dämon treibe dort sein Unwesen.', 'Los. Es git en verlassenä Tämpel im Hochland vo Admantris und mä verzellt sich, en Dämon tribt döt sis Unwese.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_YashaStart3', 'dl_npc_vincent3', 'But we also know of a powerful magical stone, the "heart of thunder", which is hidden there.', 'Aber wir wissen auch von einem mächtigen magischen Stein, dem "Donnerherz", das dort versteckt sein soll.', 'Abo mir hend au vomäne mächtigä magischä Stei ghört, em "Donnerhärz", wo döt versteckt sii sött.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_YashaStart4', 'dl_npc_vincent3', 'It is probably heavily guarded by warden spells that can kill you on sight. But the loot is worth it!', 'Er ist wahrscheinlich schwer bewacht von Wächterzaubern, die dich sofort töten, wenn sie dich sehen. Doch die Beute lohnt sich!', 'Er isch wohrschinli ufs übelsti bewacht vo Wächterzaubor, wo dich sofort töted, wenn si dich gsehnd. Abo du, es lohnt sich!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_YashaStart5', 'dl_npc_vincent3', 'Using some invisibility, you should get through anyway. Good luck!', 'Mit ein wenig Unsichtbarkeit solltest du irgendwie durchkommen. Viel Glück!', 'Mit bitz Unsichtbarkeit sötsch scho irgendwie durecho. Viel Glück!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_YashaComplete', 'dl_npc_vincent3', '(Grins) Well, I hope you didn''t get in trouble getting this one.', '(Grinst) Ich hoffe bloss, du bist nicht in Schwierigkeiten geraten, als du das holen musstest.', '(Grinst) Ich hoff eifach, du häsch kei Problem übercho, das z''holä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_YashaComplete2', 'dl_npc_vincent3', 'I always knew that you''d make great thief - eh, twilight mage. Enjoy your newly gained powers.', 'Ich wusste schon immer, dass aus dir mal ein guter Dieb - eh, Dämmerungsmagier wird. Viel Spass mit deinen neuen Fähigkeiten.', 'Ich ha scho immer gwüsst, dass us dir mol en hammo Dieb - äh, Dämmerigsmagier wird. Vil Spass mit dine neue Fähigkeite.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_VeliusFound', 'dl_npc_vincent3', 'He was abducted by some mage named Velius alongside with some other mages. I have to go to the crypt in Gandria and find out more.', 'Er wurde von einem Magier namens Velius zusammen mit anderen Magiern entführt. Ich muss in die Krypta von Gandria und mehr herausfinden.', 'Er isch vomne Magier namens Velius zeme mit andere Magier entführt worde. Ich mu id Krypta vo Gandria und me usefinde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_VeliusFound', 'dl_npc_vincent3', 'Velius? I know a mage named Velius. We did business with him. ', 'Velius? Ich kenne einen Magier namens Velius. Wir haben schon Geschäfte mit ihm gemacht.', 'Velius? Ich kenn en Magior namens Velius. Mo hend scho Gschäftli mitem gmacht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_VeliusFound2', 'dl_npc_vincent3', 'He often wanted potions... or rumours. Still, he''s not the kind of guy who abducts people.', 'Er wollte meistens Tränke... oder Gerüchte. Aber er ist nicht so der Typ, um Leute zu entführen.', 'Er hät ämel Tränk wöle... odor Grüchtli. Abo er isch ez echt nöd so dä Typ wo Lüüt entfüerä tuet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Vincent_VeliusFound3', 'dl_npc_vincent3', 'But you should definitely follow the trace and go into that crypt. Good luck!', 'Aber du solltest definitiv dieser Spur folgen und in die Krypta gehen. Viel Glück!', 'Abo du sötsch definitiv däre Spur folge und id Krypta go. Viel Glück!');
