INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_NothingToSay', 'dl_npc_vincent', '...','...','...', '...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_Stop', 'dl_npc_vincent', 'Hey you, psst! You just got some Feudal Fire, I see? Are you planning to take that to the Elder Rhendal?','Hey du, psst! Ich sehe, du hast dir gerade ein Feudales Feuer geholt. Hast du vor, es zu dem Ältesten Rhendal zu bringen?','Hey du, psst! Ich gse, du hesch grad es Feudals Füür gholt. Hesch vor, es zum Älteste Rhendal z''bringe?', '¡Psss, oye, tú! Veo que tienes Fuego Feudal. ¿Se lo vas a llevar al anciano Rhendal?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_YesWhy', 'dl_npc_vincent', 'Yes, why do you ask?','Ja, wieso fragst du?','Ja, weso frögsch?', 'Sí, ¿por qué preguntas?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_NoneOfYourBusiness', 'dl_npc_vincent', 'I think that''s none of your business.','Ich glaube, das geht dich nichts an.','Ich glaub, das gaht dich nüt ah.', 'Eso no es asunto tuyo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_NoForMyself', 'dl_npc_vincent', 'No, I bought that for myself.','Nein, das habe ich für mich gekauft.','Nei, das hani für mich kauft.', 'No, lo compré para mí.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_NoneOfMyBusiness', 'dl_npc_vincent', 'D''you think so? Well, maybe it is... maybe it isn''t. Who are you to decide?','Wirklich? Naja, vielleicht geht mich das etwas an, vielleicht auch nicht, woher willst du das wissen.','Denksch? Naja, vilicht gahts mich öppis ah, vilicht au nid... chasch du ja nid wüsse.', '¿Eso crees? Bueno, puede que sí... puede que no. ¿Y quién eres para decidirlo?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhatDoYouWant', 'dl_npc_vincent', '(Sigh) What do you want, then?','(Seufz) Also gut, was willst du?','(Süüfz) Also guet, was willsch denn?', '(Suspira) ¿Qué quieres, entonces?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Agressive', 'dl_npc_vincent', 'I''m the one who''s going to kick your butt if you don''t leave me alone.','Ich polier dir die Fresse, wenn du mich nicht in Ruhe lässt.','Ich weiss, dasich dich verhau, wend mich nid in Rue lasch.', 'Soy yo el que te va a patear el trasero sino me dejas en paz.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_GoAway', 'dl_npc_vincent', 'I think I can decide for myself. Just go away.','Ich glaube, ich kann für mich selbst entscheiden. Geh weg.','Ich glaub, ich chan für mich selber entscheide. Verpiss dich.', 'Creo que puedo decidir por mí mismo, solo vete.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_Disgruntled', 'dl_npc_vincent', '(Smirks) You know you should''t talk to strangers like that. You might get yourself in trouble... ','(Grinst) Du weisst, dass du nicht so mit Fremden reden solltest? Du könntest Probleme kriegen...','(Grinst) Du weisch, dass du nid so mit Fremde rede sötsch? Du chöntsch Problem becho...', '(Sonrisa burlona) Sabes algo, no deberías hablarles así a extraños. Puedes meterte en problemas...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_Defected', 'dl_npc_vincent', 'What a pity. You don''t even want to hear what I''d like to tell you?','Schade. Und du willst dir ganz sicher nicht anhören, was ich zu sagen habe?','Schad. Und du wetsch ganz sicher nid ghöre, wa ich z''sege han?', 'Qué pena. ¿No quieres escuchar lo que te tengo que decir?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_YouAreLying', 'dl_npc_vincent', 'Interesting. Very interesting. But you don''t need to lie to me. I know what the Elder is up to.','Interessant. Sehr interessant. Aber du musst mir keine Lügen erzählen, ich weiss, was der Älteste vor hat.','Interessant. Sehr interessant. Aber du muesch mer also ke Märli verzelle, ich weiss, was de Ältesti vorhet.', 'Interesante, muy interesante, pero no tienes que mentirme. Yo sé lo que el anciano planea.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_RhendalIsDrinker', 'dl_npc_vincent', 'Hm, It''s not the first time he makes someone buy a bottle for him. He told you he''s going to use it for "alchemistic purposes", ain''t he. But that''s a lie, he just likes getting drunk from time to time.','Hm, es ist nicht das erste mal, dass er jemanden schickt, um ihm eine Flasche zu kaufen. Er hat dir erzählt, er braucht es für "alchemistische Zwecke", nicht wahr? Aber das ist eine Lüge, er betrinkt sich eifach gern von Zeit zu Zeit.','Hm, es isch nid es erst mol daser öpper schickt zum ihm e Fläsche z''kaufe. Er het dir verzellt, er bruchts für "alchemistischi Zweck", gell. Aber das isch e Lüg, er suuft eifach gern ab und zue eine.', 'Hmmm, no es la primera vez que él le pide a alguien que le compre una botella. Apuesto a que te dijo que era para fines alquímicos, o ¿me equivocó? Todo es una mentira: solo le gusta emborracharse de vez en cuando.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_Interesting', 'dl_npc_vincent', 'Interesting. But I need to tell you something.','Interessant. Aber ich muss dir jetzt mal was sagen.','Interessant. Aber ich mun der ez mal was sege.', 'Interesante, pero déjame decirte algo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_NoQuest', 'dl_npc_vincent', 'The Elder loves this schnapps. If you bring it to him anyway, he''ll take it gladly, you know. Even an alchemist likes getting drunk from time to time.','Der Älteste liebt diesen Schnaps. Wenn du ihn ihm einfach bringst, wird er ihn gerne doch nehmen. Sogar ein Alchemist betrinkt sich gerne ab und zu.','De Ältesti liebt de Schnaps. Wenn du en ihm eifach bringsch, nimmt er ihn gern. Sogar en Alchemist betrinkt sich gern ab und zue.', 'El anciano ama este aguardiente. Sabes que si al final siempre se lo llevas, él se lo tomará encantado. Incluso a un alquimista le gusta emborracharse de vez en cuando.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_Proposal', 'dl_npc_vincent', 'And that''s pretty harmful to the people of the village - A drunk alchemist is no good alchemist. What if we give the Elder a little... suprise? (Smiles)','Und das ist ziemlich schädlich für die Dorfbewohner. Ein betrunkener Alchemist ist kein guter Alchemist. Wie wärs, wenn wir dem Ältesten eine kleine... Überaschung bereiten? (Lächelt)','Und das isch zimli schlecht für d''Dorfbewohner. En bsoffnige Alchemist isch ken guete Alchemist. We wers, wenn mer em Älteste e chlini... Überraschig mached? (Lächlet)', 'Eso es bastante peligroso para los aldeanos. Un alquimista ebrio no es buen alquimista. ¿Qué te parece si le damos al anciano una pequeña... sorpresa? (Sonríe)');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhatDoYouMean', 'dl_npc_vincent', 'What do you mean by "suprise"?','Was meinst du mit "Überaschung"?','Was meinsch mit "Überraschig"?', '¿A qué te refieres con "sorpresa"?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Defect', 'dl_npc_vincent', 'No, I''m sorry, I''m not interested.','Nein, tut mir leid, kein Interesse.','Nei sorry du, da bini nid interessiert.', 'No; lo siento, pero no me interesa.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Cooperate', 'dl_npc_vincent', 'Sure, sounds interesting. Here, take the bottle.','Ich bin dabei. Da, nimm die Flasche.','Ja guet, da bini debii. Da, nimm d''Fläsche.', 'Por supuesto, suena interesante. Toma la botella.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_Defect', 'dl_npc_vincent', 'Well, your decision is always the right decision. See you later, then...','Nun, deine Entscheidung wird wohl immer die richtige sein. Man sieht sich...','Ja guet, dini Entscheidig wird wohl immer di richtig sii. Demfall bis spöter...', 'Bueno, tu decisión es siempre la correcta. Nos vemos luego, entonces...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_ThePlan', 'dl_npc_vincent', 'I can mix something in the bottle that makes the booze disgusting. That will keep him from further drinking... at least for a while.','Ich könnte etwas in die Flasche mischen, das den Fusel ungeniessbar macht. Das wird ihn vom Trinken abhalten... Zumindest für eine Weile.','Ich chönt öppis id Fläsche mische, wo de Fusel ungnüssbar macht. Das wird ihn vom Suufe abhalte... Wenigstens für es Ziitli.', 'Puedo agregar algo en la botella que le dé un sabor desagradable al licor. Eso evitará que siga bebiendo... Al menos por un tiempo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_DontBelieve', 'dl_npc_vincent', 'I don''t believe you at all. I won''t help you.','Ich glaube dir kein Wort. Ich werde nicht helfen.','Ich glaub der kes Wort. Chasch selber mache.', 'No te creo para nada. No te ayudaré.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_Cooperation', 'dl_npc_vincent', 'Very good. (Opens bottle and puts something in it) That should do it. (Closes bottle) Come back to me after you''ve done your job. I will pay you.','Sehr gut. (Öffnet die Flasche und tut etwas hinein) Das sollte reichen. (Verschliesst die Flasche wieder) Komm zu mir zurück, wenn du den Auftrag erfüllt hast. Ich werde dich bezahlen.','Sehr guet. (Macht d''Fläsche uf und tuet öppis dri) So, das söt lange. (Macht d''Fläsche wider zue) Chum wider zu mir wend das erledigt hesch. Ich zahl der denn öppis.', 'Muy bien. (Abre la botella y le introduce algo). Con eso bastará. (Cierra la botella). Regresa conmigo después de que el trabajo esté hecho. Te pagaré.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_DoYourDuty', 'dl_npc_vincent', 'What are you waiting for? Go and fulfill your duty.','Auf was wartest du? Geh und mach, was ich dir gesagt habe.','Uf was wartisch na? Mach, wani der gseit han.', '¿Qué esperas? Ve a cumplir tu labor.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_GoodJob', 'dl_npc_vincent', 'I know that you have carried out my task. Very good. You might be of use.','Ich sehe, du hast deine Aufgabe erfüllt. Sehr gut. Du könntest noch von Nutzen sein.','Ich gse, du hesch dini Ufgab erledigt. Sehr guet. Du chöntsch na bruuchbar si.','Ya sé que hiciste lo que te pedí. Muy bien, puede que me seas de utilidad.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Vincent_SeeYouLater', 'dl_npc_vincent', 'See you later...','Man sieht sich...','Bis spöter...', 'Nos vemos luego...');
