INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Hi', 'dl_npc_yasha', '(Growls) Another mortal dares to trespass into my temple. Any last words?', '(Knurrt) Noch ein Sterblicher wagt es, in meinen Tempel einzudringen. Sprich deine letzten Worte. ', '(Chnurrt) Nomol so en Sterbliche wogts, i min Tempel iezcho. Seg dini letzte Wort.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YashaSanctuaryDone', 'dl_npc_yasha', 'Can we have a peaceful talk, demon?', 'Können wir uns friedlich unterhalten, Dämon?', 'Chömmer üs friedlich unterhalte, Dämon?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('Choice_Yasha_Attack', 'dl_npc_yasha', '[ATTACK]', '[ANGREIFEN]', '[AAGRIFE]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_YashaSanctuaryDone', 'dl_npc_yasha', '(Yasha stares at you) Hrr. You did traverse my temple without touching my sacred urns. No human did overcome the temptation to touching them before. ', '(Yasha starrt dich an) Hrr. Du hast meinen Tempel durchquert, ohne meine heiligen Urnen anzufassen. Kein Mensch hat dieser Versuchung je widerstanden.', '(D''Yasha starrt dich ah) Hrr. Du hesch min Tempel durchquert ohni eini vo mine heilige Urne ahz''lange. Ken Mensch vor dir het je dere Versuechig widerstoh chöne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_YashaSanctuaryDone2', 'dl_npc_yasha', 'I will answer all of your questions, human.', 'Ich werde all deine Fragen beantworten, Mensch.', 'Ich wird all dini Froge beantworte, Mensch.');
