INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Hi', 'dl_npc_yasha', '(Growls) Another mortal dares to trespass into my temple. Any last words?', '(Knurrt) Noch ein Sterblicher wagt es, in meinen Tempel einzudringen. Sprich deine letzten Worte. ', '(Chnurrt) Numal so än Stärblichä wagts, in minä Tempel ichär z''chu. Säg dini letschtu Wort.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_PeacefulTalk', 'dl_npc_yasha', 'Can we talk peacefully, demon?', 'Können wir uns friedlich unterhalten, Dämon?', 'Chömmer üs friedlich unterhalte, Dämon?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Yasha_Attack', 'dl_npc_yasha', 'I''m not here to talk. [ATTACK]', 'Ich bin nicht zum Reden hier. [ANGREIFEN]', 'Ich bi nid zum Rede do. [AAGRIFE]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_End', 'dl_npc_yasha', '(Back away slowly.)', '(Langsam zurückweichen.)', '(Sich vorsichtig zruggzie.)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_YashaSanctuaryDone', 'dl_npc_yasha', '(Yasha stares at you) Hrr. You did traverse my temple without touching my sacred urns. No human did overcome the temptation of touching them before. ', '(Yasha starrt dich an) Hrr. Du hast meinen Tempel durchquert, ohne meine heiligen Urnen anzufassen. Kein Mensch hat dieser Versuchung je widerstanden.', '(D''Yasha starrt dich ah) Hrr. Dü hesch minä Tempel durchquert ohni eini va minu heiligu Urnä ahz''grifu. Kei Mänsch vor diär het je der Värsüächig chännu widärstah.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_YashaSanctuaryDone2', 'dl_npc_yasha', 'I will answer your questions, human.', 'Ich werde deine Fragen beantworten, Mensch.', 'Ich wärdu dini Fragä beantwortu, Mänsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_PeacefulTalkNot', 'dl_npc_yasha', 'I''ve seen you stomping through my temple, opening my sacred urns and destroying the peaceful atmosphere.', 'Ich habe dich durch meinen Tempel stampfen gesehen, wie du meine heiligen Urnen geöffnet und die friedliche Atmosphäre zerstört hast.', 'Ich ha dich durch minä Tempel gseh stapfu, wiä dü mini Urna üfgita und die friedlich Atmosphära zärstört hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_PeacefulTalkNot2', 'dl_npc_yasha', 'You''re of the same vile kind as all humans. Before I end your miserable existence, I grant you one question. Choose wisely. ', 'Du bist genau so wertlos wie alle Menschen. Bevor ich deine erbärmliche Existenz beende, gewähre ich dir eine Frage. Wähl weise. ', 'Dü bisch genau so wärtlos wiä alli Mänschu. Bevor ich dini erbärmlich Existänz vernichtu, gwähr ich diär nu genau ei Frag. Läs wäise üs.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Attack', 'dl_npc_yasha', 'Well, prepare to die then.', 'Gut, dann mach dich bereit, zu sterben.', 'Güät, de stirbsch halt eifach so.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatGuarding', 'dl_npc_yasha', 'What are you guarding?', 'Was bewachst du?', 'Was bewachsch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mages', 'dl_npc_yasha', 'Who passed through here before me?', 'Wer ist hier vor mir durchgekommen?', 'Wer isch do vor mir durecho?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoSummoned', 'dl_npc_yasha', 'Who summoned you?', 'Wer hat dich beschworen?', 'Wer het dich beschwore?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Secret', 'dl_npc_yasha', 'What''s that secret?', 'Was ist denn dieses Geheimnis?', 'Wa isch denn da Gheimnis?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Key', 'dl_npc_yasha', 'Where''s the key you were guarding?', 'Wo ist dieser Schlüssel, den du bewacht hast?', 'Wo isch de Schlüssel, wo du bewacht hesch?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LetPass', 'dl_npc_yasha', 'Will you let me pass through your temple?', 'Wirst du mich durch deinen Tempel gehen lassen?', 'Derfi dur din Tempel laufe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LetsFight', 'dl_npc_yasha', 'Alright, I''m ready to fight. [ATTACK]', 'Gut, ich bin bereit, zu kämpfen. [ANGREIFEN]', 'Guet, ich bi parat zum kämpfe. [AGRIIFE]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_WhatGuarding', 'dl_npc_yasha', 'I was guarding a key to a secret that never should''ve been revealed.', 'Ich habe einen Schlüssel zu einem Geheimnis bewacht, das nie hätte gelüftet werden sollen.', 'Ich ha än Schlussul zuma Gheimnis bewacht, wa niä hetti sellu glüftut wärdu.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Mages', 'dl_npc_yasha', 'A filthy thief and his henchmen. They outwitted me with some magic not known to me. ', 'Ein dreckiger Dieb und sein Gefolge. Sie haben mich mit mir unbekannter Magie ausgetrickst.', 'Än dräckigä Dieb und schis Gfolg. Schi hent mich mit Magie ubärlischtut, wani nid kännt ha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_WhoSummoned', 'dl_npc_yasha', 'A necromancer, a long time ago. As so many necromancers, he underestimated the powers he was playing with.', 'Ein Nekromant, vor langer Zeit. Wie so viele Nekromanten hat der die Kräfte, mit denen er gespielt hat, unterschätzt.', 'Än Nekromant, vor langer Ziit. Wiä so vill Nekromantä het är d''Chräft, mit denu wanär gspillt het, unärschätzt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Secret', 'dl_npc_yasha', '...', '...', '...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Key', 'dl_npc_yasha', 'Grr. It got stolen. But the sword will find its way back to me.', 'Grr. Es wurde gestohlen. Aber das Schwert wird seinen Weg zurück zu mir finden.', 'Grr. Äs isch gstohlu wordu. Abär das Schwert wird schinä Wäg zrug zu miär gfindu.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_LetPass', 'dl_npc_yasha', 'Hrrr. Yes, I will. But first you will have to prove that you''re worthy. Fight me.', 'Hrrr. Ja, werde ich. Aber zuerst musst du dich als würdig erweisen. Kämpfe mit mir.', 'Hrrr. Ja, tarfsch. Abär zersch müäsch dü dich als würdig erwiisu. Kämpf mit miär.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_DieNow', 'dl_npc_yasha', 'That''s enough. Die now.', 'Das ist genug. Stirb jetzt. ', 'Fertig luschtig. Stirb jetz.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Reward', 'dl_npc_yasha', 'Ahh. I see, your soul is pure and your magic is strong. ', 'Ahh. Wie ich sehe, ist deine Seele rein und deine Magie stark.', 'Ahh. Wiä ich gseh, isch dini Seela rein und dini Magie starch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Reward2', 'dl_npc_yasha', 'Maybe you''re not as vile as the other humans I met. Maybe you can find that filthy thief and thwart his plans. ', 'Vielleicht bist du nicht so wertlos wie die anderen Menschen, die ich getroffen habe. Vielleicht kannst du diesen dreckigen Dieb finden und seine Pläne durchkreuzen.', 'Vilicht bisch dü nid so wärtlos wiä die andru Mänschä wani gitroffu ha. Vilicht chasch dü där Dieb gfindu und schini Plän zärstöru.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Reward3', 'dl_npc_yasha', 'Here, take this weapon and this pendant. May they help you reach your goals.', 'Hier, nimm diese Waffe und diesen Anhänger. Sie sollen dir helfen, deine Ziele zu erreichen.', 'Hiä, nimm die Waffä und der Schmuck. Schi sellund diär hälfu, dini Ziil z''ärreichu.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Sword', 'dl_npc_yasha', 'I found this sword. Is it yours?', 'Ich habe dieses Schwert gefunden. Gehört es dir?', 'Ich ha da Schwert gfunde. Ghört das dir?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Sword', 'dl_npc_yasha', 'Hrr. Where did you find that, human? That''s my sword, it was stolen.', 'Hrr. Wo hast du das gefunden, Mensch? Das ist mein Schwert, es wurde gestohlen.', 'Hrr. Wa hesch das gfundu, Mänsch? Das isch miis Schwert, äs isch miär gstohlu wordu.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TempleFound', 'dl_npc_yasha', 'In the temple in the North.', 'Im Tempel im Norden.', 'Im Tempel im Norde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_TempleFound', 'dl_npc_yasha', 'It''s flame has gone out. That means it has been used to enter the temple''s library. It''s too late.', 'Seine Flamme ist erloschen. Das heisst, es wurde benutzt, um die Bibliothek des Tempels zu betreten. Es ist zu spät.', 'Schini Flamma isch ärlescht. Das heisst, äs isch gibrücht wordu fär die Bibliothek im Tempel z''beträttu. Äs isch z''spat.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_TempleFound2', 'dl_npc_yasha', 'My only purpose was to keep intruders from finding the secrets of the library. I failed.', 'Meine einzige Aufgabe war es, Eindringlinge davon abzuhalten, die Geheimnisse der Bibliothek zu finden. Ich habe versagt.', 'Mini einzig Üfgab isch gsi, Ihdringlingä därfa abz''haltu, die Gheimnis fanär Bibliothek z''gfindu. Ich ha värseit.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_TempleFound3', 'dl_npc_yasha', 'But you, you could find the thief for me and punish him. Give me that sword. (Yasha takes the sword and puts it on the ground)', 'Aber du, du könntest den Dieb finden und ihn bestrafen. Gib mir dieses Schwert. (Yasha nimmt das Schwert und legt es auf den Boden)', 'Abär dü, dü chänntisch där Dieb gfindu und inu bestrafu. Gib mär das Schwert. (D''Yasha nimmt z''Schwert und leit sus ufä Bodu.)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_TempleFound4', 'dl_npc_yasha', '(Yasha uses her fiery breath to reignite the sword)', '(Yasha braucht ihren feurigen Atem um das Schwert zu entzünden)', '(D''Yasha brücht ihru letscht Schnüüf fär z''Schwert z''emprännu.)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_TempleFound5', 'dl_npc_yasha', 'Here. Take it and avenge me.', 'Hier. Nimm es und räche mich.', 'Hiä. Nimm das und räch mich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_BeSafe', 'dl_npc_yasha', 'Goodbye, and stay safe, human.', 'Lebe wohl und pass auf dich auf, Mensch.', 'Läb wohl und pass uf dich üf, Mänsch.');
