INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Hi', 'dl_npc_yasha', '(Growls) Another mortal dares to trespass into my temple. Any last words?', '(Knurrt) Noch ein Sterblicher wagt es, in meinen Tempel einzudringen. Sprich deine letzten Worte. ', '(Chnurrt) Nomol so en Sterbliche wogts, i min Tempel iezcho. Seg dini letzte Wort.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_PeacefulTalk', 'dl_npc_yasha', 'Can we have a peaceful talk, demon?', 'Können wir uns friedlich unterhalten, Dämon?', 'Chömmer üs friedlich unterhalte, Dämon?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('Choice_Yasha_Attack', 'dl_npc_yasha', 'I''m not here to talk. [ATTACK]', 'Ich bin nicht zum reden hier. [ANGREIFEN]', 'Ich bi nid zum rede do. [AAGRIFE]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_End', 'dl_npc_yasha', '(Back away slowly.)', '(Langsam zurückweichen.)', '(Sich vorsichtig zruggzie.)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_YashaSanctuaryDone', 'dl_npc_yasha', '(Yasha stares at you) Hrr. You did traverse my temple without touching my sacred urns. No human did overcome the temptation of touching them before. ', '(Yasha starrt dich an) Hrr. Du hast meinen Tempel durchquert, ohne meine heiligen Urnen anzufassen. Kein Mensch hat dieser Versuchung je widerstanden.', '(D''Yasha starrt dich ah) Hrr. Du hesch min Tempel durchquert ohni eini vo mine heilige Urne ahz''lange. Ken Mensch vor dir het je dere Versuechig widerstoh chöne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_YashaSanctuaryDone2', 'dl_npc_yasha', 'I will answer all of your questions, human.', 'Ich werde all deine Fragen beantworten, Mensch.', 'Ich wird all dini Froge beantworte, Mensch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_PeacefulTalkNot', 'dl_npc_yasha', 'I''ve seen you stomping through my temple, opening my sacred urns and destroying the peaceful atmosphere.', 'Ich habe dich durch meinen Tempel stampfen gesehen, wie du meine heiligen Urnen geöffnet und die friedliche Atmosphäre zerstört hast.', 'Ich ha dich dur min Tempel stampfe gse, we du mini Urne ufgmacht und di friedlich Atmosphäre zerstört hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_PeacefulTalkNot2', 'dl_npc_yasha', 'You''re of the same vile kind as all humans. Before I crush your miserable existence, I grant you one question. Choose wisely. ', 'Du bist genau so wertlos wie alle Menschen. Bevor ich deine erbärmliche Existenz beende, gewähre ich dir eine Frage. Wähl weise. ', 'Du bisch genau so wertlos we all Mensche. Bevor ich dini erbärmlich Existenz vernichte, gewähri dir na genau ei Frog. Wähl weise.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Attack', 'dl_npc_yasha', 'Well, prepare to die then.', 'Gut, dann mach dich bereit, zu sterben.', 'Guet, denn stirbsch halt eifach so.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatGuarding', 'dl_npc_yasha', 'What are you guarding?', 'Was bewachst du?', 'Was bewachsch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mages', 'dl_npc_yasha', 'Who did pass through here before me?', 'Wer ist hier vor mir durchgekommen?', 'Wer isch do vor mir durecho?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoSummoned', 'dl_npc_yasha', 'Who summoned you?', 'Wer hat dich beschworen?', 'Wer het dich beschwore?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Secret', 'dl_npc_yasha', 'What''s that secret?', 'Was ist denn dieses Geheimnis?', 'Wa isch denn da Gheimnis?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Key', 'dl_npc_yasha', 'Where''s that key you were guarding?', 'Wo ist dieser Schlüssel, den du bewacht hast?', 'Wo isch de Schlüssel, wo du bewacht hesch?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LetPass', 'dl_npc_yasha', 'Will you let me pass through your temple?', 'Wirst du mich durch deinen Tempel gehen lassen?', 'Derfi dur din Tempel laufe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LetsFight', 'dl_npc_yasha', 'Alright, I''m ready to fight. [ATTACK]', 'Gut, ich bin bereit, zu kämpfen. [ANGREIFEN]', 'Guet, ich bi parat zum kämpfe. [AGRIIFE]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_WhatGuarding', 'dl_npc_yasha', 'I was guarding a key to a secret that should''ve never been revealed.', 'Ich habe einen Schlüssel zu einem Geheimnis bewacht, das nie hätte gelüftet werden sollen.', 'Ich han en Schlüssel zumne Gheimnis bewacht, wo nie heti söle glüftet werde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Mages', 'dl_npc_yasha', 'A filthy thief and his henchmen. They outwit me with some magic not known to me. ', 'Ein dreckiger Dieb und sein Gefolge. Sie haben mich mit mir unbekannter Magie ausgetrickst.', 'En dreckige Dieb und sis Gefolge. Si hend mich mit Magie überlischtet woni nid kennt ha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_WhoSummoned', 'dl_npc_yasha', 'A necromancer, a long time ago. As so many necromancers, he underestimated the powers he was playing with.', 'Ein Nekromant, vor langer Zeit. Wie so viele Nekromanten hat der die Kräfte, mit denen er gespielt hat, unterschätzt.', 'En Nekromant, vor langer Ziit. We so vili Nekromante het er d''Chräft, mit dene er gspielt het, unterschätzt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Secret', 'dl_npc_yasha', '...', '...', '...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Key', 'dl_npc_yasha', 'Grr. It got stolen. But the sword will find its way back to me.', 'Grr. Es wurde gestohlen. Aber das Schwert wird seinen Weg zurück zu mir finden.', 'Grr. Es isch gstohle worde. Aber es Schwert wird sin Weg zrugg zu mir finde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_LetPass', 'dl_npc_yasha', 'Hrrr. Yes, I will. But first you will have to prove that you''re worthy. Fight me.', 'Hrrr. Ja, werde ich. Aber zuerst musst du dich als würdig erweisen. Kämpfe mit mir.', 'Hrrr. Ja, derfsch. Aber zersch muesch dich als würdig erwiise. Kämpf mit mir.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_DieNow', 'dl_npc_yasha', 'That''s enough. Die now.', 'Das ist genug. Stirb jetzt. ', 'Fertig luschtig. Stirb ez.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Reward', 'dl_npc_yasha', 'Ahh. I see, your soul is pure and your magic is strong. ', 'Ahh. Wie ich sehe ist deine Seele rein und deine Magie stark.', 'Ahh. We ich gse isch dini Seel rein und dini Magie starch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Reward2', 'dl_npc_yasha', 'Maybe you''re not as vile as the other humans I met. Maybe you can find that filthy thief and thwart his plans. ', 'Vielleicht bist du nicht so wertlos wie die anderen Menschen, die ich getroffen habe. Vielleicht kannst du diesen dreckigen Dieb finden und seine Pläne durchkreuzen.', 'Vilicht bisch du nid so wertlos we di andere Mensche woni troffe ha. Vilicht chasch du de Dieb finde und sini Plän zerstöre.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_Reward3', 'dl_npc_yasha', 'Here, take this weapon and this pendant. May they help you reaching your goals.', 'Hier, nimm diese Waffe und diesen Anhänger. Sie sollen dir helfen, deine Ziele zu erreichen.', 'Do, nimm die Waffe und de Schmuck. Si söled der helfe, dini Ziil z''erreiche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Yasha_BeSafe', 'dl_npc_yasha', 'Goodbye, and stay safe, human.', 'Lebe wohl und pass auf dich auf, Mensch.', 'Leb wohl und pass uf dich uf, Mensch.');
