INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zeff_WelcomeBack', 'dl_npc_zeff3', 'Ah, it''s you. Welcome to Gandria. A beautiful city, isn''t it.', 'Ah, du bist es. Willkommen in Gandria. Eine schöne Stadt, nicht wahr.', 'Ah, du bisches. Willkomme in Gandria. E schöni Stadt, gell.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zeff_IGotHat', 'dl_npc_zeff3', 'You''re lucky, I''ve just finished the hat I promised you. Thanks again for saving me.', 'Du hast Glück, ich habe den Hut, den ich dir versprochen habe, gerade jetzt fertiggestellt. Danke nochmals, dass du mich gerettet hast.', 'Du hesch Glück, ich ha de Huet, woni dir versproche ha, grad fertig becho. Danke namal dasd mich gretted hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Trade', 'dl_npc_zeff3', 'What do you have to offer?', 'Was hast du im Angebot?', 'Was hesch so im Agebot?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Leatherworking', 'dl_npc_zeff3', 'Could you craft something for me?', 'Könntest du mir etwas anfertigen?', 'Chöntsch du mir öppis afertige?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Thea', 'dl_npc_zeff3', 'Do you happen to need an apprentice?', 'Brauchst du zufälligerweise einen Lehrling?', 'Bruchsch du zuefälligerwiis en Lehrling?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_End', 'dl_npc_zeff3', 'See you later.', 'Bis später.', 'Bis spöter.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zeff_Leatherworking', 'dl_npc_zeff3', 'Sure, if you got a fur for me?', 'Natürlich, wenn du ein Fell für mich dabei hast?', 'Sicher, wenn du es Fell für mich debii hesch?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FireRatFur', 'dl_npc_zeff3', 'eq_fireratscarf,mi_firerat_fur,1,gold,20', 'eq_fireratscarf,mi_firerat_fur,1,gold,20', 'eq_fireratscarf,mi_firerat_fur,1,gold,20');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NekomataCloak', 'dl_npc_zeff3', 'eq_nekomataback,mi_neko_fur,3,mi_neko_teeth,2,gold,70', 'eq_nekomataback,mi_neko_fur,3,mi_neko_teeth,2,gold,70', 'eq_nekomataback,mi_neko_fur,3,mi_neko_teeth,2,gold,70');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NekomataHead', 'dl_npc_zeff3', 'eq_nekomatahead,mi_neko_fur,1,mi_neko_teeth,6,gold,100', 'eq_nekomatahead,mi_neko_fur,1,mi_neko_teeth,6,gold,100', 'eq_nekomatahead,mi_neko_fur,1,mi_neko_teeth,6,gold,100');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WolfBonnet', 'dl_npc_zeff3', 'eq_wolfbonnet,mi_wolf_fur,3,gold,40', 'eq_wolfbonnet,mi_wolf_fur,3,gold,40', 'eq_wolfbonnet,mi_wolf_fur,3,gold,40');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NothingToCraft', 'dl_npc_zeff3', 'I''ll come back later. [BACK]', 'Ich komme später wieder. [ZURÜCK]', 'Ich chum spöter namal. [ZRUGG]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zeff_FireRatFur', 'dl_npc_zeff3', 'The fur of a fire rat, how nice. It has the ability to protect you against fire.', 'Das Fell einer Feuerratte, wie hübsch. Es hat die Eigenschaft, dich vor Feuer zu schützen.', 'Es Fell vonere Füürratte, we hübsch. Es het d''''Eigeschaft, dich vor Füür z''schütze.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zeff_NekomataCloak', 'dl_npc_zeff3', 'A fine cloak for a skilled hunter. It''s not easy to slay those beasts.', 'Ein schöner Umhang für einen fähigen Jäger. Es ist nicht einfach, diese Biester zu erlegen.', 'En schöne Umhang für en fähige Jäger. Es isch nid eifach, die Biester z''erlege.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zeff_NekomataHead', 'dl_npc_zeff3', 'There you go. A very special headpiece, indeed.', 'Hier, nimm. Eine wirklich spezielle Kopfbedeckung.', 'Do, nimm. En würkli spezielli Huet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zeff_Wolfbonnet', 'dl_npc_zeff3', 'Wolf fur? I hope this isn''t some kind of sick joke. But here, take this bonnet.', 'Wolfsfelle? Ich hoffe, das ist nicht irgendein schlechter Scherz. Aber hier, nimm diese Mütze.', 'Wolfsfell? Ich hoff, das isch nid irgenden chranke Scherz. Aber guet, nimm die Chappe.');
