INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhoAreYou', 'dl_npc_zeff','Who are you?','Wer bist du?','Wer bisch du?', '¿Quién eres?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhatAreYou', 'dl_npc_zeff','What are you?','Was bist du?','Wa bisch du?', '¿Qué eres?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhatAreYouDoing', 'dl_npc_zeff','What are you doing here?','Was machst du hier?','Was machsch du da?', '¿Qué estás haciendo aquí?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Curse', 'dl_npc_zeff','What''s this curse you are talking about?','Von was für einem Fluch sprichst du?','Vo wa fürme Fluech redsch du?', '¿Cuál es esa maldición de la que hablas?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Leatherworking', 'dl_npc_zeff','Could you craft something for me?','Könntest du mir etwas anfertigen?','Chöntsch du mir öppis afertige?','¿Me podrías hacer algo?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_CU', 'dl_npc_zeff','See you later.','Bis bald.','Bis bald.', 'Nos vemos.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_SittingHere', 'dl_npc_zeff','I''m sitting here at the fire, hoping not to devour anyone before that curse devours myself.','Ich sitze hier am Feuer und hoffe, dass ich niemanden verschlinge bevor dieser Fluch mich verschlingt.','Ech sitz da am Füür und hoffe, dass ech niemer verschlinge bevor de Fluech mich verschlingt.', 'Estoy aquí sentado al pie de la fogata con la esperanza de no devorar a alguien antes de que la maldición me devore a mí.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_IAmWho', 'dl_npc_zeff','I''m Zeff.','Ich bin Zeff.','Ech be de Zeff.', 'Soy Zeff.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_IAmWhat', 'dl_npc_zeff','(Growls quietly) I see, my outer appearance is striking.','(Knurrt leise) Ich sehe, meine äusserliche Erscheinung fällt auf.','(Chnurrt liislig) Ech gse scho, mini üsserlichi Erschinig fallt grad uf.', '(Gruñe con discreción) Ya veo. Mi apariencia impresiona.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_IAmSurprised', 'dl_npc_zeff','I''m surprised you have the courage to talk to me. Most people I meet flee in terror when they see my face.','Ich bin überrascht, dass du den Mut zeigst, mich anzusprechen. Die meisten Leute fliehen voller Entsetzen, wenn sie mein Gesicht sehen.','Ech be überrascht, dass du de Muet hesch, mit mer z''rede. Die meiste Lüüt flüchtet voller Entsetze, wenn si mis Gsecht gsänd.', 'Me sorprende que tengas la valentía de hablarme. La mayoría de las personas que veo huye aterrada al ver mi rostro.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_YouLookNice', 'dl_npc_zeff','I don''t think you look terrifying.','So schrecklich siehst du jetzt auch wieder nicht aus.','So schreckli gsesch ez au wider nid us.', 'No me pareces aterrador.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_YouLookTerrible', 'dl_npc_zeff','That''s not surprising. You truly look scary.','Das überrascht mich nicht. Du siehst furchteinflössend aus.','Das überrascht mich nid. Du gsesch gfürchig us.', 'No me sorprende. Realmente te ves aterrador.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IAmNotAfraid', 'dl_npc_zeff','I''m not afraid of you.','Ich habe keine Angst vor dir.','Ich ha ke Angst vor dir.', 'No te tengo miedo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_ThankYouLooks', 'dl_npc_zeff','Thank you. You''re the first person to say something nice since I was turned into this monster.','Danke. Du bist die erste Person, die etwas nettes zu mir sagt, seit ich in dieses Monster verwandelt wurde.','Danke. Du besch di ersti Person wo öppis nätts zu mer seid, sit ich ih das Monster verwandlet worde bin.', 'Muchas gracias. Eres la primera persona que me dice algo amable desde que me convirtieron en este monstruo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_IAmScary', 'dl_npc_zeff','(Hangs his head) Yes I do. I really wish I wouldn''t have been turned in such a monster.','(Lässt seinen Kopf hängen) Ja, das stimmt. Ich wünschte wirklich, ich wäre nicht in dieses Monster verwandelt worden.','(Laht sin Chopf hänge) Ja, das stimmt. Ich wünschmer werkli, ech wär nid ih das Monster verwandlet worde.', '(Agacha la cabeza) Sí, así es. En realidad desearía que no me hubieran transformado en este monstruo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_YouShouldBe', 'dl_npc_zeff','Maybe you should be. My mind is still human, but I feel that it will also be turned into the one of a monster soon.','Vielleicht solltest du aber. Ich kann momentan immer noch wie ein Mensch denken, aber ich spüre, dass ich mich bald ganz in ein Monster verwandeln werde.','Vellecht settisch aber. Ich chan zwar jetzt immerno wie en Mensch dänke, aber ech spür, dass ech mich bald ganz in es Monster verwandle werde.', 'Tal vez deberías estarlo. Mi mente todavía es humana, pero siento que pronto se convertirá en la de un monstruo también.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhyMonster', 'dl_npc_zeff','Why were you turned into... this?','Wieso wurdest du in... das verwandelt?','Wieso bisch denn i... das verwandlet worde?', '¿Por qué te transformaron en... esto?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Werewolf', 'dl_npc_zeff','Are you a werewolf?','Bist du ein Werwolf?','Bisch du en Werwolf?', '¿Eres un hombre lobo?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_Werewolf', 'dl_npc_zeff','You''ve never met a werewolf, have you? A werewolf wouldn''t be able to talk to you.','Du bist noch nie wirklich einem Werwolf begegnet, nicht wahr? Ein Werwolf könnte nicht so mit dir sprechen.','Du besch nonie werklich emene Werwolf begegnet, oder? En Werwolf chönt ned so mit der rede.', 'Nunca has conocido a un hombre lobo, ¿verdad? Un hombre lobo no podría hablarte.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_WhyMonster', 'dl_npc_zeff','A horrible curse was cast upon me - and now I can''t return to the city of Gandria, I''d be a threat to the citizens.','Ein schrecklicher Fluch wurde mir auferlegt - und jetzt kann ich nicht in die Stadt Gandria zurückkehren, ich würde eine Bedrohung für die Bürger sein.','En schreckliche Fluech het mech troffe - und jetzt chani ned id Stadt Gandria zrugg.', 'Me arrojaron una horrible maldición y ahora no puedo regresar a la cuidad de Gandria; sería una amenaza para los ciudadanos.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_Craft', 'dl_npc_zeff','Sure, if you got a fur for me?','Natürlich, wenn du ein Fell für mich dabei hast?','Sicher, wenn du es Fell für mech derbi hesch?', 'Claro, ¿tienes una piel para mí?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_NothingToCraft', 'dl_npc_zeff','I''ll come back later. [BACK]','Ich komme später wieder. [ZURÜCK]','Ich chum spöter namal. [ZRUGG]', 'Regresaré después. [REGRESAR]');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_FireRatFur', 'dl_npc_zeff','The fur of a fire rat, how nice. It has the ability to protect you against fire.','Das Fell einer Feuerratte, wie hübsch. Es hat die Eigenschaft, dich vor Feuer zu schützen.','Es Fell vonere Fürratte, so rüdig. Es het d''Eigeschaft, dich vor Füür z''schütze.', 'La piel de una rata de fuego, qué bien. Tiene la habilidad de protegerte contra el fuego.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_CurseStronger', 'dl_npc_zeff','So, this curse is getting stronger?','Dieser Fluch wird also stärker?','De Fluech wird also stärcher?', 'Entonces, ¿la maldición se está haciendo más fuerte?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_HelpCurse', 'dl_npc_zeff','Can I help you somehow breaking that curse?','Kann ich dir irgendwie helfen, den Fluch zu brechen?','Chan ich dir irgendwie helfe, de Fluech z''breche?', '¿Te puedo ayudar de alguna manera a romper la maldición?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_LivedInGandria', 'dl_npc_zeff','You were a citizen of Gandria?','Du warst ein Bürger von Gandria?','Du bisch en Bürger vo Gandria gsi?','¿Eras ciudadano de Gandria?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Sheep', 'dl_npc_zeff','A few sheep went missing...','Den Bauern fehlen ein paar Schafe...','De Buure fehled es paar Schöfli...', 'Se perdieron unas ovejas...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_HelpCurse', 'dl_npc_zeff','Maybe. If you are brave enough, you can go and try to kill that thing. Killing the creator of a curse will break it in most cases.','Vielleicht. Wenn du mutig genug bist, kannst du probieren, dieses Ding zu töten. Vernichtet man den Schöpfer eines Fluches, wird dieser meistens dadurch gebrochen.','Vellecht. Wenn du gnueg Muet hesch, chasch ja probiere, das Ding z''töte. Wemmer de Schöpfer vomene Fluech vernichtet, chamer so au meistens de Fluech selber bräche.', 'Tal vez. Si tienes la valentía suficiente, puedes intentar matar a esa cosa. En la mayoría de los casos, matar al creador de la maldición la rompe.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_HelpCurse2', 'dl_npc_zeff','The cave where it hides is right outside of the city. But be cautious and don''t let it stare into your eyes.','Die Höhle, wo es sich versteckt, ist direkt vor der Stadt. Aber nimm dich in Acht und lass es nicht in diene Augen schauen.','D''Höhli, wo''s es sich versteckt isch direkt vor de Stadt. Aber pass huere uf und lah es ned ih dini Auge luege.', 'La cueva en la que se esconde está justo a las afueras de la ciudad, pero ten cuidado y no dejes que te mire a los ojos.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_CurseStronger', 'dl_npc_zeff','Yes. In the beginning, this fur grew and shortly after that, I got wolf ears and eyes. Then this magnificent tail popped out and now I''m wondering for how long I can still walk on two legs.','Ja. Am Anfang ist mir dieses Fell gewachsen und kurz danach habe ich Wolfsohren und -Augen gekriegt. Dann ist dieser prächtige Schwanz aufgetaucht und jetzt frage ich mich, wie lange ich noch auf zwei Beinen laufen kann.','Ja. Am Aafang isch mir das Fell gwachse und nachhär hani Wolfsohre und -Auge becho. Denn isch de rüdigi Schwanz uftaucht und jetzt frog ich mich, wie lang ich no uf zwei Bei laufe chan.', 'Sí. Al principio, me crecía pelaje y, poco después, me salieron ojos y orejas de lobo. Luego salió esta majestuosa cola y ahora me pregunto por cuánto tiempo más podré caminar en dos piernas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_Curse', 'dl_npc_zeff','The curse that forces me to have the appearance of a monster. And soon, also its mind.','De Fluech, wo mich dezue zwingt, uszgseh wie es Monster. Und bald gsehni ned nur so us, sondern denk au so.','De Fluech, wo mich dezue zwingt, uszgse wene Monster. Und bald gsehni nid nur so uus, sondern denk au so.', 'La maldición que me obliga a tener la apariencia de un monstruo, y muy pronto, también su mente.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_HowCursed', 'dl_npc_zeff','Why did you get cursed?','Wieso wurdest du verflucht?','Weso bisch denn verfluecht worde?', '¿Por qué te maldijeron?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_Hunting', 'dl_npc_zeff','I went out of the city to hunt for fur, when I discovered a cave full of wolves.','Ich ging aus der Stadt, um zu jagen, damit ich meinen Fellvorrat aufstocken konnte. Dann habe ich eine Höhle voller Wölfe entdeckt.','Ech be us de Stadt gange zum go jage, damit ech min Fellvorrat hetti chönne ufstocke. Denn hani e Höhli voller Wölf entdeckt.', 'Salí de la ciudad a cazar pieles y descubrí una cueva llena de lobos.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_Hunting2', 'dl_npc_zeff','So I entered that cave, hoping to get one or two of them with my "skinning knife" (holds up his sword). But I didn''t get far.','Also ging ich hinein, in der Hoffnung, einen oder zwei mit meinem "Kürschnermesser" (hebt sein Schwert hoch) zu erlegen. Aber ich bin nicht weit gekommen.','Also bini inne gange, ide Hoffnig eine oder zwei mit mim "Kürschnermesser" (hebt sis Schwert ufe) z''erlege. Aber ech ben ned wiit cho.', 'Me adentré en la cueva con la esperanza de agarrar una o dos pieles con mi cuchillo para desollar. (Levanta su espada) Pero no llegué muy lejos.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_Hunting3', 'dl_npc_zeff','Then this... thing appeared. I couldn''t see much, because it was dark in there, but it was huge. One glowing eye, staring at me... The next thing I remember is waking up at the entry of the cave, covered in fur.','Dann ist dieses... Ding aufgetaucht. Ich konnte nicht viel sehen, weil es so dunkel war da drin, aber es war riesig. Ein glühendes Auge, das mich anstarrte... Das Nächste, an das ich mich erinnere, ist, dass ich vor der Höhle aufgewacht bin, bedeckt mit Fell.','Denn isch das... Ding uftaucht. Ech ha ned viel gse, wells so dunkel gsi esch, aber es esch huere gross gsi. Mit eim lüüchtende Aug, wo es mich agstarrt het... und s Nöchste wo mech chan drah erinnere isch dasi vor de Höhli glege ben, bedeckt mit Fell.', 'Luego esta... cosa apareció. No pude ver mucho porque estaba oscuro, pero era enorme. Un ojo brillante me miraba fijamente… Luego recuerdo despertar a la entrada de la cueva cubierto de este pelaje.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_LivedGandria', 'dl_npc_zeff','Yes, I was. I had a beautiful little leather workshop in Gandria. (Sobs) But it seems like I''ll never see it again.','Ja. Ich hatte eine wunderschöne kleine Lederwerkstatt in Gandria. (Schluchzt) Aber es scheint, als werde ich sie nie wieder sehen.','Ja. Ech ha en herzigi chliini Lederwerkstatt in Gandria gha. (Schluchzt) Aber jetzt gsehts so us, als würdi die nie wieder gseh.', 'Sí, solía serlo. Tenía una marroquinería en Gandria. (Solloza) Pero al parecer, nunca la voy a volver a ver.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhyDontReturn', 'dl_npc_zeff','Why don''t you try to go back?','Wieso versuchst du nicht, zurück zu gehen?','Weso probiersch nid, zrugg z''ga?', '¿Por qué no intentas regresar?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Sorry', 'dl_npc_zeff','I''m so sorry for you.','Du tust mir wirklich Leid.','Tuesch mer echt recht Leid.', 'Lo siento mucho.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_BadGuards', 'dl_npc_zeff','The city guards won''t believe what happened and try to kill me on sight. I''d rather die here, alone and without harming anyone.','Die Stadtwachen werden nicht glauben, was passiert ist. Sie werden mich töten, wenn sie mich sehen. Ich bleibe lieber hier und sterbe alleine, ohne jemandem zu schaden.','D''Stadtwache werded mer ned glaube, was passiert esch. Si werded mich töte, wenn si mech gsähnd. Ech bliebe lieber da und stirb elei, ohni dasi öpper verletze.', 'Los guardias de la ciudad no van a creer lo ocurrido e intentarán matarme cuando me vean. Prefiero morir acá, solo y sin lastimar a nadie.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_SheepWhatDo', 'dl_npc_zeff','What can I do to convince you to stop stealing sheep?','Wie kann ich dich überzeugen, keine Schafe mehr zu stehlen?','Wie chanich dich überzüüge, ke Schöfli me z''chlaue?', '¿Qué puedo hacer para convencerte de que dejes de robar ovejas?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_AttackHim', 'dl_npc_zeff','I was asked to kill the sheep thief. And I will do so. [ATTACK]','Ich habe den Auftrag, den Schafdieb zu töten... [ANGREIFEN]','Ich han de Uftrag, de Schafdieb z''töte. Und das wirdi au. [AAGRIFE]', 'Me pidieron que asesinara al ladrón y eso es lo que haré. [ATACAR]');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IGotMeat', 'dl_npc_zeff','I got some meat for you. I hope that''s enough.','Ich habe dir Fleisch mitgebracht. Ich hoffe das reicht.','Ich ha der Fleisch mitbracht. Ich hoff das langt.', 'Tengo algo de carne para ti. Espero que sea suficiente.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_YouSuspect', 'dl_npc_zeff','Yes... yes... and now, only because I look like a wolf, I''m your first suspect.','Ja... ja... und nur weil ich wie ein Wolf aussehe, bin ich gleich dein erster Verdächtiger.','Jaja... und nur weli wie en Wolf usgseh, beni grad din ersti Verdächtigi.', 'Sí... Sí... y como ahora parezco un lobo, soy tu primer sospechoso.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_YoureRight', 'dl_npc_zeff','Well, to be honest, you''re totally right. I was the one who stole the sheep. But I can''t just walk into the tavern and buy some meat, can I?','Aber wenn ich ehrlich bin, hast du natürlich recht. Ich habe die Schafe gestohlen. Aber ich kann auch nicht einfach in die Taverne laufen und mir Fleisch kaufen, oder?','ber weni ehrlich ben, hesch natürlich rächt. Ech han die Schöfli klaut. Aber ech cha au ned eifach id Taverne laufe und mer Fleisch chaufe, oder?', 'Bueno, para ser honesto, estás completamente en lo correcto. Yo fui el que robó las ovejas, pero no puedo simplemente entrar a la taberna y comprar carne, ¿verdad?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_BadassSword', 'dl_npc_zeff','You have a badass sword, killing sheep is just low.','Mit einem solch starken Schwert ist Schafe töten einfach nur schwach.','Mit dem krasse Schwert isch Schöfli kille eifach nur asi.', 'Tienes una espada impresionante. Matar ovejas es caer muy bajo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_ComeAndTry', 'dl_npc_zeff','That''s the last mistake you''ll ever make.','Das ist der letzte Fehler, den du jemals begehen wirst.','Das isch de letzti Fähler, wo du jemals mache wersch.','Ese será tu último error.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_BadassSword', 'dl_npc_zeff','Yes, but when I get hungry, I can''t control myself anymore. But better sheep than people, I guess.','Ja, aber wenn ich Hunger habe, kann ich mich nicht mehr kontrolliere. Aber besser Schafe als Menschen, was.','Ja, aber wenn ich Hunger han, chan ich mech nüme kontrolliere. Aber besser Schöfli als Mensche, gäll.', 'Sí, pero cuando estoy hambriento, no me puedo controlar. Aunque supongo que es mejor matar ovejas que personas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_HungryWolf', 'dl_npc_zeff','If you can get me a ham and a sausage from the tavern, I can stop killing sheep, at least for a while. And after that, I will go for other prey, promised.','Wenn du mir einen Schinken und eine Wurst aus der Taverne bringst, werde ich aufhören, die Schafe zu reissen, zumindest für eine Weile. Und danach werde ich mir andere Beute suchen, versprochen.','Wenn du mer en Schinke und e Wurst us de Taverne holsch, chani ufhöre, Schöfli z''risse, zumindest für es Zitli. Und nachene suechi mer en anderi Bühti, versproche.', 'Si logras conseguirme un poco de carne de la taberna, puedo dejar de matar ovejas, al menos por un rato. Luego de eso, cazaré otra presa, lo prometo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_ThanksForMeat', 'dl_npc_zeff','Thank you, that''s great. You can tell the farmers that the beast won''t come again. And if you wish, I can help you out with my leatherworking skills, as long as I''m still human enough to craft something.','Danke, das ist perfekt. Du kannst den Bauern sagen, dass das Biest nicht mehr auftauchen wird. Und wenn du möchstest, kann ich dir mit meinen Ledererkünsten helfen, zumindest so lange, wie ich noch genügend menschlich bin.','Danke, das isch perfekt. Du chasch de Buure säge, dass s Biescht nümme uftauche werd. Und wend wetsch, chan ech der mit mine Ledererkünst hälfe, zumindest so lang, woni na gnueg Mensch bin.', 'Muchas gracias, eso es genial. Puedes decirles a los granjeros que la bestia no volverá de nuevo. Y, si lo deseas, puedo ayudarte con mis habilidades de marroquinería, siempre y cuando todavía quede algo de humano en mí para crear algo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_MyCurse', 'dl_npc_zeff','I got cursed too, do you know what it is? (Show the mark)','Ich wurde auch verflucht, weisst du, was das ist? (Mal zeigen)','Ich bi au verfluecht worde. Weisch, wa das isch? (Mal zeige)', 'Yo también fui maldecido. ¿Sabes qué es esto? (Muestra la marca)');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_ShowMark', 'dl_npc_zeff','Hrr... (Glares at your mark) I''ve never seen something like that.','Hrr... (Starrt dein Mal an) Ich habe noch nie so etwas gesehen.','Hrr... (Starrt dis Mal ah) Ech han nonie so öppis we das gseh.', 'Grr... (Mira mi marca fijamente) Nunca había visto algo así.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_ShowMark2', 'dl_npc_zeff','But I think you got off cheaply... I mean look at my face! Having a fancy tattoo can''t be worse than be forced to kill sheep every night.','Aber ich finde, du bist ziemlich gut davongekommen... Ich meine, schau dir mal mein Gesicht an! Ein hübsches Tattoo kann doch kaum schlimmer sein, als der Zwang, jede Nacht ein Schaf zu holen.','Aber ech find, du besch ja zimlich guet wegcho... Ech mein, lueg der mal mis Gsicht ah! Es härziges Tattoo cha doch chum schlimmer sii, als de Zwang, jedi Nacht es Schöfli z''hole.', 'Claro, a ti te fue bien... ¡Tan solo mira mi rostro! Tener un elegante tatuaje no puede ser peor que estar obligado a matar ovejas cada noche.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_ItWasYou', 'dl_npc_zeff','So it was YOU who stole the sheep from the farmers!','DU bist also der Dieb, der die Schafe stiehlt!','DU bisch also de Dieb, wo d''Schöfli chlaut!', '¡Entonces fuiste TÚ quien robó las ovejas de los granjeros!');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_YouAreRight', 'dl_npc_zeff','Maybe you''re right...','Vielleicht hast du ja recht...','Vilicht hesch ja recht...', 'Tal vez tengas razón...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_MyCurseIsWorse', 'dl_npc_zeff','My curse could be worse, I just don''t know.','Mein Fluch könnte schlimmer sein, ich weiss es einfach nicht.','Min Fluech chönt schlimmer sii, ich weisses eifach nid.', 'Mi maldición podría ser peor, solo que no lo sé.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_BeastSlain', 'dl_npc_zeff','I found the beast that cursed you in the caverns...','Ich habe das Biest, das dich verfluchte, in den Höhlen gefunden...','Ich hans Viich wo dich verfluecht het ide Höhle gfunde...', 'Encontré a la bestia que te maldijo en las cavernas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Cendric_BeastSlain', 'dl_npc_zeff','... It''s dead.','... Es ist tot.','... Es isch tot.', '... Está muerta.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_BeastSlain', 'dl_npc_zeff','Really? (Stares at his paws) I don''t feel any different.','Wirklich? (Starrt auf seine Pfoten) Ich spüre keinen Unterschied.','Würklich? (Starrt uf sini Pfote) Ech gspür kein Unterschied.', '¿De verdad? (Mira sus garras) No siento nada distinto.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_BeastSlain2', 'dl_npc_zeff','Though the curse has not worsened lately. Maybe the transformation just stopped...','Der Fluch wurde in letzter Zeit aber auch nicht mehr schlimmer. Vielleicht wurde die Verwandlung einfach gestoppt.','De Fluech esch aber i letzter Ziit au ned schlimmer worde. Vellecht esch d''Verwandlig eifach gstoppt worde.', 'Aunque la maldición no ha empeorado últimamente. Tal vez la transformación simplemente se detuvo...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_StayWolf', 'dl_npc_zeff','Yes, it seems like you''ll stay a wolf.','Ja, wahrscheinlich musst du ein Wolf bleiben.','Ja, wahrschinli muesch en Wolf bliibe.', 'Sí, parece que seguirás siendo un lobo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_ReturnToGandria', 'dl_npc_zeff','Will you be able to return to Gandria anyway?','Wirst du trotzdem nach Gandria zurückkehren?','Wirsch trotzdem nach Gandria zrugg gah?', 'Aun así, ¿podrás regresar a Gandria?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_StayWolf', 'dl_npc_zeff','I guess so. If my mind keeps clear, I think I can even get to like that form. But when I think about returning home...','Es scheint so. Wenn ich einen klaren Kopf behalte, kann ich mich vielleicht sogar mit dieser Gestalt anfreunden. Aber wenn ich daran denke, zurückzukehren...','Es schiint so. Wenn ech en klare Chopf phalte chan, chani mer vellecht sogar met dere Gstalt aafründe. Aber wen ich drüber nachedenke, zrugg z''ga...', 'Supongo que sí. Si mi mente se mantiene lúcida, creo que incluso me podría empezar a gustar esta apariencia. Sin embargo, cuando pienso en regresar a casa...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_ReturningHome', 'dl_npc_zeff','I''m afraid, I still can''t get past the guards. Maybe you could try to tell them what happened and convince them to let me in? When I get my workshop back, I''m sure I could craft you a natty hat as a reward.','Ich fürchte, ich komme immer noch nicht an den Wachen vorbei. Vielleicht könntest du ihnen sagen, was passiert ist, und sie davon überzeugen, mich wieder hereinzulassen? Ich könnte dir bestimmt einen schicken Hut als Belohnung anfertigen, wenn ich meine Werkstatt wieder zurück habe.','Ech fürchte, ech chume immer nonig ah de Wache verbii. Vellecht chöntisch du ehne verzelle, was passiert esch, und si dervo überzüüge, mech weder ihne z lah? Ech chönt der en u huere Huet afertige als Dank, wen ich mini Werkstatt wieder han.', 'Me temo que todavía no puedo pasar por los guardias. ¿Tal vez tú podrías comentarles lo ocurrido y convencerlos de que me dejen entrar? Cuando regrese a mi taller, estoy seguro de que podré elaborar un elegante sombrero como recompensa para ti.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_GoHomeZeff', 'dl_npc_zeff','I''ve talked to the guards. They''ll let you in.','Ich habe mit den Wachen gesprochen. Sie werden dich reinlassen.','Ich ha mit de Wache gredt. Si werded dich ielah.', 'Hablé con los guardias. Te dejarán entrar.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Zeff_IWillGoHome', 'dl_npc_zeff','Thank you for the great news. I can''t wait to see my beautiful house again! Don''t forget to visit me in Gandria to get your reward.','Danke für die guten Neuigkeiten! Ich kann es kaum erwarten, mein hübsches Häuschen wieder zu sehen. Vergiss nicht, mich in Gandria zu besuchen, um deine Belohnung abzuholen.','Danke für die guete Neuigkeite! Ech chas chuum erwarte, mis hübsche Hüsli weder zgseh. Vergiss ned, mer in Gandria en Bsuech zmache, um dini Belohnig abhole.', 'Gracias por las buenas noticias. ¡Me muero por ver mi hermosa casa de nuevo! No olvides visitarme en Gandria para darte tu recompensa.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zeff_Wolfbonnet', 'dl_npc_zeff', 'Wolf fur? I hope this isn''t some kind of sick joke. But here, take this bonnet.', 'Wolfsfelle? Ich hoffe, das ist nicht irgendein schlechter Scherz. Aber hier, nimm diese Mütze.', 'Wolfsfell? Ech hoffe, das isch ned ergenden chranke Scherz. Aber guet, nimm die Chappe.');
