INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_Hello', 'dl_npc_zephyra', 'Don''t come any closer! There''s already enough damage done. Go home to where you belong!', 'Komm ja nicht näher! Es wurde schon genügend Schaden angerichtet. Geh dorthin zurück, wo du herkommst!', 'Chum jo nid nöcher! Es isch scho gnueg Schade agrichted worde. Gang wieder det ane wod hercho bisch!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatsTheMatter', 'dl_npc_zephyra', 'What''s the matter?', 'Was ist denn los?', 'Was isch denn los?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_zephyra', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_DeadCat', 'dl_npc_zephyra', 'Why are you wearing a dead cat on your head?', 'Wieso trägst du eine tote Katze auf deinem Kopf?', 'Wieso hesch du e toti Chatz uf dim Chopf?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoYasha', 'dl_npc_zephyra', 'Tell me more about that "Yasha".', 'Erzähl mir mehr von dieser "Yasha".', 'Verzell mer me vo dere "Yasha".');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoDroves', 'dl_npc_zephyra', 'Who did pass through here?', 'Wer ist denn hier durchgekommen?', 'Wer isch denn da durecho?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoMages', 'dl_npc_zephyra', 'How did those mages look like?', 'Wie haben diese Magier ausgsehen?', 'Wie hend die Magier usgse?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YashaDone', 'dl_npc_zephyra', 'I talked to Yasha. She''s pacified again.', 'Ich habe mit Yasha gesprochen. Sie ist jetzt wieder besänftigt.', 'Ich ha mit de Yasha gredt. Si isch ez wieder besänftigt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GottaGo', 'dl_npc_zephyra', 'Well, I gotta go now. Thanks for the information.', 'Nun, ich muss jetzt los. Danke für die Informationen.', 'Jo du, ich mu ez los. Danke für d''Infos.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_WhatsTheMatter', 'dl_npc_zephyra', 'I haven''t seen anyone passing through here in ages. And suddenly, they are coming in droves and destroying this sanctuary. ', 'Ich habe schon seit Ewigkeiten niemanden mehr hier durchkommen sehen. Und plötzlich kommen sie in Scharen und zerstören dieses Heiligtum.', 'Ich ha scho sit Ewigkeite niemert me da durecho gse. Und jetz chömeds plötzli in Schare und zerstöred das Heiligtum.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_WhoAreYou', 'dl_npc_zephyra', 'I''m Zephrya, hermit and shaman of Yasha.', 'Ich bin Zephyra, Einsiedlerin und Yasha''s Schamanin. ', 'Ich bin d''Zephyra, Einsiedlerin und de Yasha ihri Schamanin.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_DeadCat', 'dl_npc_zephyra', 'That''s no dead cat, it''s a demon''s fur. It''s nice and warm.', 'Das ist keine tote Katze, es ist ein Dämonenfell. Es ist schon warm.', 'Das isch ke toti Chatz, es isch es Dämonefell. Es git schön warm.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_DemonSlayer', 'dl_npc_zephyra', 'So you''re a demon slayer?', 'Du bist also eine Dämonentöterin?', 'Du bisch also e Dämonetöterin?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NiceCat', 'dl_npc_zephyra', 'It''s looking nice. Where can I get one too?', 'Sieht hübsch aus. Wo kann ich auch eins bekommen?', 'Gseht hübsch us. Wo chani au eis becho?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_DemonSlayer', 'dl_npc_zephyra', 'No, I would never hurt one. They''re protecting this valley, you know.', 'Nein, ich würde nie einem etwas zu leide tun. Sie beschützen unser Tal, weisst du.', 'Nei, ich wür nie eim öppis atue. Si beschützed das Tal, weisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_NiceCat', 'dl_npc_zephyra', 'Don''t you dare! Only Yasha''s shamans are allowed to wear this headpiece.', 'Wag es ja nicht! Nur Yasha''s Schamanen dürfen diesen Kopfschmuck tragen.', 'Wogs jo nid! Nur de Yasha ihri Schamane derfed de Choppfschmuck alege.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_WhoYasha', 'dl_npc_zephyra', 'Yasha is a powerful demon who watches over the secrets of this valley.', 'Yasha ist eine mächtige Dämonin, die über die Geheimnisse dieses Tals wacht.', 'D''Yasha isch e mächtigi Dämonin, wo über d''Gheimnis vo dem Tal wacht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_WhoYasha2', 'dl_npc_zephyra', 'She''s been sleeping for many years, but those yokels did wake her up. I hope they''re paying right now for their foolishness.', 'Sie hat viele Jahre lang geschlafen, aber diese Tölpel haben sie aufgeweckt. Ich hoffe, sie büssen gerade für ihre Dummheit.', 'Si het vieli Johr lang gschlofe, aber die Tubelichöpf hend si gweckt. Ich hoff si büessed grad für ihri Dummheite.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_WhoDroves', 'dl_npc_zephyra', 'A group of foolish mages. I wanted to keep their from heading straight for disaster, but they wouldn''t listen.', 'Eine Gruppe törichter Magier. Ich wollte sie davon abbringen, direkt ins Verderben zu rennen, aber sie wollten nicht hören.', 'E Gruppe vo futzdumme Magier. Ich ha si devo abhalte wele, direkt is Verderbe z''renne, aber si hend nid g''lost.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_WhoDroves2', 'dl_npc_zephyra', 'It''s always those "adventurers", looking for big treasure, without any respect. Unbelievable.', 'Es sind immer diese "Abenteurer", die nach den grossen Schätzen suchen, ohne jedweden Respekt. Unglaublich.', 'Es sind immer die "Obentüürer", wo nach de grosse Schätz sueched, völlig respektlos. Unglaublich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_WhoMages', 'dl_npc_zephyra', 'Hm. There was one women, fair hair, white dress. One of them was a boy, about your age, with red shaggy hair.', 'Hm. Eine davon war eine Frau, helles Haar, ein weisses Kleid. Da war auch ein Junge, etwa so alt wie du, mit rotem, verstrubbelten Haaren.', 'Hm. Eini devo isch e Frau gsi, helli Hoor, es wisses Chleid. Es het au en Bueb debi gha, öppe so alt we du, roti, strubbeligi Hoore.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_WhoMages2', 'dl_npc_zephyra', 'The third one had dark dreadlocks... but their leader frightened me the most.', 'Der dritte hatte dunkle Dreads... aber ihr Anführer hat mir am meisten Angst eingejagt.', 'De dritt het dunkli Rastas gha... aber ihre Aführer het mer am meiste Angst gmacht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_WhoMages3', 'dl_npc_zephyra', 'Tall, grey hair, piercing eyes. He was the only one that talked to me. The other three wouldn''t react. But they''re probably all dead now anyway.', 'Gross, graues Haar, durchdringender Blick. Er war der einzige, der mit mir gesprochen hat, die anderen drei haben nicht reagiert. Aber sie sind nun alle sowieso tot. ', 'Gross, graui Hoore, en durchdringende Blick. Er isch de einzig gsi wo mitmer gredt het, di andere hend nid reagiert. Aber si sind vermuetlich eh all tot ez.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_YashaDone', 'dl_npc_zephyra', '(Zephyra nods) Great. Thank you very much for your help. Take this potion with you, may it help you on your ways.', '(Zephyra nickt) Grossartig. Vielen Dank für deine Hilfe. Hier, nimm diesen Trank mit dir, möge er dir auf weiteren Wegen helfen.', '(D''Zephyra nickt) Grossargit. Danke vielmol für dini Hilf. Do, nimm de Trank mit dir. Er söll dir uf wiitere Wege helfe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_GottaGo', 'dl_npc_zephyra', 'I see, I can''t keep you from going in as well. But, just promise me one thing:', 'Ich sehe schon, ich kann dich nicht davon abhalten, hineinzugehen. Aber, versprich mir eins:', 'Ich gse, ich cha dich nid devo abhalte, iezgo. Aber, versprich mir eifach eis:');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_GottaGo2', 'dl_npc_zephyra', 'Don''t open any of the sacred urns in the temple, if your life means anything to you.', 'Öffne keine der heiligen Urnen im Tempel, wenn dir dein Leben lieb ist.', 'Mach kein vode heilige Urne im Tempel uf, wenn dir dis Lebe lieb isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YashaOK', 'dl_npc_zephyra', 'I will try to respect it, m''lady.', 'Ich versuche, es zu respektieren, meine Dame.', 'Ich probier, es z''respektiere, mini Dame.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YashaNOK', 'dl_npc_zephyra', '(Evil grin) I will take anything I want, understood?', '(Böses Grinsen) Ich werde alles nehmen was ich will, verstanden?', '(Böses Grinse) Ich wird alles ne wani will, verstande?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_YashaOK', 'dl_npc_zephyra', 'I was hoping you''d say this. Be careful, and the demon may spare your life.', 'Ich habe gehofft, du würdest das sagen. Sei vorsichtig, und der Dämon könnte dein Leben verschonen.', 'Ich ha ghofft, dass das sege würsch. Sig vorsichtig und de Dämon chönt dis Läbe verschone.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Zephyra_YashaNOK', 'dl_npc_zephyra', 'I hope the demon will rip your soul apart.', 'Ich hoffe der Dämon wird deine Seele zerreissen.', 'Ich hoff de Dämon wird dini Seel verrisse.');
