INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Darkness', 'cutscene','Darkness...','Dunkelheit...','Dunkelheit...','Oscuridad...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('WhatHappened', 'cutscene','What... what happened?','Was... was ist passiert?','Was... was isch passiert?','¿Qué... qué pasó?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('WhereAmI', 'cutscene','Where am I?','Wo bin ich?','Wo bin ich?','¿Dónde estoy?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Chapter1', 'cutscene','- Who Am I? -','- Wer bin ich? -','- Wer bin ich? -','- ¿Quién soy? -');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Elder:HereDrink', 'cutscene','Here, take the potion and drink it as fast as possible.','Da, nimm den Trank und trink ihn so schnell wie möglich.','Da, nimm de Trank und trink ihn so schnell we möglich.','Ten; toma la poción y bébela tan rápido como puedas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Elder:ItWillReveal', 'cutscene','It might feel a little bit strange, but it will bring clarity.','Es wird sich vielleicht etwas komisch anfühlen, aber der Trank wird Klarheit bringen.','Es wird sich vilicht chli komisch afühle aber de Trank wird Klarheit bringe.','Puede sentirse algo extraña, pero te dará claridad.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Elder:TakeWater', 'cutscene','And take this water, it will help you get rid of the potions'' raw, burning taste.','Und trink danach dieses Wasser, es hilft gegen den brennenden Geschmack des Trankes.','Und nimm na chli Wasser, es hilft der de gruusig Trank abezspüele.','Y bebe esta agua que te ayudará a deshacerte del sabor crudo y ardiente de las pociones.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:LetsDrink', 'cutscene','Okay, let''s get this over with.','Okay, bringen wir''s hinter uns.','Okay, bringemers hinter üs.','Está bien, acabemos con esto de una vez.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:Cough', 'cutscene','*Cough!*','*Hust!*','*Huest!*','*¡Cof!*');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:Aah', 'cutscene','Aaah!','Aaah!','Aaah!','¡Aaah!');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Crash', 'cutscene','*Crash*','*Splitter*','*Scherbel*','¡Crac!');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:ItBurns', 'cutscene','It burns!','Es brennt!','Es brännt!','¡Quema!');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:WhatsHappening', 'cutscene','What''s happening with me...','Was passiert mit mir...','Was passiert mit mir...','¿Qué me está pasando?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:IFeelSomething', 'cutscene','There, I feel something...','Da, ich spüre etwas...','Da, ich gspühr öppis...','Espera. Siento algo...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:SoCold', 'cutscene','So cold...','So kalt...','So chalt...','Mucho frío...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:AmI', 'cutscene','It''s... me?','Bin das... ich?','Bi das... ich?','¿Soy... yo?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:AMage', 'cutscene','A mage, that''s what I was.','Ein Magier war ich also.','Ich bin also en Magier gsi.','Un mago. Eso es lo que era.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:TheVisionOver', 'cutscene','The vision is over.','Die Vision ist vorbei.','D''Vision isch verbii.','La visión terminó.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:TheWaterFrozen', 'cutscene','The water... it''s frozen.','Das Wasser... Es ist gefroren.','Es Wasser... Es isch gfrore.','El agua... está congelada.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:IDidThat', 'cutscene','I think I did that.','Ich glaube, das war ich.','Ich glaub, da bin ich gsi.','Creo que yo hice eso.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:WhatsThis', 'cutscene','But, what''s this?!','Aber, was ist das?!','Aber, was isch das?!','Pero, ¿qué es esto?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:AMark', 'cutscene','That mark...','Dieses Zeichen...','Da Zeiche...','Esa marca...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:WasntHereBefore', 'cutscene','It wasn''t there before!','Es war vorher noch nicht da!','Da isch vorene nanig da gsi!','¡Antes no estaba ahí!');
