INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Darkness', 'cutscene','Darkness...','Dunkelheit...','Dunkelheit...','Oscuridad...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('WhatHappened', 'cutscene','What... what happened?','Was... was ist passiert?','Was... was isch passiert?','¿Qué... qué pasó?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('WhereAmI', 'cutscene','Where am I?','Wo bin ich?','Wo bin ich?','¿Dónde estoy?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Chapter1', 'cutscene','- Who Am I? -','- Wer bin ich? -','- Wer bin ich? -','- ¿Quién soy? -');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Elder:HereDrink', 'cutscene','Here, take the potion and drink it as fast as possible.','Da, nimm den Trank und trink ihn so schnell wie möglich.','Da, nimm de Trank und trink ihn so schnell we möglich.','Ten; toma la poción y bébela tan rápido como puedas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Elder:ItWillReveal', 'cutscene','It might feel a little bit strange, but it will bring clarity.','Es wird sich vielleicht etwas komisch anfühlen, aber der Trank wird Klarheit bringen.','Es wird sich vilicht chli komisch afühle aber de Trank wird Klarheit bringe.','Puede sentirse algo extraña, pero te dará claridad.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Elder:TakeWater', 'cutscene','And take this water, it will help you get rid of the potions'' raw, burning taste.','Und trink danach dieses Wasser, es hilft gegen den brennenden Geschmack des Trankes.','Und nimm na chli Wasser, es hilft der de gruusig Trank abezspüele.','Y bebe esta agua que te ayudará a pasar el sabor crudo y amargo de las pociones.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:LetsDrink', 'cutscene','Okay, let''s get this over with.','Okay, bringen wir''s hinter uns.','Okay, bringemers hinter üs.','Está bien, acabemos con esto de una vez.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:Cough', 'cutscene','*Cough!*','*Hust!*','*Huest!*','*¡Cof!*');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Cendric:Aah', 'cutscene','Aaah!','Aaah!','Aaah!','¡Aaah!');
