INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Reload', 'hint','Reload level','Level neu laden','Level neu lade','Recargar nivel');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Reload', 'hint_desc','Press the Escape key to go back to a previous checkpoint and reload the level.','Drücke Escape um zu einem vorherigen Kontrollpunkt zu gehen und das Level neu zu laden.','Druck Escape zum zumne vorherige Checkpoint zrugg z''ga und es Level neu z''lade.','Presiona la tecla de escape para regresar al punto de control anterior y recargar el nivel.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Spellbook', 'hint','Spellbook','Zauberbuch','Zauberbuech','Libro de hechizos');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Spellbook', 'hint_desc','to open up the spellbook and equip your new spell.','um das Zauberbuch zu öffnen und deinen neuen Zauber auszurüsten.','zum es Zauberbuech ufmache und din neue Zauber uusz''rüste','para abrir el libro de hechizos y equipar un nuevo hechizo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Checkpoint', 'hint','Checkpoint','Kontrollpunkt','Checkpoint','Punto de control');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Checkpoint', 'hint_desc','Activating a checkpoint will allow you to start again from its position when you die.','Wenn du einen Kontrollpunkt aktivierst, kannst du dich dorthin zurückversetzen, falls du stirbst.','Wenn du en Checkpoint aktiviersch chasch du vo det starte falls du verrecksch.','Activar un punto de control te permitirá empezar desde esa posición cuando mueras.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Modifier', 'hint','Magical gems','Magische Edelsteine','Magischi Edelstei','Gemas mágicas');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Modifier', 'hint_desc','Magical gems alter your spells. You can equip them outside of a level using your spellbook.','Magische Edelsteine verändern deine Zauber. Du kannst sie im Zauberbuch ausserhalb eines Levels ausrüsten.','Magischi Edelstei verändered dini Zauber. Du chasch si im Zauberbuech usserhalb vomne Level usrüste.','Las gemas mágicas modifican los hechizos. Las puedes equipar fuera del nivel usando el libro de hechizos.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Waypoint', 'hint','Waypoint','Wegpunkt','Wägpunkt','Coordenadas');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Waypoint', 'hint_desc','After you''ve activated a waypoint, you can teleport back using a teleport stone.','Nachdem du einen Wegpunkt aktiviert hast, kannst du dich mit einem Teleportstein dorthin zurück teleportieren.','Wenn du en Wägpunkt aktiviert hesch, chasch dich mitme Teleportstei det ane zrugg teleportiere.','Después de activar las coordenadas, puedes teletransportarte de regreso siempre.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Document', 'hint','Documents','Dokumente','Dokument','Documentos');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('Document', 'hint_desc','Documents can be read by right-clicking them in the inventory.','Dokumente können im Inventar mit einem Rechtsklick gelesen werden.','Dokument chasch im Inventar mit me Rechtsklick läse.','Puedes leer los documentos haciendo clic con el botón derecho sobre ellos en el inventario.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('ObserverSpell', 'hint','Observer Spells','Beobachter-Zauber','Beobachter-Zauber','Hechizos de vigilancia');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('ObserverSpell', 'hint_desc','Observer spells guard some houses to make sure that nothing is stolen. If you steal something while such a spell watches you, it will stun you and put you in jail. The observer spells will turn orange as long as they see you.','Beobachter-Zauber bewachen gewisse Häuser, damit niemand etwas stehlen kann. Wenn du etwas stiehlst, während ein Zauber dich beobachtet, kommst du ins Gefängnis. Beobachter-Zauber werden orange, solange sie dich sehen können.','Beobachter-Zauber bewached gwüssi Hüser, damit nüt klaut wird. Wenn du öppis chlausch, während so en Zauber dich beobachtet, chunsch is Gfängnis. Beobachter-Zauber werded orange, solang si dich gse chönd.','Los hechizos de vigilancia mantienen ciertas casas seguras para evitar robos. Si robas algo mientras uno de estos hechizos te está viendo, te aturdirá y te pondrá en la cárcel. Los hechizos de vigilancia se tornarán anaranjados mientras te vean.');