INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Pickup', 'hint','Item pickup','Gegenstände aufnehmen','Gegeständ ufnä');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Pickup', 'hint_desc','Click on an item with the right mouse key to pickup.','Klicke einen Gegenstand mit der rechten Maustaste an, um ihn aufzuheben.','Klick en Gegestand mit de rechte Muustaschte ah zum ihn neh.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Highlight', 'hint','Highlight items','Gegenstände markieren','Gegeständ markiere');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Highlight', 'hint_desc','to highlight items that can be picked up.','um aufhebbare Gegenstände zu markieren.','zum ufhebbari Gegeständ markiere.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Reload', 'hint','Reload level','Level neu laden','Level neu lade');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Reload', 'hint_desc','Press the Escape key to go back to a previous checkpoint and reload the level.','Drücke Escape um zu einem vorherigen Kontrollpunkt zu gehen und das Level neu zu laden.','Druck Escape zum zumne vorherige Checkpoint zrugg z''ga und es Level neu z''lade.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Spellbook', 'hint','Spellbook','Zauberbuch','Zauberbuech');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Spellbook', 'hint_desc','to open up the spellbook and equip your new spell.','um das Zauberbuch zu öffnen und deinen neuen Zauber auszurüsten.','zum es Zauberbuech ufmache und din neue Zauber uusz''rüste');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Checkpoint', 'hint','Checkpoint','Kontrollpunkt','Checkpoint');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Checkpoint', 'hint_desc','Activating a checkpoint will allow you to start again from its position when you die.','Wenn du einen Kontrollpunkt aktivierst, kannst du dich dorthin zurückversetzen, falls du stirbst.','Wenn du en Checkpoint aktiviersch chasch du vo det starte falls du verrecksch.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Modifier', 'hint','Magical gems','Magische Edelsteine','Magischi Edelstei');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Modifier', 'hint_desc','Magical gems alter your spells. You can equip them outside of a level using your spellbook.','Magische Edelsteine verändern deine Zauber. Du kannst sie im Zauberbuch ausserhalb eines Levels ausrüsten.','Magischi Edelstei verändered dini Zauber. Du chasch si im Zauberbuech usserhalb vomne Level usrüste.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Waypoint', 'hint','Waypoint','Wegpunkt','Wägpunkt');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('Waypoint', 'hint_desc','After you''ve activated a waypoint, you can take a teleport back, as long as you''re in the same region. Use a mouse click on the map or the minimap to teleport.','Nachdem du einen Wegpunkt aktiviert hast, kannst du dich dorthin zurück teleportieren, sofern du dich im selben Gebiet befindest. Teleportiere dich mit einem Mausklick auf der Karte zu einem Wegpunkt.','Wenn du en Wägpunkt aktiviert hesch, chasch dich det ane zrugg teleportiere, sofern du im gliiche Gebiet bisch. Teleportier dich mitme Muusklick uf de Karte zumne Wägpunkt.');
