INSERT INTO text (text_id, english, german, swiss_german) values ('druid_25','The druids do not think that much of you.','Die Druiden halten nicht sonderlich viel von dir.','D''Druide halted nid sonderlich viel vo dir.');
INSERT INTO text (text_id, english, german, swiss_german) values ('druid_50','The druids appreciate your help.','Die Druiden schätzen deine Hilfe.','D''Druide schätzed dini Hilf.');
