INSERT INTO text (text_id, english, german, swiss_german) values ('necromancer_25','You are unworthy in the eyes of the Necromancers','Du bist unwürdig in den Augen der Nekromanten.','Du bisch unwürdig ide Auge vode Nekromante.');
INSERT INTO text (text_id, english, german, swiss_german) values ('necromancer_50','You''ve caught the interest of the Necromancers.','Du hast das Interesse der Nekromanten geweckt.','Du hesch es Interesse vode Nekromante gweckt.');
INSERT INTO text (text_id, english, german, swiss_german) values ('necromancer_75','The Necromancers respect you.','Die Nekromanten respektieren dich.','D''Nekromante respektiered dich.');
INSERT INTO text (text_id, english, german, swiss_german) values ('necromancer_100','The Necromancers are highly impressed by your deeds.','Die Nekromanten sind von deinen Taten höchst beeindruckt.','D''Nekromante sind vo dine Tate höchst beidruckt.');
INSERT INTO text (text_id, english, german, swiss_german) values ('thief','Guild of the Shadow Stalkers','Gilde der Schattenläufer','Gilde vode Schatteläufer');
