INSERT INTO text (text_id, english, german, swiss_german) values ('thief_25','The guild doesn''t trust you.','Die Gilde vertraut dir nicht.','D''Gilde traut dir nid.');
INSERT INTO text (text_id, english, german, swiss_german) values ('thief_50','You have the potential to be a worthy member of the guild.','Du hast das Potential, ein würdiges Mitglied der Gilde zu werden.','Du hesch es Potential zum es würdigs Mitglied vode Gilde z''werde.');
INSERT INTO text (text_id, english, german, swiss_german) values ('thief_75','You are considered a full member of the guild.','Du bist ein vollständiges Mitglied der Gilde.','Du bisch es vollständigs Mitglied vode Gilde.');
INSERT INTO text (text_id, english, german, swiss_german) values ('thief_100','You have grown irreplaceable for the guild.','Du bist für die Gilde unersetzlich geworden.','Du bisch für d''Gilde unersetzlich worde.');
