INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DestroyBlocks', 'speech','Destroy these rocks with your weapon so we can move on!','Zerstöre diese Felsen mit deiner Waffe, dass wir weiter gehen können!','Zestör die Felse mit dinere Waffe so dasmer wiiter chömed!');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('SpikesAreBad', 'speech','I don''t think you should destroy those though...','Diese hier solltest du aber besser nicht zerstören...','Aber die da sötsch glaub besser nid zerstöre...');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('FindAWay', 'speech','Do you think you could get up there and get that potion?','Glaubst du, du kommst hier hoch und kannst diesen Trank holen?','Wa meinsch, chunsch echt da ufe und chasch de Trank det hole?');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('KillRats', 'speech','Oh, watch out for these two big rats over there! They can bite quite violently.','Oh, pass auf! Dort drüben sind zwei Ratten und die können ziemlich heftig zubeissen.','Oh, pass uf! Det enne sind zwei Ratte wo zimli heftig bisse chönd.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('IAmLeaving', 'speech','There must be a nest somewhere... Let''s leave this place.','Hier muss es irgendwo ein Nest geben... Gehen wir hier besser raus.','Da mueses irgendwo es Nest gä... Gömmer besser weg vo da.');